const handleCreateMultiGameRental = async (rentalData) => {
  console.log('๐ ุฅูุดุงุก ุชุฃุฌูุฑ ูุชุนุฏุฏ ุงูุฃูุนุงุจ', {
    customer: rentalData.customerInfo.name,
    gamesCount: rentalData.rentalItems.length
  });
  
  try {
    if (!rentalData.rentalItems || rentalData.rentalItems.length === 0) {
      alert('โ ูู ูุชู ุงุฎุชูุงุฑ ุฃู ุฃูุนุงุจ');
      return;
    }
    
    // ุงูุชุญูู ูู ุงูุดููุช
    const shiftValid = await validateShiftBeforeRental();
    if (!shiftValid) {
      return;
    }
    
    // ุฅูุดุงุก ูุงุฆูุฉ ุงูุชุฃุฌูุฑุงุช
    const rentalsToCreate = rentalData.rentalItems.map(item => {
      const game = games.find(g => g.id === item.gameId);
      
      if (!game) {
        throw new Error(`ุงููุนุจุฉ ุบูุฑ ููุฌูุฏุฉ: ${item.gameId}`);
      }
      
      return {
        game_id: item.gameId,
        game_name: game.name,
        customer_name: rentalData.customerInfo.name.trim(),
        customer_phone: rentalData.customerInfo.phone || '00000000000',
        rental_type: item.rentalType || 'fixed',
        duration_minutes: item.duration || 15,
        price_per_15min: game.price_per_15min || 100,
        total_amount: item.rentalType === 'fixed' ? calculateItemPrice(item) : 0,
        paid_amount: item.rentalType === 'fixed' ? calculateItemPrice(item) : 0,
        payment_method: rentalData.paymentMethod || 'ูุงุด',
        payment_status: item.rentalType === 'fixed' ? 'ูุฏููุน ูุณุจูุงู' : 'ุนูุฏ ุงูุฅููุงุก',
        quantity: item.quantity || 1,
        notes: item.notes || ''
      };
    });
    
    console.log('๐ค ุฅุฑุณุงู ุทูุจุงุช ุงูุชุฃุฌูุฑ:', rentalsToCreate.length);
    
    // ุฅุฑุณุงู ุงูุทูุจุงุช ุจุดูู ูุชุณูุณู
    const results = [];
    let totalRevenue = 0;
    
    for (const rentalItem of rentalsToCreate) {
      try {
        const response = await api.createSmartRental({
          game_id: rentalItem.game_id,
          customer_name: rentalItem.customer_name,
          customer_phone: rentalItem.customer_phone,
          rental_type: rentalItem.rental_type
        });
        
        if (response.success) {
          results.push({
            success: true,
            game: rentalItem.game_name,
            rental_number: response.rental_number,
            type: rentalItem.rental_type === 'fixed' ? 'ุซุงุจุช' : 'ููุชูุญ',
            amount: rentalItem.total_amount
          });
          
          // ุฅุถุงูุฉ ุงูุฅูุฑุงุฏ ููููุช ุงูุซุงุจุช
          if (rentalItem.rental_type === 'fixed') {
            totalRevenue += rentalItem.total_amount;
          }
        } else {
          results.push({
            success: false,
            game: rentalItem.game_name,
            error: response.message
          });
        }
      } catch (error) {
        results.push({
          success: false,
          game: rentalItem.game_name,
          error: error.message
        });
      }
      
      // ุชุฃุฎูุฑ ุจุณูุท ุจูู ุงูุทูุจุงุช ูุชุฌูุจ ุงูุญูู ุนูู ุงูุฎุงุฏู
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    
    // ุชุญููู ุงููุชุงุฆุฌ
    const successful = results.filter(r => r.success);
    const failed = results.filter(r => !r.success);
    
    // ุชุญุฏูุซ ุงูุฅูุฑุงุฏ ูู ุงููุงุฌูุฉ
    if (totalRevenue > 0) {
      setStats(prev => ({
        ...prev,
        todayRevenue: (prev.todayRevenue || 0) + totalRevenue
      }));
    }
    
    // ุนุฑุถ ุงููุชุงุฆุฌ
    let message = '';
    
    if (successful.length > 0) {
      message += `โ ุชู ุฅูุดุงุก ${successful.length} ุชุฃุฌูุฑ ุจูุฌุงุญ:\n`;
      successful.forEach((item, index) => {
        message += `${index + 1}. ${item.game} (${item.type}) - ${item.rental_number}\n`;
      });
    }
    
    if (failed.length > 0) {
      message += `\nโ ูุดู ุฅูุดุงุก ${failed.length} ุชุฃุฌูุฑ:\n`;
      failed.forEach((item, index) => {
        message += `${index + 1}. ${item.game}: ${item.error}\n`;
      });
    }
    
    alert(message);
    
    // ุฅุบูุงู ุงูููุฏุงู ูุชุญุฏูุซ ุงูุจูุงูุงุช
    if (successful.length > 0) {
      setShowRentalModal(false);
      setSelectedGames([]);
      
      setTimeout(() => {
        loadActiveRentalsForCurrentShift();
        loadGames();
      }, 1500);
    }
    
  } catch (error) {
    console.error('๐ฅ ุฎุทุฃ ูู ุฅูุดุงุก ุงูุชุฃุฌูุฑ ุงููุชุนุฏุฏ:', error);
    alert('โ ุญุฏุซ ุฎุทุฃ ูู ุฅูุดุงุก ุงูุชุฃุฌูุฑ ุงููุชุนุฏุฏ: ' + (error.message || 'ุชุฃูุฏ ูู ุงุชุตุงู ุงูุฎุงุฏู'));
  }
};

