import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [backendStatus, setBackendStatus] = useState('ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÇŸÇ...');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑ ÿ®ÿßŸÑŸÅÿπŸÑ
    const token = localStorage.getItem('token');
    const userStr = localStorage.getItem('user');
    
    if (token && userStr) {
      try {
        const user = JSON.parse(userStr);
        if (user.role === 'admin') {
          navigate('/admin/dashboard');
        } else {
          navigate('/employee/dashboard');
        }
      } catch (e) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
    
    checkBackendStatus();
  }, [navigate]);

  const checkBackendStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      if (!response.ok) throw new Error('ÿßŸÑÿÆÿßÿØŸÖ ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠');
      const data = await response.json();
      
      if (data.success && data.database === 'connected') {
        setBackendStatus('‚úÖ ÿßŸÑŸÜÿ∏ÿßŸÖ ŸäÿπŸÖŸÑ ÿ®ÿ¥ŸÉŸÑ ÿ∑ÿ®ŸäÿπŸä');
      } else {
        setBackendStatus('‚ö†Ô∏è ÿßŸÑŸÜÿ∏ÿßŸÖ ŸäÿπŸÖŸÑ ŸàŸÑŸÉŸÜ ŸáŸÜÿßŸÉ ŸÖÿ¥ŸÉŸÑÿ© ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
      }
    } catch (error) {
      setBackendStatus('‚ùå ÿßŸÑÿÆÿßÿØŸÖ ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ ÿπŸÑŸâ http://localhost:5000');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿØÿÆŸÑÿ©
    if (!email.trim() || !password.trim()) {
      setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸàŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ');
      return;
    }
    
    if (!email.includes('@')) {
      setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');
      return;
    }
    
    setLoading(true);
    setError('');

    try {
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ...');
      
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          email: email.trim().toLowerCase(),
          password: password 
        })
      });
      
      const data = await response.json();
      console.log('üìã ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿÆÿßÿØŸÖ:', data);
      
      if (!response.ok || !data.success) {
        throw new Error(data.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ. ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
      }
      
      if (!data.token || !data.user) {
        throw new Error('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ∫Ÿäÿ± ŸÖŸÉÿ™ŸÖŸÑÿ©');
      }
      
      // ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä localStorage
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      
      console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠:', data.user);
      
      // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá
      setTimeout(() => {
        if (data.user.role === 'admin') {
          navigate('/admin/dashboard');
        } else {
          navigate('/employee/dashboard');
        }
      }, 500);
      
    } catch (err) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', err);
      
      // ÿ±ÿ≥ÿßÿ¶ŸÑ ÿÆÿ∑ÿ£ ŸÖÿÆÿµÿµÿ©
      if (err.message.includes('network')) {
        setError('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ Backend');
      } else if (err.message.includes('credentials')) {
        setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©');
      } else {
        setError(err.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ');
      }
    } finally {
      setLoading(false);
    }
  };

  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ®ŸäÿßŸÜÿßÿ™ ÿßÿÆÿ™ÿ®ÿßÿ±
  const handleTestLogin = (testEmail, testPassword) => {
    setEmail(testEmail);
    setPassword(testPassword);
    
    // ÿ™ÿ£ÿÆŸäÿ± ÿ®ÿ≥Ÿäÿ∑ ÿ´ŸÖ ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
    setTimeout(() => {
      const fakeEvent = { preventDefault: () => {} };
      handleLogin(fakeEvent);
    }, 100);
  };

  // ÿ™ÿ¥ÿ∫ŸäŸÑ Backend ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸäÿπŸÖŸÑ
  const openBackendInstructions = () => {
    alert(`üöÄ ŸÉŸäŸÅŸäÿ© ÿ™ÿ¥ÿ∫ŸäŸÑ Backend:

1. ÿßŸÅÿ™ÿ≠ ŸÜÿßŸÅÿ∞ÿ© Terminal ÿ¨ÿØŸäÿØÿ©
2. ÿßŸÉÿ™ÿ®: cd backend
3. ÿßŸÉÿ™ÿ®: npm run dev
4. ÿßŸÜÿ™ÿ∏ÿ± ÿ≠ÿ™Ÿâ ÿ™ÿ±Ÿâ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©: "üöÄ Server running on port 5000"
5. ÿ´ŸÖ ÿ£ÿπÿØ ÿ™ÿ≠ŸÖŸäŸÑ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©

üìå ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ŸÖŸÑŸÅ .env Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ:
DB_CONNECTION_STRING=your_mongodb_connection_string
JWT_SECRET=your_jwt_secret_key`);
  };

  return (
    <div className="login-page">
      <div className="login-container">
        {/* ÿßŸÑÿÆŸÑŸÅŸäÿ© */}
        <div className="login-background">
          <div className="background-gradient"></div>
          <div className="background-pattern"></div>
        </div>

        {/* ÿßŸÑÿ¥ÿπÿßÿ± */}
        <div className="company-header">
          <div className="company-logo">
            <div className="logo-circle">
              <span className="logo-text">L3B</span>
            </div>
          </div>
          <h1 className="company-name">L3BTY Store</h1>
          <p className="company-tagline">ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
        </div>

        {/* ÿ®ÿ∑ÿßŸÇÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ */}
        <div className="login-card">
          <div className="card-header">
            <h2>üîê ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ</h2>
            <p>ÿ£ÿØÿÆŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ≠ÿ≥ÿßÿ®ŸÉ ŸÑŸÑŸàÿµŸàŸÑ ŸÑŸÑŸÜÿ∏ÿßŸÖ</p>
          </div>

          {/* ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ */}
          <div className="backend-status-card">
            <div className="status-content">
              <span className="status-icon">‚ö°</span>
              <div className="status-text">
                <p className="status-title">ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ</p>
                <p className={`status-message ${backendStatus.includes('‚úÖ') ? 'success' : backendStatus.includes('‚ö†Ô∏è') ? 'warning' : 'error'}`}>
                  {backendStatus}
                </p>
              </div>
            </div>
            {backendStatus.includes('‚ùå') && (
              <button 
                className="btn-help"
                onClick={openBackendInstructions}
              >
                <span className="help-icon">üöÄ</span>
                ŸÉŸäŸÅŸäÿ© ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ
              </button>
            )}
          </div>

          {/* ÿ±ÿ≥ÿßÿ¶ŸÑ ÿßŸÑÿÆÿ∑ÿ£ */}
          {error && (
            <div className="alert alert-danger">
              <span className="alert-icon">‚ö†Ô∏è</span>
              <div className="alert-content">
                <strong>ÿÆÿ∑ÿ£</strong>
                <p>{error}</p>
              </div>
              <button className="alert-close" onClick={() => setError('')}>‚úï</button>
            </div>
          )}

          {/* ŸÜŸÖŸàÿ∞ÿ¨ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ */}
          <form onSubmit={handleLogin} className="login-form">
            <div className="form-group">
              <label htmlFor="email" className="form-label">
                <span className="label-icon">üìß</span>
                ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
              </label>
              <div className="input-with-icon">
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="admin@l3bty.com"
                  required
                  className="form-input"
                  disabled={loading}
                />
                <span className="input-icon">üë§</span>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="password" className="form-label">
                <span className="label-icon">üîí</span>
                ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
              </label>
              <div className="input-with-icon">
                <input
                  type={showPassword ? "text" : "password"}
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  required
                  className="form-input"
                  disabled={loading}
                />
                <button 
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={loading}
                >
                  {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                </button>
              </div>
            </div>

            <button 
              type="submit" 
              className={`btn-login ${loading ? 'loading' : ''}`}
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  ÿ¨ÿßÿ±Ÿä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ...
                </>
              ) : (
                <>
                  <span className="btn-icon">üîë</span>
                  ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
                </>
              )}
            </button>
          </form>

          {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿßŸÑÿ≥ÿ±Ÿäÿπ */}
          <div className="quick-login">
            <h4 className="quick-login-title">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ÿ≥ÿ±Ÿäÿπ:</h4>
            <div className="quick-login-buttons">
              <button 
                className="quick-btn admin"
                onClick={() => handleTestLogin('admin@l3bty.com', '123456')}
                disabled={loading}
              >
                <span className="quick-icon">üëë</span>
                ÿßŸÑŸÖÿØŸäÿ±
              </button>
              <button 
                className="quick-btn manager"
                onClick={() => handleTestLogin('manager@l3bty.com', '123456')}
                disabled={loading}
              >
                <span className="quick-icon">üëî</span>
                ŸÖÿØŸäÿ± ŸÅÿ±ÿπ
              </button>
              <button 
                className="quick-btn employee"
                onClick={() => handleTestLogin('employee@l3bty.com', '123456')}
                disabled={loading}
              >
                <span className="quick-icon">üë§</span>
                ŸÖŸàÿ∏ŸÅ
              </button>
            </div>
          </div>

          {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© */}
          <div className="login-extras">
            <div className="debug-section">
              <button 
                className="btn-debug"
                onClick={() => {
                  console.log('=== ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ™ÿµÿ≠Ÿäÿ≠ ===');
                  console.log('Email:', email);
                  console.log('Password Length:', password.length);
                  console.log('Backend Status:', backendStatus);
                  console.log('LocalStorage Token:', localStorage.getItem('token'));
                  console.log('LocalStorage User:', localStorage.getItem('user'));
                  alert('ÿ™ŸÖ ÿπÿ±ÿ∂ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ™ÿµÿ≠Ÿäÿ≠ ŸÅŸä Console (F12)');
                }}
                disabled={loading}
              >
                <span className="debug-icon">üõ†Ô∏è</span>
                ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ™ÿµÿ≠Ÿäÿ≠
              </button>
            </div>
          </div>
        </div>

        {/* ÿßŸÑŸÅŸàÿ™ÿ± */}
        <footer className="login-footer">
          <div className="footer-content">
            <p className="footer-text">
              ¬© 2024 L3BTY Store - ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿ∑Ÿàÿ±
            </p>
            <div className="footer-links">
              <span className="footer-version">ÿßŸÑÿ•ÿµÿØÿßÿ± 2.0.0</span>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Login;
