import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw, Calculator 
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || amount === '') return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/---- --:--';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  if (isNaN(start.getTime()) || isNaN(end.getTime())) return 0;
  return Math.max(0, Math.round((end - start) / (1000 * 60)));
};

// Ø£Ù†Ø´Ø¦ ÙƒØ§Ø¦Ù† cache
const apiCache = {
  rentals: {},
  games: {},
  shift: {},
  
  set: function(key, data, expiry = 30000) { // 30 Ø«Ø§Ù†ÙŠØ© Ø§ÙØªØ±Ø§Ø¶ÙŠØ§Ù‹
    this[key] = {
      data,
      expiry: Date.now() + expiry,
      timestamp: Date.now()
    };
  },
  
  get: function(key) {
    const item = this[key];
    if (!item) return null;
    
    if (Date.now() > item.expiry) {
      delete this[key];
      return null;
    }
    
    return item.data;
  },
  
  clear: function(key) {
    if (key) {
      delete this[key];
    } else {
      this.rentals = {};
      this.games = {};
      this.shift = {};
    }
  }
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  
  const [loading, setLoading] = useState({
    games: false,
    rentals: false,
    shift: false,
    stats: false,
    processing: false
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
const lastRequestRef = useRef({});
const isMountedRef = useRef(true);
const refreshTimeoutRef = useRef(null);
const dataLoadedRef = useRef(false);

  const [refreshInterval, setRefreshInterval] = useState(null);
const lastRefreshRef = useRef(Date.now());



  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  const processingRef = useRef(false);
  const dataLoadTimeoutRef = useRef(null);
  const shiftCheckIntervalRef = useRef(null);

    // âœ… 7. ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ - Ù…Ø­Ø³Ù†
  const loadGames = useCallback(async () => {
    if (!user?.branch_id) {
      setGames([]);
      setAllGames([]);
      return;
    }
    
    try {
      setLoading(prev => ({ ...prev, games: true }));
      
      const response = await api.loadGamesSmart(user.branch_id);
        
      if (response.success && response.data) {
        setGames(response.data);
        setAllGames(response.data);
        
        const availableCount = response.data.filter(game => 
          game.status === 'Ù…ØªØ§Ø­'
        ).length;
        
        setStats(prev => ({
          ...prev,
          availableGames: availableCount,
          totalGames: response.data.length
        }));
      } else {
        setGames([]);
        setAllGames([]);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setGames([]);
      setAllGames([]);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  // âœ… 8. ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ - Ù…Ø­Ø³Ù†
  const loadCurrentShift = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, shift: true }));
      
      const response = await api.getCurrentShift();
      
      if (response.success && response.data) {
        const shift = response.data;
        
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          localStorage.setItem('current_shift', JSON.stringify(shift));
          return shift;
        } else {
          console.warn('âš ï¸ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø· Ù„Ø§ ÙŠÙ†ØªÙ…ÙŠ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ');
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } else {
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, [user?.id, user?.name]);
  
  const calculateRealRevenue = useCallback(async (shiftId, branchId) => {
    if (!shiftId || !branchId) return 0;
    
    try {
      const response = await api.get('/rentals', {
        params: {
          shift_id: shiftId,
          branch_id: branchId,
          limit: 1000
        }
      });
      
      if (response.success && response.data) {
        const rentals = Array.isArray(response.data) ? response.data : [];
        
        return rentals.reduce((total, rental) => {
          if (rental.status === 'Ù…ÙƒØªÙ…Ù„') {
            const rentalType = getRentalType(rental);
            if (rentalType === 'open') {
              return total + parseFloat(rental.final_amount || 0);
            } else {
              return total + parseFloat(rental.paid_amount || rental.total_amount || 0);
            }
          }
          return total;
        }, 0);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø­Ù‚ÙŠÙ‚ÙŠ:', error);
      setError('Ø®Ø·Ø£ ÙÙŠ Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯');
    }
    
    return 0;
  }, []);

  // âœ… 2. ØªØ­Ù…ÙŠÙ„ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª - Ù…Ø­Ø³Ù†
  const loadShiftRevenue = useCallback(async () => {
    if (!currentShift?.id || !user?.branch_id) return;
    
    try {
      setLoading(prev => ({ ...prev, stats: true }));
      const revenue = await calculateRealRevenue(currentShift.id, user.branch_id);
      
      setStats(prev => ({
        ...prev,
        todayRevenue: revenue
      }));
      
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª:', error);
      setError('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯');
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  }, [currentShift?.id, user?.branch_id, calculateRealRevenue]);

  // âœ… 3. ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯ - Ù…Ø­Ø³Ù†
  const updateRevenueOnRentalComplete = useCallback((rental, amount) => {
    if (!rental || !currentShift?.id) return;
    
    try {
      const rentalType = getRentalType(rental);
      
      if (rentalType === 'open') {
        setStats(prev => ({
          ...prev,
          todayRevenue: (prev.todayRevenue || 0) + amount
        }));
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯:', error);
    }
  }, [currentShift?.id]);

  // âœ… 4. Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© - Ù…Ø­Ø³Ù†
  const loadCompletedRentals = useCallback(async () => {
    if (!isManager || !currentShift?.id || !user?.branch_id) return;
    
    try {
      const response = await api.get('/rentals', {
        params: {
          shift_id: currentShift.id,
          branch_id: user.branch_id,
          status: 'Ù…ÙƒØªÙ…Ù„',
          limit: 100
        }
      });
      
      if (response.success && response.data) {
        setCompletedRentals(response.data);
        
        const revenue = response.data.reduce((total, rental) => {
          if (rental.status === 'Ù…ÙƒØªÙ…Ù„') {
            const rentalType = getRentalType(rental);
            if (rentalType === 'open') {
              return total + parseFloat(rental.final_amount || 0);
            } else {
              return total + parseFloat(rental.paid_amount || rental.total_amount || 0);
            }
          }
          return total;
        }, 0);
        
        setStats(prev => ({
          ...prev,
          todayRevenue: revenue,
          todayRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©:', error);
      setCompletedRentals([]);
    }
  }, [currentShift?.id, isManager, user?.branch_id]);
// ==================== ğŸ”§ Ø¥ØµÙ„Ø§Ø­Ø§Øª Ù…ØªÙ‚Ø¯Ù…Ø© Ù„Ù„ÙˆÙ‚Ø§ÙŠØ© Ù…Ù† ØªÙƒØ±Ø§Ø± Ø§Ù„Ø·Ù„Ø¨Ø§Øª ====================


// âœ… 5. ØªØ­Ø¯ÙŠØ¯ Ù†ÙˆØ¹ Ø§Ù„ØªØ£Ø¬ÙŠØ± - Ù…Ø­Ø³Ù†
const getRentalType = useCallback((rental) => {
  if (!rental) return 'fixed';
  
  // âœ… Ø§Ù„Ø£ÙˆÙ„ÙˆÙŠØ© Ù„Ù„Ù†ÙˆØ¹ Ø§Ù„Ù…Ø­Ø¯Ø¯ ØµØ±Ø§Ø­Ø©
  if (rental.rental_type === 'open' || rental.is_open_time === 1) {
    return 'open';
  }
  
  if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
    return 'fixed';
  }
  
  // âœ… Ø§Ù„Ø³Ù‚ÙˆØ· Ø¹Ù„Ù‰ Ø­Ø§Ù„Ø© Ø§Ù„Ø¯ÙØ¹ Ø¥Ø°Ø§ Ù„Ù… ÙŠÙƒÙ† Ø§Ù„Ù†ÙˆØ¹ Ù…Ø­Ø¯Ø¯Ø§Ù‹
  if (rental.payment_status === 'Ø¹Ù†Ø¯ Ø§Ù„Ø¥Ù†Ù‡Ø§Ø¡' || rental.payment_status === 'pending') {
    return 'open';
  }
  
  if (rental.payment_status === 'Ù…Ø¯ÙÙˆØ¹ Ù…Ø³Ø¨Ù‚Ø§Ù‹' || rental.payment_status === 'paid') {
    return 'fixed';
  }
  
  // âœ… Ø§Ù„Ø§ÙØªØ±Ø§Ø¶ÙŠ ÙˆÙ‚Øª Ø«Ø§Ø¨Øª
  return 'fixed';
}, []);

const loadActiveRentalsForCurrentShift = useCallback(async (force = false) => {
  if (!currentShift?.id || !user?.branch_id) {
    setActiveRentals([]);
    return;
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    console.log(`ğŸ“¥ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ù„Ù„Ø´ÙŠÙØª ${currentShift.id}`);
    
    // âœ… Ø§Ø³ØªØ®Ø¯Ù… Ù†Ù‚Ø·Ø© API ÙˆØ§Ø­Ø¯Ø© Ù…ÙˆØ¬ÙˆØ¯Ø© ÙØ¹Ù„ÙŠØ§Ù‹
    const response = await api.get('/rentals/active', {
      params: {
        shift_id: currentShift.id,
        branch_id: user.branch_id
      }
    });
    
    console.log('ğŸ“¦ Ø§Ø³ØªØ¬Ø§Ø¨Ø© API:', response);
    
    let rentals = [];
    if (response.success && response.data) {
      rentals = Array.isArray(response.data) ? response.data : 
                (response.data.data && Array.isArray(response.data.data) ? response.data.data : []);
    }
    
    // ØªØµÙÙŠØ© Ù„Ù„ØªØ£ÙƒÙŠØ¯
    const shiftRentals = rentals.filter(rental => 
      rental.shift_id == currentShift.id && rental.status === 'Ù†Ø´Ø·'
    );
    
    console.log(`ğŸ“Š Ø¹Ø¯Ø¯ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©: ${shiftRentals.length}`);
    setActiveRentals(shiftRentals);
    
    setStats(prev => ({
      ...prev,
      activeRentals: shiftRentals.length
    }));
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
}, [currentShift?.id, user?.branch_id]);

// âœ… Ø¯Ø§Ù„Ø© Ù…Ø³Ø§Ø¹Ø¯Ø© Ù„Ù„ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙÙˆØ±ÙŠ
