import React, { useState, useEffect, useCallback, useMemo } from 'react';
import api from '../../services/api';
import './AdminPages.css';

const UsersManagement = () => {
  // üîπ States ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('all');
  const [branches, setBranches] = useState([]);
  const [confirmDelete, setConfirmDelete] = useState(null);
  const [showPassword, setShowPassword] = useState(false);
  
  const itemsPerPage = 10;
  
  // üîπ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
  const stats = useMemo(() => {
    return {
      total: users.length,
      admins: users.filter(user => user.role === 'admin').length,
      managers: users.filter(user => user.role === 'branch_manager').length,
      employees: users.filter(user => user.role === 'employee').length,
      active: users.filter(user => user.is_active).length,
      inactive: users.filter(user => !user.is_active).length
    };
  }, [users]);

  // üîπ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ¨ÿØŸäÿØ
  const [newUser, setNewUser] = useState({
    username: '',
    email: '',
    password: '123456',
    name: '',
    role: 'employee',
    branch_id: '',
    phone: '',
    is_active: true
  });

  // üîπ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿπŸÜÿØ ÿßŸÑÿ™ŸáŸäÿ¶ÿ©
  useEffect(() => {
    loadData();
  }, []);

  // üîπ ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿµŸÅÿ≠ÿ© ÿ•ŸÑŸâ 1 ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÅŸÑÿ™ÿ±
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, filterRole]);

// üîπ ÿØÿßŸÑÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ - ŸÜÿ≥ÿÆÿ© ŸÖÿ≠ÿ≥ŸÜÿ©
const loadData = useCallback(async () => {
  try {
    setLoading(true);
    setError('');
    setSuccess('');
    
    console.log('üë• ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ...');
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ÿ£ŸàŸÑÿßŸã
    const token = localStorage.getItem('token');
    if (!token) {
      setError('ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã');
      window.location.href = '/login';
      return;
    }
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
    const userStr = localStorage.getItem('user');
    if (userStr) {
      const user = JSON.parse(userStr);
      if (user.role !== 'admin' && user.role !== 'branch_manager') {
        setError('ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿµŸÑÿßÿ≠Ÿäÿ© ŸÑÿπÿ±ÿ∂ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©');
        setLoading(false);
        return;
      }
    }
    
    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ¨ŸÑÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ ŸàÿßŸÑŸÅÿ±Ÿàÿπ
    let usersSuccess = false;
    let branchesSuccess = false;
    
    // ÿ¨ŸÑÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
    try {
      console.log('üì° ŸÖÿ≠ÿßŸàŸÑÿ© ÿ¨ŸÑÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ...');
      const usersResponse = await api.getUsers();
      console.log('üìä ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ:', usersResponse);
      
      if (usersResponse.success) {
        setUsers(usersResponse.data || []);
        usersSuccess = true;
      } else {
        console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ:', usersResponse.message);
        
        // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿÆÿ∑ÿ£ 403ÿå ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÑŸäÿ≥ ŸÑÿØŸäŸá ÿµŸÑÿßÿ≠Ÿäÿ©
        if (usersResponse.status === 403) {
          setError('ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿµŸÑÿßÿ≠Ÿäÿ© ŸÑÿπÿ±ÿ∂ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ');
        }
      }
    } catch (userError) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ:', userError);
    }
    
    // ÿ¨ŸÑÿ® ÿßŸÑŸÅÿ±Ÿàÿπ
    try {
