      try {
        const targetUser = users.find(u => u.id === id);
        if (!targetUser) return;
        
        const response = await api.updateUser(id, {
          is_active: !currentStatus
        });
        
        if (response.success) {
          setSuccess(`‚úÖ ÿ™ŸÖ ${currentStatus ? 'ÿ™ÿπÿ∑ŸäŸÑ' : 'ÿ™ŸÅÿπŸäŸÑ'} ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ "${targetUser.name}" ÿ®ŸÜÿ¨ÿßÿ≠`);
          await loadData();
        } else {
          setError(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ≠ÿßŸÑÿ©');
        }
      } catch (updateError) {
        setError('ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ');
      }
    } else {
      setError(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ≠ÿßŸÑÿ©');
    }
