    switch(role) {
      case 'admin': return 'üëë';
      case 'branch_manager': return 'üè¢';
      case 'employee': return 'üë§';
      default: return '‚ùì';
    }
  };

  const getRoleColor = (role) => {
    switch(role) {
      case 'admin': return '#ef4444';
      case 'branch_manager': return '#f59e0b';
      case 'employee': return '#3b82f6';
