useEffect(() => {
  const validateUserAndToken = async () => {
    try {
      const token = localStorage.getItem('token');
      const userStr = localStorage.getItem('user');
      
      console.log('üîç ŸÅÿ≠ÿµ ÿßŸÑÿ¨ŸÑÿ≥ÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ©:', {
        tokenExists: !!token,
        userExists: !!userStr
      });
      
      if (!token || !userStr) {
        console.log('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ™ŸàŸÉŸÜ ÿ£Ÿà ŸÖÿ≥ÿ™ÿÆÿØŸÖÿå ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© login');
        window.location.href = '/login';
        return;
      }
      
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿπ ÿßŸÑÿÆÿßÿØŸÖ
      try {
        const profileResponse = await api.getProfile();
        console.log('üë§ ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© profile:', profileResponse);
        
        if (!profileResponse.success) {
          console.log('‚ö†Ô∏è ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿå ÿ™ŸÜÿ∏ŸäŸÅ localStorage Ÿàÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá');
          localStorage.clear();
          window.location.href = '/login';
          return;
        }
        
        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©
        const user = profileResponse.data || JSON.parse(userStr);
        if (user.role !== 'admin' && user.role !== 'branch_manager') {
          setError('ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿµŸÑÿßÿ≠Ÿäÿ© ŸÑÿπÿ±ÿ∂ Ÿáÿ∞Ÿá ÿßŸÑÿµŸÅÿ≠ÿ©');
          return;
        }
        
        console.log('‚úÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿµÿßŸÑÿ≠:', user);
        
      } catch (profileError) {
        console.error('‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', profileError);
        localStorage.clear();
        window.location.href = '/login';
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ:', error);
    }
  };
  
  validateUserAndToken();
}, []);

  // üîπ ŸÅŸÑÿ™ÿ±ÿ© Ÿàÿ™ÿ±ÿ™Ÿäÿ® ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
  const filteredUsers = useMemo(() => {
    return users.filter(user => {
      const matchesSearch = searchTerm === '' || 
        (user.name && user.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (user.email && user.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (user.username && user.username.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (user.phone && user.phone.includes(searchTerm));
      
      const matchesRole = filterRole === 'all' || user.role === filterRole;
      
      return matchesSearch && matchesRole;
    });
  }, [users, searchTerm, filterRole]);

  const totalPages = Math.ceil(filteredUsers.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedUsers = filteredUsers.slice(startIndex, startIndex + itemsPerPage);

  // üîπ ÿ™ŸàŸÑŸäÿØ ŸÉŸÑŸÖÿ© ŸÖÿ±Ÿàÿ± ÿπÿ¥Ÿàÿßÿ¶Ÿäÿ©
  const generatePassword = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    let password = '';
    for (let i = 0; i < 8; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return password;
  };

  // ŸÅŸä UsersManagement.jsxÿå ÿ£ÿ∂ŸÅ Ÿáÿ∞ÿß useEffect ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
