import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
// import activityService from '../../services/activityService'; // Ø¥Ø²Ø§Ù„Ø© Ù‡Ø°Ø§ Ø¥Ø°Ø§ Ù„Ù… ÙŠÙƒÙ† Ù…ÙˆØ¬ÙˆØ¯Ø§Ù‹
import './AdminPages.css';

const GamesManagement = () => {
  const [games, setGames] = useState([]);
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingGame, setEditingGame] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterBranch, setFilterBranch] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [activeTab, setActiveTab] = useState('games');
  const [stats, setStats] = useState({
    totalGames: 0,
    availableGames: 0,
    rentedGames: 0,
    maintenanceGames: 0
  });
  
  const itemsPerPage = 10;

  const [newGame, setNewGame] = useState({
    name: '',
    category: 'Ø³ÙŠØ§Ø±Ø§Øª',
    price_per_hour: '',
    deposit_amount: 500,
    branch_id: '',
    description: '',
    image_url: '',
    status: 'Ù…ØªØ§Ø­',
    minimum_age: 16,
    max_speed: '',
    battery_life: '',
    weight_limit: ''
  });

  const categories = [
    'Ø³ÙŠØ§Ø±Ø§Øª', 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', 'Ø£Ø®Ø±Ù‰'
  ];

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Ø¬Ù„Ø¨ Ø§Ù„ÙØ±ÙˆØ¹
      const branchesResponse = await authService.getBranches();
      if (branchesResponse.success) {
        setBranches(branchesResponse.data);
        if (branchesResponse.data.length > 0) {
          setNewGame(prev => ({
            ...prev,
            branch_id: branchesResponse.data[0].id
          }));
        }
      }

      // Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      await loadGames();
      
    } catch (error) {
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadGames = async () => {
    try {
      const response = await authService.getGames();
      if (response.success) {
        // âœ… Ø¥ØµÙ„Ø§Ø­: Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙˆØ¶Ù…Ø§Ù† ÙˆØ¬ÙˆØ¯ price_per_hour
        const safeGames = response.data.map(game => ({
          ...game,
          price_per_hour: game.price_per_hour || 0,
          deposit_amount: game.deposit_amount || 0,
          rental_count: game.rental_count || 0,
          total_revenue: game.total_revenue || 0
        }));
        
        setGames(safeGames);
        calculateStats(safeGames);
      } else {
        setError(response.message);
      }
    } catch (error) {
      console.error('Error loading games:', error);
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨');
    }
  };

  const calculateStats = (gamesList) => {
    const totalGames = gamesList.length;
    const availableGames = gamesList.filter(g => g.status === 'Ù…ØªØ§Ø­').length;
    const rentedGames = gamesList.filter(g => g.status === 'Ù…Ø¤Ø¬Ø±Ø©').length;
    const maintenanceGames = gamesList.filter(g => g.status === 'ØµÙŠØ§Ù†Ø©').length;
    
    setStats({
      totalGames,
      availableGames,
      rentedGames,
      maintenanceGames
    });
  };

// Ø¥ØµÙ„Ø§Ø­ handleAddGame ÙÙŠ GamesManagement.jsx
const handleAddGame = async () => {
  try {
    if (!newGame.name || !newGame.price_per_hour || !newGame.branch_id) {
      alert('ÙŠØ±Ø¬Ù‰ Ù…Ù„Ø¡ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø­Ù‚ÙˆÙ„ Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©');
      return;
    }

    // âš¡ Ø¥ØµÙ„Ø§Ø­: ØªØ­ÙˆÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¥Ù„Ù‰ Ø§Ù„Ø£Ø±Ù‚Ø§Ù…
    const gameData = {
      name: newGame.name,
      category: newGame.category || 'Ø³ÙŠØ§Ø±Ø§Øª',
      price_per_hour: parseFloat(newGame.price_per_hour) || 0,
      deposit_amount: parseFloat(newGame.deposit_amount) || 500,
      branch_id: parseInt(newGame.branch_id),
      description: newGame.description || '',
      image_url: newGame.image_url || '',
      status: newGame.status || 'Ù…ØªØ§Ø­',
      minimum_age: parseInt(newGame.minimum_age) || 16,
      max_speed: newGame.max_speed || '',
      battery_life: newGame.battery_life || '',
      weight_limit: newGame.weight_limit || ''
    };

    console.log('ðŸ“¤ Ø¥Ø±Ø³Ø§Ù„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù„Ø¹Ø¨Ø©:', gameData);

    const response = await authService.createGame(gameData);
    
    if (response.success) {
      alert('âœ… ØªÙ… Ø¥Ø¶Ø§ÙØ© Ø§Ù„Ù„Ø¹Ø¨Ø© Ø¨Ù†Ø¬Ø§Ø­');
      setShowAddModal(false);
      resetNewGame();
      await loadGames();
    } else {
      alert(response.message || 'ÙØ´Ù„ ÙÙŠ Ø¥Ø¶Ø§ÙØ© Ø§Ù„Ù„Ø¹Ø¨Ø©');
    }
  } catch (error) {
    console.error('Error adding game:', error);
    alert('ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…: ' + error.message);
  }
};


const handleUpdateGame = async () => {
  if (!editingGame) return;

  try {
    // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©
    if (!editingGame.name || !editingGame.price_per_hour || !editingGame.branch_id) {
      alert('ÙŠØ±Ø¬Ù‰ Ù…Ù„Ø¡ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø­Ù‚ÙˆÙ„ Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©');
      return;
    }

    // âœ… Ø¥ØµÙ„Ø§Ø­: Ø§Ø³ØªØ®Ø¯Ø§Ù… authService Ø¨Ø¯Ù„Ø§Ù‹ Ù…Ù† api Ù…Ø¨Ø§Ø´Ø±Ø©
    const response = await authService.updateGame(editingGame.id, editingGame);
    
    if (response.success) {
      alert('âœ… ØªÙ… ØªØ­Ø¯ÙŠØ« Ø§Ù„Ù„Ø¹Ø¨Ø© Ø¨Ù†Ø¬Ø§Ø­');
      setShowEditModal(false);
      setEditingGame(null);
      await loadGames(); // Ø¥Ø¹Ø§Ø¯Ø© ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
    } else {
      alert(`âŒ ${response.message || 'ÙØ´Ù„ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ù„Ø¹Ø¨Ø©'}`);
    }
  } catch (error) {
    console.error('Error updating game:', error);
    alert('âŒ ØªØ¹Ø°Ø± Ø§Ù„Ø§ØªØµØ§Ù„ Ø¨Ø§Ù„Ø®Ø§Ø¯Ù…. ØªØ£ÙƒØ¯ Ù…Ù† ØªØ´ØºÙŠÙ„ Ø§Ù„Ø®Ø§Ø¯Ù…');
  }
};


  const handleDeleteGame = async (id, name) => {
    if (!window.confirm(`Ù‡Ù„ ØªØ±ÙŠØ¯ Ø­Ø°Ù Ø§Ù„Ù„Ø¹Ø¨Ø© "${name}"ØŸ\nÙ…Ù„Ø§Ø­Ø¸Ø©: Ù„Ø§ ÙŠÙ…ÙƒÙ† Ø­Ø°Ù Ø§Ù„Ù„Ø¹Ø¨Ø© Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ù…Ø±ØªØ¨Ø·Ø© Ø¨ØªØ£Ø¬ÙŠØ±Ø§Øª Ø³Ø§Ø¨Ù‚Ø©.`)) return;

    try {
      // âœ… Ø¥ØµÙ„Ø§Ø­: Ø§Ø³ØªØ®Ø¯Ø§Ù… authService Ø¨Ø¯Ù„Ø§Ù‹ Ù…Ù† AdminService
      const response = await authService.deleteGame(id);
      
      if (response.success) {
        alert('âœ… ØªÙ… Ø­Ø°Ù Ø§Ù„Ù„Ø¹Ø¨Ø© Ø¨Ù†Ø¬Ø§Ø­');
        loadGames();
        // ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ù†Ø´Ø§Ø·
        // if (activityService && activityService.log) {
        //   await activityService.log('delete', 'game', id, name, 
        //     `ØªÙ… Ø­Ø°Ù Ø§Ù„Ù„Ø¹Ø¨Ø©: ${name}`);
        // }
      } else {
