// frontend/src/services/rentalService.js
class RentalService {
  constructor() {
    this.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
  }

  getEmployeeStats = async () => {
    return {
      success: true,
      data: {
        available_games: 8,
        active_rentals: 0,
        completed_rentals: 0,
        canceled_rentals: 0
      }
    };
  };

  getActiveRentals = async () => {
    return {
      success: true,
      data: []
    };
  };

  getAvailableGames = async () => {
    const games = [
      { id: 1, name: 'دريفت كار', price_per_hour: 120, image_url: 'drift-car.jpg' },
      { id: 2, name: 'هافر بورد', price_per_hour: 60, image_url: 'hoverboard.jpg' },
      { id: 3, name: 'موتسكل كهربائي', price_per_hour: 100, image_url: 'electric-bike.jpg' },
      { id: 4, name: 'عربيه كهربائيه', price_per_hour: 150, image_url: 'electric-car.jpg' },
      { id: 5, name: 'سكوتر كهربائي', price_per_hour: 50, image_url: 'electric-scooter.jpg' },
      { id: 6, name: 'هارلي', price_per_hour: 180, image_url: 'harley.jpg' },
      { id: 7, name: 'سيجواي', price_per_hour: 70, image_url: 'segway.jpg' },
      { id: 8, name: 'كريزي كار', price_per_hour: 130, image_url: 'crazy-car.jpg' }
    ];
    
    return {
      success: true,
      data: games
    };
  };

  startRental = async (rentalData) => {
    return {
      success: true,
      message: 'تم بدء التأجير بنجاح (وضع الاختبار)',
      data: { id: Date.now(), ...rentalData, started_at: new Date().toISOString() }
    };
  };

  cancelRental = async (rentalId, reason = '') => {
    return {
      success: true,
      message: 'تم إلغاء التأجير (وضع الاختبار)'
    };
  };

  extendRental = async (rentalId) => {
    return {
      success: true,
      message: 'تم تمديد التأجير 15 دقيقة (وضع الاختبار)'
    };
  };
}

const rentalService = new RentalService();
export default rentalService;
