// src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('available');
  const [showRentalForm, setShowRentalForm] = useState(false);
  const [selectedRental, setSelectedRental] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [discount, setDiscount] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftLoading, setShiftLoading] = useState(true);
  
  // Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [stats, setStats] = useState({
    available: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // Refs Ù„Ù„Ù…Ø¤Ù‚ØªØ§Øª
  const refreshIntervalRef = useRef(null);
  const timersRef = useRef({});

  // Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ø«Ø§Ø¨ØªØ©
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©' },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©' },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†' },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª' },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª' }
  ];

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      loadShiftData().then(() => {
        loadData();
      });

      // Ø¶Ø¨Ø· interval Ù„ØªÙƒØ±Ø§Ø± Ø§Ù„ØªØ­Ù…ÙŠÙ„ ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      refreshIntervalRef.current = setInterval(() => {
        loadData();
      }, 30000);

      return () => {
        if (refreshIntervalRef.current) {
          clearInterval(refreshIntervalRef.current);
        }
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ==================== SHIFT MANAGEMENT ====================

  const loadShiftData = async () => {
    try {
      setShiftLoading(true);
      
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      
      if (response.success) {
        if (response.data) {
          setCurrentShift(response.data);
        } else {
          setCurrentShift(null);
        }
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
    } finally {
      setShiftLoading(false);
    }
  };

  const handleStartShift = async () => {
    try {
      const response = await api.startShift();
      
      if (response.success) {
        setCurrentShift(response.data);
        showNotification('success', 'ØªÙ… Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
      } else {
        showNotification('error', response.message || 'ÙØ´Ù„ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª:', error);
      showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

  const handleEndShift = async () => {
    if (!currentShift) return;
    
    try {
      const response = await api.endShift(currentShift.id);
      
      if (response.success) {
        showNotification('success', 'ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
        setCurrentShift(null);
      } else {
        showNotification('error', response.message || 'ÙØ´Ù„ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª');
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª:', error);
      showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

  // ==================== FUNCTIONS ====================

  const loadData = async () => {
    try {
      setLoading(true);
      
      if (!user?.branch_id) return;
      
      const tasks = [
        loadBranchData(),
        loadGamesData(),
        loadActiveRentals(),
        loadDashboardStats()
      ];
      
      await Promise.allSettled(tasks);
      
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ Ø¹Ø§Ù… ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadBranchData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get(`/branches/${user.branch_id}`);
      
      if (response.success) {
        setBranch(response.data);
      }
    } catch (error) {
      console.warn('âš ï¸ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', error.message);
    }
  };

  const loadGamesData = async () => {
    try {
      if (!user?.branch_id) return;
      
