import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import {
  Building2, Users, Gamepad2, Clock, DollarSign, ShoppingCart,
  CheckCircle, XCircle, AlertCircle, TrendingUp, Calendar,
  Printer, Download, RefreshCw, Eye, User, Phone, MapPin,
  ChevronLeft, ChevronRight, BarChart3, PieChart, Filter,
  TrendingDown, Activity, Zap, Award, Target, CreditCard,
  ArrowUp, ArrowDown, Loader2, Search, Mail, Package,
  Settings, LogOut, Bell, Moon, Sun, LayoutDashboard,
  Briefcase, Wallet, Receipt, FileText, Smartphone,
  Tablet, Laptop, Watch, Headphones, Camera, Speaker,
  Tv, Printer as PrinterIcon, Copy, Check, X
} from 'lucide-react';
import './AdminPages.css';

// ==================== دوال المساعدة العامة ====================
const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || amount === '') return '0 ج.م';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatNumber = (num) => {
  if (num === null || num === undefined) return '0';
  return new Intl.NumberFormat('ar-EG').format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/---- --:--';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const formatDate = (dateString) => {
  if (!dateString) return '--/--/----';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/----';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  } catch {
    return '--/--/----';
  }
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  if (isNaN(start.getTime()) || isNaN(end.getTime())) return 0;
  return Math.max(0, Math.round((end - start) / (1000 * 60)));
};

const getStatusColor = (status) => {
  switch(status?.toLowerCase()) {
    case 'نشط':
    case 'active':
      return 'success';
    case 'مكتمل':
    case 'completed':
      return 'info';
    case 'ملغي':
    case 'cancelled':
      return 'danger';
    case 'معلق':
    case 'pending':
      return 'warning';
    default:
      return 'secondary';
  }
};

const getStatusText = (status) => {
  switch(status?.toLowerCase()) {
    case 'active':
      return 'نشط';
    case 'completed':
      return 'مكتمل';
    case 'cancelled':
      return 'ملغي';
    case 'pending':
      return 'معلق';
    default:
      return status || 'غير معروف';
  }
};

// ==================== المكونات الفرعية ====================

// مكون بطاقة الإحصاء
const StatCard = React.memo(({ title, value, icon: Icon, color, trend, trendValue, subtitle, loading }) => {
  if (loading) {
    return (
      <div className="admin-stat-card admin-skeleton">
        <div className="admin-stat-skeleton"></div>
      </div>
    );
  }

  return (
    <div className={`admin-stat-card admin-stat-${color}`}>
      <div className="admin-stat-icon">
        <Icon size={24} />
      </div>
      <div className="admin-stat-content">
        <div className="admin-stat-title">{title}</div>
        <div className="admin-stat-value">{value}</div>
        {subtitle && <div className="admin-stat-subtitle">{subtitle}</div>}
        {trend && (
          <div className={`admin-stat-trend admin-trend-${trend > 0 ? 'up' : 'down'}`}>
            {trend > 0 ? <ArrowUp size={14} /> : <ArrowDown size={14} />}
            <span>{Math.abs(trend)}%</span>
          </div>
        )}
      </div>
    </div>
  );
});

// مكون بطاقة الفرع
const BranchCard = React.memo(({ branch, onClick }) => {
  const occupancyRate = branch.total_games > 0 
    ? Math.round(((branch.total_games - branch.available_games) / branch.total_games) * 100)
    : 0;

  return (
    <div className="admin-branch-card" onClick={() => onClick(branch)}>
      <div className="admin-branch-header">
        <div className="admin-branch-icon">
          <Building2 size={24} />
        </div>
        <div className="admin-branch-info">
          <h3>{branch.name}</h3>
          <span className="admin-branch-location">
            <MapPin size={14} />
            {branch.location || 'موقع غير محدد'}
          </span>
        </div>
      </div>
      
      <div className="admin-branch-stats">
        <div className="admin-branch-stat">
          <span className="admin-stat-label">الألعاب</span>
          <span className="admin-stat-value">{branch.total_games || 0}</span>
        </div>
        <div className="admin-branch-stat">
          <span className="admin-stat-label">متاحة</span>
          <span className="admin-stat-value admin-available">{branch.available_games || 0}</span>
        </div>
        <div className="admin-branch-stat">
          <span className="admin-stat-label">مؤجرة</span>
          <span className="admin-stat-value admin-rented">
            {(branch.total_games || 0) - (branch.available_games || 0)}
          </span>
        </div>
      </div>
      
      <div className="admin-branch-occupancy">
        <div className="admin-occupancy-label">
          <span>نسبة الإشغال</span>
          <span>{occupancyRate}%</span>
        </div>
        <div className="admin-progress-bar">
          <div 
            className={`admin-progress-fill ${occupancyRate > 70 ? 'admin-progress-high' : occupancyRate > 30 ? 'admin-progress-medium' : 'admin-progress-low'}`}
            style={{ width: `${occupancyRate}%` }}
          ></div>
        </div>
      </div>
      
      <div className="admin-branch-footer">
        <span className="admin-branch-revenue">
          <DollarSign size={14} />
          {formatCurrency(branch.today_revenue || 0)}
        </span>
        <span className="admin-branch-rentals">
          <ShoppingCart size={14} />
          {branch.active_rentals || 0} نشط
        </span>
      </div>
    </div>
  );
});

// مكون جدول التأجيرات
const RentalsTable = React.memo(({ rentals, loading, onViewDetails, type = 'all' }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  const filteredRentals = useMemo(() => {
    if (!rentals?.length) return [];
    
    return rentals.filter(rental => {
      // تصفية حسب النوع
      if (type === 'active' && rental.status !== 'نشط') return false;
      if (type === 'completed' && rental.status !== 'مكتمل') return false;
      
      // تصفية حسب البحث
      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        const matchesSearch = 
          rental.customer_name?.toLowerCase().includes(term) ||
          rental.customer_phone?.includes(term) ||
          rental.game_name?.toLowerCase().includes(term) ||
          rental.branch_name?.toLowerCase().includes(term);
        if (!matchesSearch) return false;
      }
      
      // تصفية حسب الحالة
      if (filterStatus !== 'all') {
        if (filterStatus === 'active' && rental.status !== 'نشط') return false;
        if (filterStatus === 'completed' && rental.status !== 'مكتمل') return false;
        if (filterStatus === 'cancelled' && rental.status !== 'ملغي') return false;
      }
      
      return true;
    });
  }, [rentals, searchTerm, filterStatus, type]);

  if (loading) {
    return (
      <div className="admin-loading-state">
        <Loader2 className="admin-spinner" size={32} />
        <p>جاري تحميل التأجيرات...</p>
      </div>
    );
  }

  return (
    <div className="admin-rentals-table-container">
      <div className="admin-table-filters">
        <div className="admin-search-box">
          <Search size={18} />
          <input
            type="text"
            placeholder="بحث بالعميل أو اللعبة أو الفرع..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {searchTerm && (
            <button onClick={() => setSearchTerm('')} className="admin-clear-search">
              <X size={16} />
            </button>
          )}
        </div>
        
        <select 
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="admin-filter-select"
        >
          <option value="all">جميع الحالات</option>
          <option value="active">نشط</option>
          <option value="completed">مكتمل</option>
          <option value="cancelled">ملغي</option>
        </select>
      </div>

      <div className="admin-rentals-table">
        <div className="admin-table-header">
          <div className="admin-table-cell">#</div>
          <div className="admin-table-cell">العميل</div>
          <div className="admin-table-cell">اللعبة</div>
          <div className="admin-table-cell">الفرع</div>
          <div className="admin-table-cell">وقت البدء</div>
          <div className="admin-table-cell">المدة</div>
          <div className="admin-table-cell">المبلغ</div>
          <div className="admin-table-cell">الحالة</div>
          <div className="admin-table-cell">الإجراءات</div>
        </div>

        {filteredRentals.length === 0 ? (
          <div className="admin-empty-state">
            {searchTerm ? (
              <>
                <Search size={48} />
                <p>لا توجد نتائج مطابقة للبحث</p>
              </>
            ) : (
              <>
                <Receipt size={48} />
                <p>لا توجد تأجيرات لعرضها</p>
              </>
            )}
          </div>
        ) : (
          filteredRentals.map((rental, index) => (
            <div key={rental.id} className="admin-table-row">
              <div className="admin-table-cell">{index + 1}</div>
              
              <div className="admin-table-cell">
                <div className="admin-customer-info">
                  <User size={16} />
                  <span>{rental.customer_name || 'غير محدد'}</span>
                </div>
                {rental.customer_phone && rental.customer_phone !== '00000000000' && (
                  <div className="admin-customer-phone">
                    <Phone size={12} />
                    <span>{rental.customer_phone}</span>
                  </div>
                )}
              </div>
              
              <div className="admin-table-cell">
                <div className="admin-game-info">
                  <Gamepad2 size={16} />
                  <span>{rental.game_name || 'غير محدد'}</span>
                </div>
              </div>
              
              <div className="admin-table-cell">
                <div className="admin-branch-info">
                  <Building2 size={16} />
                  <span>{rental.branch_name || 'غير محدد'}</span>
                </div>
              </div>
              
              <div className="admin-table-cell">
                <div className="admin-time-info">
                  <Clock size={14} />
                  <span>{formatDateTime(rental.start_time)}</span>
                </div>
              </div>
              
              <div className="admin-table-cell">
                {rental.duration_minutes ? `${rental.duration_minutes} د` : '--'}
              </div>
              
              <div className="admin-table-cell">
                <span className="admin-amount">
                  {formatCurrency(rental.total_amount || 0)}
                </span>
              </div>
              
              <div className="admin-table-cell">
                <span className={`admin-status-badge admin-status-${getStatusColor(rental.status)}`}>
                  {getStatusText(rental.status)}
                </span>
              </div>
              
              <div className="admin-table-cell">
                <button
                  onClick={() => onViewDetails(rental)}
                  className="admin-btn-icon"
                  title="عرض التفاصيل"
                >
                  <Eye size={16} />
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
});

// مكون تفاصيل التأجير
const RentalDetailsModal = React.memo(({ show, onClose, rental }) => {
  if (!show || !rental) return null;

  return (
    <div className="admin-modal-overlay" onClick={onClose}>
      <div className="admin-modal" onClick={e => e.stopPropagation()}>
        <div className="admin-modal-header">
          <h2>
            <Receipt size={24} />
            تفاصيل التأجير #{rental.rental_number || rental.id}
          </h2>
          <button onClick={onClose} className="admin-modal-close">
            <X size={24} />
          </button>
        </div>
        
        <div className="admin-modal-body">
          <div className="admin-details-grid">
            <div className="admin-details-section">
              <h3>معلومات العميل</h3>
              <div className="admin-details-row">
                <span className="admin-details-label">الاسم:</span>
                <span className="admin-details-value">{rental.customer_name || 'غير محدد'}</span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">الهاتف:</span>
                <span className="admin-details-value">{rental.customer_phone || 'غير محدد'}</span>
              </div>
            </div>
            
            <div className="admin-details-section">
              <h3>معلومات اللعبة</h3>
              <div className="admin-details-row">
                <span className="admin-details-label">اسم اللعبة:</span>
                <span className="admin-details-value">{rental.game_name || 'غير محدد'}</span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">الفرع:</span>
                <span className="admin-details-value">{rental.branch_name || 'غير محدد'}</span>
              </div>
            </div>
            
            <div className="admin-details-section">
              <h3>معلومات الوقت</h3>
              <div className="admin-details-row">
                <span className="admin-details-label">وقت البدء:</span>
                <span className="admin-details-value">{formatDateTime(rental.start_time)}</span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">وقت الانتهاء:</span>
                <span className="admin-details-value">{formatDateTime(rental.end_time)}</span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">المدة:</span>
                <span className="admin-details-value">
                  {rental.duration_minutes ? `${rental.duration_minutes} دقيقة` : 'غير محدد'}
                </span>
              </div>
            </div>
            
            <div className="admin-details-section">
              <h3>المعلومات المالية</h3>
              <div className="admin-details-row">
                <span className="admin-details-label">المبلغ الإجمالي:</span>
                <span className="admin-details-value admin-amount-large">
                  {formatCurrency(rental.total_amount || 0)}
                </span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">المبلغ المدفوع:</span>
                <span className="admin-details-value">
                  {formatCurrency(rental.paid_amount || 0)}
                </span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">حالة الدفع:</span>
                <span className="admin-details-value">
                  {rental.payment_status || 'غير محدد'}
                </span>
              </div>
            </div>
            
            <div className="admin-details-section">
              <h3>معلومات إضافية</h3>
              <div className="admin-details-row">
                <span className="admin-details-label">الحالة:</span>
                <span className={`admin-status-badge admin-status-${getStatusColor(rental.status)}`}>
                  {getStatusText(rental.status)}
                </span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">الموظف:</span>
                <span className="admin-details-value">{rental.employee_name || 'غير محدد'}</span>
              </div>
              <div className="admin-details-row">
                <span className="admin-details-label">ملاحظات:</span>
                <span className="admin-details-value">{rental.notes || 'لا توجد ملاحظات'}</span>
              </div>
            </div>
          </div>
        </div>
        
        <div className="admin-modal-footer">
          <button onClick={onClose} className="admin-btn admin-btn-secondary">
            إغلاق
          </button>
        </div>
      </div>
    </div>
  );
});

// مكون إحصائيات الفترة الزمنية
const TimeRangeStats = React.memo(({ stats, loading }) => {
  if (loading) {
    return (
      <div className="admin-time-stats admin-skeleton">
        <div className="admin-stats-skeleton"></div>
      </div>
    );
  }

  return (
    <div className="admin-time-stats">
      <div className="admin-stat-row">
        <div className="admin-stat-item">
          <span className="admin-stat-label">إجمالي الإيرادات</span>
          <span className="admin-stat-value">{formatCurrency(stats.total_revenue || 0)}</span>
        </div>
        <div className="admin-stat-item">
          <span className="admin-stat-label">عدد التأجيرات</span>
          <span className="admin-stat-value">{formatNumber(stats.total_rentals || 0)}</span>
        </div>
      </div>
      <div className="admin-stat-row">
        <div className="admin-stat-item">
          <span className="admin-stat-label">متوسط قيمة التأجير</span>
          <span className="admin-stat-value">{formatCurrency(stats.average_amount || 0)}</span>
        </div>
        <div className="admin-stat-item">
          <span className="admin-stat-label">أعلى إيراد في يوم</span>
          <span className="admin-stat-value">{formatCurrency(stats.max_daily_revenue || 0)}</span>
        </div>
      </div>
    </div>
  );
});

// مكون الرسم البياني البسيط
const SimpleChart = React.memo(({ data, type = 'bar', height = 200 }) => {
  if (!data || data.length === 0) {
    return (
      <div className="admin-chart-placeholder">
        <BarChart3 size={48} />
        <p>لا توجد بيانات كافية للعرض</p>
      </div>
    );
  }

  const maxValue = Math.max(...data.map(d => d.value), 1);

  return (
    <div className="admin-simple-chart" style={{ height }}>
      {data.map((item, index) => (
        <div key={index} className="admin-chart-bar-container">
          <div 
            className="admin-chart-bar"
            style={{ 
              height: `${(item.value / maxValue) * 100}%`,
              backgroundColor: item.color || '#3b82f6'
            }}
          >
            <span className="admin-chart-value">{item.value}</span>
          </div>
          <span className="admin-chart-label">{item.label}</span>
        </div>
      ))}
    </div>
  );
});

// ==================== المكون الرئيسي ====================
const AdminDashboard = () => {
  const { user } = useAuth();
  
  // حالات التحميل
  const [loading, setLoading] = useState({
    initial: true,
    stats: false,
    branches: false,
    rentals: false,
    games: false,
    charts: false
  });
  
  // حالات البيانات
  const [stats, setStats] = useState({
    total_branches: 0,
    total_users: 0,
    total_games: 0,
    available_games: 0,
    active_rentals: 0,
    today_revenue: 0,
    today_rentals: 0,
    today_completed: 0,
    total_revenue: 0,
    total_rentals: 0,
    average_daily_revenue: 0
  });
  
  const [branches, setBranches] = useState([]);
  const [rentals, setRentals] = useState([]);
  const [recentRentals, setRecentRentals] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [games, setGames] = useState([]);
  
  // حالات إضافية
  const [error, setError] = useState(null);
  const [selectedRental, setSelectedRental] = useState(null);
  const [showRentalDetails, setShowRentalDetails] = useState(false);
  const [timeRange, setTimeRange] = useState('today'); // today, week, month, year
  const [viewMode, setViewMode] = useState('overview'); // overview, branches, rentals, games
  
  // بيانات الرسوم البيانية
  const [revenueChartData, setRevenueChartData] = useState([]);
  const [rentalsChartData, setRentalsChartData] = useState([]);
  const [branchesChartData, setBranchesChartData] = useState([]);
  
  // Refs للتحكم في التحديثات
  const isMountedRef = useRef(true);
  const updateLockRef = useRef(false);
  const refreshTimerRef = useRef(null);
  const retryCountRef = useRef(0);

  // ==================== دوال التحميل ====================

  // تحميل جميع البيانات
  const loadAllData = useCallback(async (force = false) => {
    // منع التحميل المتزامن
    if (updateLockRef.current && !force) {
      console.log('⏳ [LOCKED] هناك تحميل قيد التنفيذ');
      return;
    }

    try {
      updateLockRef.current = true;
      setLoading(prev => ({ ...prev, initial: true }));
      setError(null);

      // تحميل البيانات الأساسية أولاً
      const [branchesRes, rentalsRes, gamesRes, statsRes] = await Promise.allSettled([
        api.get('/branches'),
        api.get('/rentals', { params: { limit: 1000 } }),
        api.get('/games'),
        api.get('/stats/dashboard')
      ]);

      // معالجة الفروع
      if (branchesRes.status === 'fulfilled' && branchesRes.value.success) {
        const branchesData = branchesRes.value.data || [];
        setBranches(branchesData);
        
        // تحميل إحصائيات كل فرع
        const branchStatsPromises = branchesData.map(async (branch) => {
          try {
            const [gamesRes, rentalsRes] = await Promise.allSettled([
              api.get(`/branches/${branch.id}/games`),
              api.get('/rentals', { params: { branch_id: branch.id, limit: 1 } })
            ]);

            const games = gamesRes.status === 'fulfilled' && gamesRes.value.success 
              ? gamesRes.value.data || [] 
              : [];

            const today = new Date().toISOString().split('T')[0];
            const todayRentals = rentalsRes.status === 'fulfilled' && rentalsRes.value.success
              ? (rentalsRes.value.data || []).filter(r => 
                  r.created_at?.startsWith(today) && r.status === 'مكتمل'
                )
              : [];

            const todayRevenue = todayRentals.reduce((sum, r) => 
              sum + (r.total_amount || 0), 0
            );

            return {
              ...branch,
              total_games: games.length,
              available_games: games.filter(g => g.status === 'متاح').length,
              active_rentals: rentalsRes.status === 'fulfilled' && rentalsRes.value.success
                ? (rentalsRes.value.data || []).filter(r => r.status === 'نشط').length
                : 0,
              today_revenue: todayRevenue,
              today_rentals: todayRentals.length
            };
          } catch (error) {
            console.error(`خطأ في تحميل إحصائيات الفرع ${branch.id}:`, error);
            return {
              ...branch,
              total_games: 0,
              available_games: 0,
              active_rentals: 0,
              today_revenue: 0,
              today_rentals: 0
            };
          }
        });

        const branchesWithStats = await Promise.all(branchStatsPromises);
        setBranches(branchesWithStats);
      }

      // معالجة التأجيرات
      if (rentalsRes.status === 'fulfilled' && rentalsRes.value.success) {
        const rentalsData = rentalsRes.value.data || [];
        setRentals(rentalsData);
        
        // تصنيف التأجيرات
        setActiveRentals(rentalsData.filter(r => r.status === 'نشط'));
        setCompletedRentals(rentalsData.filter(r => r.status === 'مكتمل'));
        setRecentRentals(rentalsData.slice(0, 10));
      }

      // معالجة الألعاب
      if (gamesRes.status === 'fulfilled' && gamesRes.value.success) {
        setGames(gamesRes.value.data || []);
      }

      // معالجة الإحصائيات
      if (statsRes.status === 'fulfilled' && statsRes.value.success) {
        setStats(statsRes.value.data);
      } else {
        // حساب الإحصائيات محلياً إذا فشل الطلب
        calculateLocalStats(branchesRes, rentalsRes, gamesRes);
      }

      // تحميل بيانات الرسوم البيانية
      await loadChartData();

      retryCountRef.current = 0;

    } catch (error) {
      console.error('❌ خطأ في تحميل البيانات:', error);
      setError('حدث خطأ في تحميل البيانات. يرجى المحاولة مرة أخرى.');
      
      // محاولة إعادة التحميل
      if (retryCountRef.current < 3) {
        retryCountRef.current++;
        const delay = Math.min(1000 * Math.pow(2, retryCountRef.current), 10000);
        
        refreshTimerRef.current = setTimeout(() => {
          loadAllData(force);
        }, delay);
      }
    } finally {
      if (isMountedRef.current) {
        setLoading(prev => ({ ...prev, initial: false }));
      }
      updateLockRef.current = false;
    }
  }, []);

  // حساب الإحصائيات محلياً
  const calculateLocalStats = useCallback((branchesRes, rentalsRes, gamesRes) => {
    const branchesData = branchesRes.status === 'fulfilled' && branchesRes.value.success 
      ? branchesRes.value.data || [] 
      : [];
    
    const rentalsData = rentalsRes.status === 'fulfilled' && rentalsRes.value.success 
      ? rentalsRes.value.data || [] 
      : [];
    
    const gamesData = gamesRes.status === 'fulfilled' && gamesRes.value.success 
      ? gamesRes.value.data || [] 
      : [];

    const today = new Date().toISOString().split('T')[0];
    const todayRentals = rentalsData.filter(r => 
      r.created_at?.startsWith(today) && r.status === 'مكتمل'
    );
    
    const todayRevenue = todayRentals.reduce((sum, r) => 
      sum + (r.total_amount || 0), 0
    );

    const totalRevenue = rentalsData
      .filter(r => r.status === 'مكتمل')
      .reduce((sum, r) => sum + (r.total_amount || 0), 0);

    setStats({
      total_branches: branchesData.length,
      total_users: 0, // سيتم تحميلها بشكل منفصل
      total_games: gamesData.length,
      available_games: gamesData.filter(g => g.status === 'متاح').length,
      active_rentals: rentalsData.filter(r => r.status === 'نشط').length,
      today_revenue: todayRevenue,
      today_rentals: todayRentals.length,
      today_completed: todayRentals.length,
      total_revenue: totalRevenue,
      total_rentals: rentalsData.length,
      average_daily_revenue: totalRevenue / 30 || 0 // تقديري
    });
  }, []);

  // تحميل بيانات الرسوم البيانية
  const loadChartData = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, charts: true }));

      // بيانات الإيرادات لآخر 7 أيام
      const last7Days = [];
      const revenueData = [];
      const rentalsData = [];

      for (let i = 6; i >= 0; i--) {
        const date = new Date();
        date.setDate(date.getDate() - i);
        const dateStr = date.toISOString().split('T')[0];
        
        const dayRentals = rentals.filter(r => 
          r.created_at?.startsWith(dateStr) && r.status === 'مكتمل'
        );
        
        const dayRevenue = dayRentals.reduce((sum, r) => 
          sum + (r.total_amount || 0), 0
        );

        last7Days.push({
          label: date.toLocaleDateString('ar-EG', { weekday: 'short' }),
          date: dateStr,
          revenue: dayRevenue,
          rentals: dayRentals.length
        });

        revenueData.push({
          label: date.toLocaleDateString('ar-EG', { weekday: 'short' }),
          value: dayRevenue,
          color: '#10b981'
        });

        rentalsData.push({
          label: date.toLocaleDateString('ar-EG', { weekday: 'short' }),
          value: dayRentals.length,
          color: '#3b82f6'
        });
      }

      setRevenueChartData(revenueData);
      setRentalsChartData(rentalsData);

      // بيانات الفروع
      const branchData = branches.slice(0, 5).map(branch => ({
        label: branch.name,
        value: branch.today_revenue || 0,
        color: `hsl(${Math.random() * 360}, 70%, 50%)`
      }));

      setBranchesChartData(branchData);

    } catch (error) {
      console.error('❌ خطأ في تحميل بيانات الرسوم البيانية:', error);
    } finally {
      if (isMountedRef.current) {
        setLoading(prev => ({ ...prev, charts: false }));
      }
    }
  }, [rentals, branches]);

  // التحميل الأولي
  useEffect(() => {
    isMountedRef.current = true;
    
    loadAllData(true);

    return () => {
      isMountedRef.current = false;
      if (refreshTimerRef.current) {
        clearTimeout(refreshTimerRef.current);
      }
    };
  }, [loadAllData]);

  // تحديث دوري كل 5 دقائق
  useEffect(() => {
    const interval = setInterval(() => {
      if (isMountedRef.current && !updateLockRef.current) {
        loadAllData();
      }
    }, 300000); // 5 دقائق

    return () => clearInterval(interval);
  }, [loadAllData]);

  // ==================== دوال المساعدة ====================

  const handleRefresh = useCallback(() => {
    loadAllData(true);
  }, [loadAllData]);

  const handleViewRentalDetails = useCallback((rental) => {
    setSelectedRental(rental);
    setShowRentalDetails(true);
  }, []);

  const handleViewBranch = useCallback((branch) => {
    // يمكن توجيه المستخدم إلى صفحة تفاصيل الفرع
    window.location.href = `/admin/branches/${branch.id}`;
  }, []);

  // ==================== Render ====================

  if (loading.initial) {
    return (
      <div className="admin-loading-screen">
        <div className="admin-spinner-large"></div>
        <p>جاري تحميل لوحة التحكم...</p>
        <small>يتم تحميل البيانات الحقيقية من النظام</small>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* رأس الصفحة */}
      <div className="admin-header">
        <div className="admin-header-left">
          <h1>
            <LayoutDashboard size={32} />
            لوحة تحكم المدير
          </h1>
          <div className="admin-welcome">
            <span className="admin-greeting">مرحباً،</span>
            <span className="admin-user-name">{user?.name}</span>
            <span className="admin-user-role">
              ({user?.role === 'admin' ? 'مدير النظام' : 'مدير فرع'})
            </span>
          </div>
        </div>
        
        <div className="admin-header-right">
          <div className="admin-date-time">
            <Calendar size={16} />
            <span>{new Date().toLocaleDateString('ar-EG', { 
              weekday: 'long', 
              year: 'numeric', 
              month: 'long', 
              day: 'numeric' 
            })}</span>
            <Clock size={16} />
            <span>{formatTime(new Date())}</span>
          </div>
          
          <button 
            onClick={handleRefresh}
            className="admin-btn admin-btn-outline"
            disabled={loading.stats}
          >
            {loading.stats ? (
              <Loader2 className="admin-spinner" size={16} />
            ) : (
              <RefreshCw size={16} />
            )}
            تحديث
          </button>
          
          <button 
            className="admin-btn admin-btn-primary"
            onClick={() => window.print()}
          >
            <Printer size={16} />
            طباعة
          </button>
        </div>
      </div>

      {/* رسالة الخطأ */}
      {error && (
        <div className="admin-alert admin-alert-error">
          <AlertCircle size={20} />
          <span>{error}</span>
          <button onClick={handleRefresh} className="admin-alert-close">
            <X size={16} />
          </button>
        </div>
      )}

      {/* بطاقات الإحصائيات الرئيسية */}
      <div className="admin-stats-grid">
        <StatCard
          title="إجمالي الفروع"
          value={formatNumber(stats.total_branches)}
          icon={Building2}
          color="primary"
          loading={loading.stats}
        />
        
        <StatCard
          title="إجمالي المستخدمين"
          value={formatNumber(stats.total_users)}
          icon={Users}
          color="success"
          loading={loading.stats}
        />
        
        <StatCard
          title="إجمالي الألعاب"
          value={formatNumber(stats.total_games)}
          icon={Gamepad2}
          color="info"
          subtitle={`${formatNumber(stats.available_games)} متاح`}
          loading={loading.stats}
        />
        
        <StatCard
          title="التأجيرات النشطة"
          value={formatNumber(stats.active_rentals)}
          icon={Activity}
          color="warning"
          loading={loading.stats}
        />
        
        <StatCard
          title="إيرادات اليوم"
          value={formatCurrency(stats.today_revenue)}
          icon={DollarSign}
          color="success"
          subtitle={`${formatNumber(stats.today_rentals)} تأجير`}
          loading={loading.stats}
        />
        
        <StatCard
          title="إجمالي الإيرادات"
          value={formatCurrency(stats.total_revenue)}
          icon={TrendingUp}
          color="primary"
          subtitle={`${formatNumber(stats.total_rentals)} تأجير`}
          loading={loading.stats}
        />
      </div>

      {/* لوحة التنقل بين الأقسام */}
      <div className="admin-view-tabs">
        <button
          className={`admin-tab ${viewMode === 'overview' ? 'active' : ''}`}
          onClick={() => setViewMode('overview')}
        >
          <PieChart size={18} />
          نظرة عامة
        </button>
        <button
          className={`admin-tab ${viewMode === 'branches' ? 'active' : ''}`}
          onClick={() => setViewMode('branches')}
        >
          <Building2 size={18} />
          الفروع
        </button>
        <button
          className={`admin-tab ${viewMode === 'rentals' ? 'active' : ''}`}
          onClick={() => setViewMode('rentals')}
        >
          <Receipt size={18} />
          التأجيرات
        </button>
        <button
          className={`admin-tab ${viewMode === 'games' ? 'active' : ''}`}
          onClick={() => setViewMode('games')}
        >
          <Gamepad2 size={18} />
          الألعاب
        </button>
      </div>

      {/* محتوى حسب وضع العرض */}
      <div className="admin-view-content">
        {viewMode === 'overview' && (
          <>
            {/* الرسم البياني للإيرادات */}
            <div className="admin-chart-card">
              <div className="admin-chart-header">
                <h3>
                  <BarChart3 size={20} />
                  تحليل الإيرادات (آخر 7 أيام)
                </h3>
                <select 
                  value={timeRange}
                  onChange={(e) => setTimeRange(e.target.value)}
                  className="admin-chart-select"
                >
                  <option value="today">اليوم</option>
                  <option value="week">هذا الأسبوع</option>
                  <option value="month">هذا الشهر</option>
                  <option value="year">هذا العام</option>
                </select>
              </div>
              <SimpleChart 
                data={revenueChartData} 
                type="bar" 
                height={250}
              />
            </div>

            {/* قسمين متجاورين */}
            <div className="admin-dashboard-grid">
              {/* آخر التأجيرات */}
              <div className="admin-card">
                <div className="admin-card-header">
                  <h3>
                    <Clock size={20} />
                    آخر التأجيرات
                  </h3>
                  <span className="admin-badge">{recentRentals.length}</span>
                </div>
                <div className="admin-recent-list">
                  {recentRentals.slice(0, 5).map((rental, index) => (
                    <div key={rental.id} className="admin-recent-item">
                      <div className="admin-recent-info">
                        <span className="admin-recent-index">{index + 1}</span>
                        <div className="admin-recent-details">
                          <span className="admin-recent-customer">{rental.customer_name}</span>
                          <span className="admin-recent-game">{rental.game_name}</span>
                        </div>
                      </div>
                      <div className="admin-recent-meta">
                        <span className="admin-recent-time">{formatTime(rental.start_time)}</span>
                        <span className={`admin-status-dot admin-status-${getStatusColor(rental.status)}`}></span>
                      </div>
                    </div>
                  ))}
                </div>
                <button 
                  className="admin-view-all"
                  onClick={() => setViewMode('rentals')}
                >
                  عرض الكل <ChevronLeft size={14} />
                </button>
              </div>

              {/* إحصائيات الفروع */}
              <div className="admin-card">
                <div className="admin-card-header">
                  <h3>
                    <Target size={20} />
                    أداء الفروع
                  </h3>
                  <span className="admin-badge">{branches.length} فرع</span>
                </div>
                <div className="admin-branches-mini">
                  {branches.slice(0, 3).map(branch => (
                    <div key={branch.id} className="admin-branch-mini">
                      <div className="admin-branch-mini-header">
                        <span className="admin-branch-mini-name">{branch.name}</span>
                        <span className="admin-branch-mini-revenue">
                          {formatCurrency(branch.today_revenue || 0)}
                        </span>
                      </div>
                      <div className="admin-branch-mini-stats">
                        <span>📊 {branch.active_rentals || 0} نشط</span>
                        <span>🎮 {branch.available_games || 0}/{branch.total_games || 0}</span>
                      </div>
                    </div>
                  ))}
                </div>
                <button 
                  className="admin-view-all"
                  onClick={() => setViewMode('branches')}
                >
                  عرض الكل <ChevronLeft size={14} />
                </button>
              </div>
            </div>
          </>
        )}

        {viewMode === 'branches' && (
          <div className="admin-branches-grid">
            {branches.map(branch => (
              <BranchCard 
                key={branch.id} 
                branch={branch} 
                onClick={handleViewBranch}
              />
            ))}
          </div>
        )}

        {viewMode === 'rentals' && (
          <div className="admin-rentals-view">
            <div className="admin-rentals-tabs">
              <button 
                className={`admin-rentals-tab ${!selectedRental?.type ? 'active' : ''}`}
                onClick={() => setSelectedRental(null)}
              >
                جميع التأجيرات ({rentals.length})
              </button>
              <button 
                className={`admin-rentals-tab ${selectedRental?.type === 'active' ? 'active' : ''}`}
                onClick={() => setSelectedRental({ type: 'active' })}
              >
                النشطة ({activeRentals.length})
              </button>
              <button 
                className={`admin-rentals-tab ${selectedRental?.type === 'completed' ? 'active' : ''}`}
                onClick={() => setSelectedRental({ type: 'completed' })}
              >
                المكتملة ({completedRentals.length})
              </button>
            </div>

            <RentalsTable
              rentals={selectedRental?.type === 'active' ? activeRentals : 
                       selectedRental?.type === 'completed' ? completedRentals : rentals}
              loading={loading.rentals}
              onViewDetails={handleViewRentalDetails}
              type={selectedRental?.type || 'all'}
            />
          </div>
        )}

        {viewMode === 'games' && (
          <div className="admin-games-view">
            <div className="admin-games-stats">
              <div className="admin-games-stat">
                <span className="admin-games-stat-label">إجمالي الألعاب</span>
                <span className="admin-games-stat-value">{formatNumber(stats.total_games)}</span>
              </div>
              <div className="admin-games-stat">
                <span className="admin-games-stat-label">الألعاب المتاحة</span>
                <span className="admin-games-stat-value admin-available">{formatNumber(stats.available_games)}</span>
              </div>
              <div className="admin-games-stat">
                <span className="admin-games-stat-label">الألعاب المؤجرة</span>
                <span className="admin-games-stat-value admin-rented">
                  {formatNumber(stats.total_games - stats.available_games)}
                </span>
              </div>
            </div>

            <div className="admin-games-grid">
              {games.slice(0, 12).map(game => (
                <div key={game.id} className="admin-game-card">
                  <div className="admin-game-image">
                    <img 
                      src={game.image_url || '/images/default-game.jpg'} 
                      alt={game.name}
                      onError={(e) => e.target.src = '/images/default-game.jpg'}
                    />
                  </div>
                  <div className="admin-game-info">
                    <h4>{game.name}</h4>
                    <div className="admin-game-details">
                      <span className="admin-game-price">{formatCurrency(game.price_per_15min)}/15د</span>
                      <span className={`admin-game-status ${game.status === 'متاح' ? 'admin-status-available' : 'admin-status-rented'}`}>
                        {game.status}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* نافذة تفاصيل التأجير */}
      <RentalDetailsModal
        show={showRentalDetails}
        onClose={() => setShowRentalDetails(false)}
        rental={selectedRental}
      />
    </div>
  );
};

export default AdminDashboard;
