import { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const BranchesManagement = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  const [loadingGames, setLoadingGames] = useState(false);
  const [gamesError, setGamesError] = useState('');
  
  const [newBranch, setNewBranch] = useState({
    name: '',
    location: '',
    city: 'ÿßŸÑŸÇÿßŸáÿ±ÿ©',
    contact_phone: '',
    contact_email: '',
    opening_time: '10:00:00',
    closing_time: '22:00:00'
  });

  const defaultGamesList = [
    { id: 1, name: 'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±', category: 'ÿ≥Ÿäÿßÿ±ÿßÿ™', suggestedPrice: 150, image: 'drift-car.jpg' },
    { id: 2, name: 'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ', category: 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', suggestedPrice: 80, image: 'hoverboard.jpg' },
    { id: 3, name: 'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ÿØÿ±ÿßÿ¨ÿßÿ™ ŸÜÿßÿ±Ÿäÿ©', suggestedPrice: 120, image: 'electric-bike.jpg' },
    { id: 4, name: 'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', category: 'ÿ≥Ÿäÿßÿ±ÿßÿ™', suggestedPrice: 200, image: 'electric-car.jpg' },
    { id: 5, name: 'ÿ≥ŸÉŸàÿ™ÿ± ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', suggestedPrice: 70, image: 'electric-scooter.jpg' },
    { id: 6, name: 'Ÿáÿßÿ±ŸÑŸä', category: 'ÿØÿ±ÿßÿ¨ÿßÿ™ ŸÜÿßÿ±Ÿäÿ©', suggestedPrice: 250, image: 'harley.jpg' },
    { id: 7, name: 'ÿ≥Ÿäÿ¨ŸàÿßŸä', category: 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', suggestedPrice: 90, image: 'segway.jpg' }
  ];

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getBranches();
      
      if (response.success) {
        setBranches(response.data);
      } else {
        setError(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ');
      }
    } catch (error) {
      setError('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
    } finally {
      setLoading(false);
    }
  };

  const loadBranchGames = async (branchId) => {
    try {
      setLoadingGames(true);
      setGamesError('');
      
      const response = await authService.getBranchGames(branchId);
      
      if (response.success) {
        // ‚úÖ ÿ™ÿµŸÅŸäÿ© ŸÅŸÇÿ∑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
        const availableGames = response.data.filter(game => game.status === 'ŸÖÿ™ÿßÿ≠');
        setBranchGames(availableGames);
      } else {
        const errorMessage = response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ';
        setGamesError(errorMessage);
        setBranchGames([]);
      }
    } catch (error) {
      setGamesError('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ: ' + error.message);
      setBranchGames([]);
    } finally {
      setLoadingGames(false);
    }
  };

  useEffect(() => {
    loadBranches();
  }, []);

  const handleAddBranch = async () => {
    if (!newBranch.name || !newBranch.location) {
      alert('ÿßÿ≥ŸÖ ÿßŸÑŸÅÿ±ÿπ ŸàÿßŸÑŸÖŸàŸÇÿπ ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ');
      return;
    }

    try {
      const response = await authService.createBranch(newBranch);
      
      if (response.success) {
        alert('‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅÿ±ÿπ ÿ®ŸÜÿ¨ÿßÿ≠');
        setShowAddModal(false);
        setNewBranch({
          name: '',
          location: '',
          city: 'ÿßŸÑŸÇÿßŸáÿ±ÿ©',
          contact_phone: '',
          contact_email: '',
          opening_time: '10:00:00',
          closing_time: '22:00:00'
        });
        loadBranches();
      } else {
        alert(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅÿ±ÿπ');
      }
    } catch (error) {
      alert('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const response = await authService.updateBranch(id, {
        is_active: currentStatus ? 0 : 1
      });
      
      if (response.success) {
        loadBranches();
      } else {
        alert(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ©');
      }
    } catch (error) {
      alert('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
    }
  };

  const handleDeleteBranch = async (id) => {
    const choice = window.confirm(
      'ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ÿßŸÑÿ≠ÿ∞ŸÅ:\n\n' +
      '"ŸÖŸàÿßŸÅŸÇ" ŸÑŸÑÿ≠ÿ∞ŸÅ ÿßŸÑŸÜŸáÿßÿ¶Ÿä (Ÿäÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÜŸáÿßÿ¶ŸäÿßŸã)\n' +
      '"ÿ•ŸÑÿ∫ÿßÿ°" ŸÑŸÑÿ™ÿπÿ∑ŸäŸÑ ŸÅŸÇÿ∑ (Ÿäÿ∏ŸÑ ÿßŸÑŸÅÿ±ÿπ ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸàŸÑŸÉŸÜŸá ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑)'
    );

    try {
      if (choice) {
        if (!window.confirm('‚ö†Ô∏è ÿ™ÿ≠ÿ∞Ÿäÿ±: Ÿáÿ∞ÿß ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ° ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿ™ÿ±ÿßÿ¨ÿπ ÿπŸÜŸá!\nŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ ŸÜŸáÿßÿ¶ŸäÿßŸã ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ÿü')) {
          return;
        }
        
        const response = await authService.deleteBranch(id);
        
        if (response.success) {
          alert('‚úÖ ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ ŸÜŸáÿßÿ¶ŸäÿßŸã ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
          loadBranches();
        } else {
          const deactivateResponse = await authService.updateBranch(id, { is_active: 0 });
          if (deactivateResponse.success) {
            alert('‚ö†Ô∏è ÿ™ŸÖ ÿ™ÿπÿ∑ŸäŸÑ ÿßŸÑŸÅÿ±ÿπ ŸÅŸÇÿ∑ (ŸÑÿß ŸäŸÖŸÉŸÜ ÿ≠ÿ∞ŸÅŸá ŸÜŸáÿßÿ¶ŸäÿßŸã ŸÑÿ£ŸÜŸá Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ£ŸÑÿπÿßÿ® ÿ£Ÿà ŸÖŸàÿ∏ŸÅŸäŸÜ)');
            loadBranches();
          } else {
            alert(deactivateResponse.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿ£Ÿà ÿ™ÿπÿ∑ŸäŸÑ ÿßŸÑŸÅÿ±ÿπ');
          }
        }
      } else {
        const response = await authService.updateBranch(id, { is_active: 0 });
        
        if (response.success) {
          alert('‚úÖ ÿ™ŸÖ ÿ™ÿπÿ∑ŸäŸÑ ÿßŸÑŸÅÿ±ÿπ ÿ®ŸÜÿ¨ÿßÿ≠');
          loadBranches();
        } else {
          alert(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿπÿ∑ŸäŸÑ ÿßŸÑŸÅÿ±ÿπ');
        }
      }
    } catch (error) {
      alert('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
    }
  };

  const handleOpenGamesModal = async (branch) => {
    setSelectedBranch(branch);
    setAvailableGames(defaultGamesList);
    setGamesError('');
    
    await loadBranchGames(branch.id);
    
    setShowGamesModal(true);
  };

  const handleAddGameToBranch = async (game) => {
    if (!selectedBranch) return;
    
    const isAlreadyAdded = branchGames.some(g => 
      g.name.toLowerCase() === game.name.toLowerCase()
    );
    
    if (isAlreadyAdded) {
      alert(`‚ö†Ô∏è ÿßŸÑŸÑÿπÿ®ÿ© "${game.name}" ŸÖŸàÿ¨ŸàÿØÿ© ÿ®ÿßŸÑŸÅÿπŸÑ ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑŸÅÿ±ÿπ`);
      return;
    }
    
    const price = prompt(`ÿ£ÿØÿÆŸÑ ÿ≥ÿπÿ± ÿßŸÑÿ≥ÿßÿπÿ© ŸÑŸÑÿπÿ®ÿ© "${game.name}" ÿ®ÿßŸÑÿ¨ŸÜŸäŸá:`, game.suggestedPrice);
    
    if (!price || isNaN(price) || parseFloat(price) <= 0) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿ≥ÿπÿ± ÿµÿ≠Ÿäÿ≠ ÿ£ŸÉÿ®ÿ± ŸÖŸÜ ÿßŸÑÿµŸÅÿ±');
      return;
    }

    try {
      const gameData = {
        name: game.name,
        description: game.name || "ŸÑÿπÿ®ÿ© ÿ¨ÿØŸäÿØÿ©",
        category: game.category || "ÿ≥Ÿäÿßÿ±ÿßÿ™",
        price_per_hour: parseFloat(price),
        price_per_15min: Math.ceil(parseFloat(price) / 4),
        branch_id: selectedBranch.id,
        status: 'ŸÖÿ™ÿßÿ≠',
        is_active: 1,
        min_rental_time: 15,
        max_rental_time: 120,
        minimum_age: 16,
        max_speed: parseInt(game.max_speed) || 30,
        image_url: game.image || ""
      };
      
      console.log("üì§ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ±ÿ≥ŸÑÿ©:", gameData);
      
      const response = await authService.createGame(gameData);
      
