import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import activityService from '../../services/activityService';
import './AdminPages.css';

const RentalsManagement = () => {
  const [rentals, setRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedRental, setSelectedRental] = useState(null);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    game_id: 'all',
    customer_name: '',
    rental_number: '',
    date_from: '',
    date_to: '',
    payment_status: 'all'
  });
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    completed: 0,
    cancelled: 0,
    totalRevenue: 0
  });
  
  const itemsPerPage = 15;

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // تحميل الفروع والألعاب
      const [branchesResponse, gamesResponse] = await Promise.all([
        authService.getBranches(),
        authService.getGames()
      ]);
      
      if (branchesResponse.success) setBranches(branchesResponse.data);
      if (gamesResponse.success) setGames(gamesResponse.data);
      
      // تحميل التأجيرات
      await loadRentals();
      
    } catch (error) {
      setError('تعذر تحميل البيانات');
      console.error('Load data error:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadRentals = async () => {
    try {
      // بناء params البحث
      const params = {};
      Object.keys(filters).forEach(key => {
        if (filters[key] && filters[key] !== 'all') {
          params[key] = filters[key];
        }
      });
      
      const response = await authService.getRentals(params);
      if (response.success) {
        setRentals(response.data);
        calculateStats(response.data);
      } else {
        setError(response.message);
      }
    } catch (error) {
      console.error('Error loading rentals:', error);
      setError('تعذر تحميل التأجيرات');
    }
  };

  const calculateStats = (rentalsList) => {
    const total = rentalsList.length;
    const active = rentalsList.filter(r => r.status === 'نشط').length;
    const completed = rentalsList.filter(r => r.status === 'مكتمل').length;
    const cancelled = rentalsList.filter(r => r.status === 'ملغي').length;
    const totalRevenue = rentalsList
      .filter(r => r.status === 'مكتمل' && r.payment_status === 'مدفوع')
      .reduce((sum, rental) => sum + (rental.total_amount || 0), 0);
    
    setStats({ total, active, completed, cancelled, totalRevenue });
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleApplyFilters = () => {
    setCurrentPage(1);
    setShowFilters(false);
    loadRentals();
  };

  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      branch_id: 'all',
      game_id: 'all',
      customer_name: '',
      rental_number: '',
      date_from: '',
      date_to: '',
      payment_status: 'all'
    });
    setCurrentPage(1);
    loadRentals();
  };

  const handleViewDetails = async (rental) => {
    setSelectedRental(rental);
    setShowDetailsModal(true);
  };

  const handleEndRental = async (rental) => {
    const paymentMethod = prompt('أدخل طريقة الدفع (كاش/بطاقة):', 'كاش');
    if (!paymentMethod) return;

    try {
      const response = await authService.endRental(rental.id, {
        payment_method: paymentMethod
      });
      
      if (response.success) {
        // تسجيل النشاط
        await activityService.log('update', 'rental', rental.id, rental.rental_number, 
          `تم إنهاء تأجير رقم: ${rental.rental_number}`);
        
        alert('✅ تم إنهاء التأجير بنجاح');
        loadRentals();
        setShowDetailsModal(false);
      } else {
        alert(response.message || 'فشل في إنهاء التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء إنهاء التأجير');
    }
  };

  const handleCancelRental = async (rental) => {
    const confirmCancel = window.confirm('هل تريد إلغاء هذا التأجير؟');
    if (!confirmCancel) return;

    try {
      const response = await authService.cancelRental(rental.id);
      
      if (response.success) {
        // تسجيل النشاط
        await activityService.log('update', 'rental', rental.id, rental.rental_number, 
          `تم إلغاء تأجير رقم: ${rental.rental_number}`);
        
        alert('✅ تم إلغاء التأجير بنجاح');
        loadRentals();
      } else {
        alert(response.message || 'فشل في إلغاء التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء إلغاء التأجير');
    }
  };

  const handleExtendRental = async (rental) => {
    const hours = prompt('أدخل عدد الساعات الإضافية:', '1');
    if (!hours || isNaN(hours) || hours <= 0) return;

    try {
      const response = await authService.extendRental(rental.id, {
        extra_hours: parseInt(hours)
      });
      
      if (response.success) {
        // تسجيل النشاط
        await activityService.log('update', 'rental', rental.id, rental.rental_number, 
          `تم تمديد تأجير رقم: ${rental.rental_number} لمدة ${hours} ساعة`);
        
        alert(`✅ تم تمديد التأجير ${hours} ساعة`);
        loadRentals();
      } else {
        alert(response.message || 'فشل في تمديد التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء تمديد التأجير');
    }
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number') return '0.00 ج';
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ج';
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatTimeOnly = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadgeClass = (status) => {
    switch(status?.toLowerCase()) {
      case 'نشط': return 'status-active';
      case 'مكتمل': return 'status-completed';
      case 'ملغي': return 'status-cancelled';
      default: return 'status-default';
    }
  };

  const getPaymentStatusBadgeClass = (status) => {
    switch(status?.toLowerCase()) {
      case 'مدفوع': return 'payment-paid';
      case 'غير مدفوع': return 'payment-unpaid';
      case 'جزئي': return 'payment-partial';
      default: return 'payment-default';
    }
  };

  const calculateRemainingTime = (startTime, durationMinutes) => {
    const endTime = new Date(new Date(startTime).getTime() + durationMinutes * 60000);
    const now = new Date();
    const diffMs = endTime - now;
    
    if (diffMs <= 0) return 'منتهي';
    
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMinutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));
    
    return `${diffHours} س ${diffMinutes} د`;
  };

  const filteredRentals = [...rentals].sort((a, b) => 
    new Date(b.created_at) - new Date(a.created_at)
  );

  const totalPages = Math.ceil(filteredRentals.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedRentals = filteredRentals.slice(startIndex, startIndex + itemsPerPage);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل التأجيرات...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <div className="header-content">
          <h1>
            <span className="header-icon">⏱️</span>
            إدارة التأجيرات
            <span className="rentals-count">{rentals.length} تأجير</span>
          </h1>
          <p className="page-subtitle">إدارة وتتبع جميع تأجيرات النظام</p>
        </div>
        <div className="header-actions">
          <button 
            className={`btn-secondary ${showFilters ? 'active' : ''}`}
            onClick={() => setShowFilters(!showFilters)}
          >
            🔍 البحث المتقدم
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
          <button className="btn-retry" onClick={loadData}>
            🔄 إعادة المحاولة
          </button>
        </div>
      )}

      {/* إحصائيات سريعة */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">📊</div>
          <div className="stat-content">
            <h3>إجمالي التأجيرات</h3>
            <h2>{stats.total}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">🚀</div>
          <div className="stat-content">
            <h3>النشطة حالياً</h3>
            <h2>{stats.active}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">✅</div>
          <div className="stat-content">
            <h3>المكتملة</h3>
            <h2>{stats.completed}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">💰</div>
          <div className="stat-content">
            <h3>إجمالي الإيرادات</h3>
            <h2>{formatCurrency(stats.totalRevenue)}</h2>
          </div>
        </div>
      </div>

      {/* البحث المتقدم */}
      {showFilters && (
        <div className="advanced-filters">
          <div className="filters-header">
            <h3>🔍 البحث المتقدم</h3>
            <button 
              className="close-filters"
              onClick={() => setShowFilters(false)}
            >
              ✕
            </button>
          </div>
          
          <div className="filters-grid">
            <div className="filter-group">
              <label>رقم العملية</label>
              <input
                type="text"
                value={filters.rental_number}
                onChange={(e) => handleFilterChange('rental_number', e.target.value)}
                placeholder="أدخل رقم التأجير"
                className="form-input"
              />
            </div>
            
            <div className="filter-group">
              <label>اسم العميل</label>
              <input
                type="text"
                value={filters.customer_name}
                onChange={(e) => handleFilterChange('customer_name', e.target.value)}
                placeholder="اسم العميل"
                className="form-input"
              />
            </div>
            
            <div className="filter-group">
              <label>الحالة</label>
              <select
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
                className="form-select"
              >
                <option value="all">جميع الحالات</option>
                <option value="نشط">نشط</option>
                <option value="مكتمل">مكتمل</option>
                <option value="ملغي">ملغي</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>حالة الدفع</label>
              <select
                value={filters.payment_status}
                onChange={(e) => handleFilterChange('payment_status', e.target.value)}
                className="form-select"
              >
                <option value="all">جميع الحالات</option>
                <option value="مدفوع">مدفوع</option>
                <option value="غير مدفوع">غير مدفوع</option>
                <option value="جزئي">جزئي</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>الفرع</label>
              <select
                value={filters.branch_id}
                onChange={(e) => handleFilterChange('branch_id', e.target.value)}
                className="form-select"
              >
                <option value="all">جميع الفروع</option>
                {branches.map(branch => (
                  <option key={branch.id} value={branch.id}>
                    {branch.name}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="filter-group">
              <label>اللعبة</label>
              <select
                value={filters.game_id}
                onChange={(e) => handleFilterChange('game_id', e.target.value)}
                className="form-select"
              >
                <option value="all">جميع الألعاب</option>
                {games.map(game => (
                  <option key={game.id} value={game.id}>
                    {game.name} - {game.branch_name}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="filter-group">
              <label>التاريخ من</label>
              <input
                type="date"
                value={filters.date_from}
                onChange={(e) => handleFilterChange('date_from', e.target.value)}
                className="form-input"
              />
            </div>
            
            <div className="filter-group">
              <label>التاريخ إلى</label>
              <input
                type="date"
                value={filters.date_to}
                onChange={(e) => handleFilterChange('date_to', e.target.value)}
                className="form-input"
              />
            </div>
          </div>
          
          <div className="filters-actions">
            <button className="btn-secondary" onClick={handleResetFilters}>
              🗑️ مسح الفلاتر
            </button>
            <button className="btn-primary" onClick={handleApplyFilters}>
              🔍 تطبيق البحث
            </button>
          </div>
        </div>
      )}

      {/* جدول التأجيرات */}
      <div className="table-container">
        <div className="table-header">
          <h3>📋 قائمة التأجيرات</h3>
          <div className="table-info">
            عرض {Math.min(itemsPerPage, paginatedRentals.length)} من {filteredRentals.length} تأجير
          </div>
        </div>
        
        {paginatedRentals.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">⏱️</div>
            <h3>لا توجد تأجيرات</h3>
            <p>لم يتم العثور على تأجيرات تطابق معايير البحث</p>
            <button className="btn-secondary" onClick={handleResetFilters}>
              عرض جميع التأجيرات
            </button>
          </div>
        ) : (
          <>
            <div className="table-responsive">
              <table className="data-table rentals-table">
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>الفرع</th>
                    <th>المدة</th>
                    <th>المبلغ</th>
                    <th>حالة الدفع</th>
                    <th>الحالة</th>
                    <th>الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
                  {paginatedRentals.map((rental) => (
                    <tr key={rental.id}>
                      <td>
                        <strong className="rental-number">#{rental.rental_number}</strong>
                        <div className="rental-dates">
                          <small>بداية: {formatTimeOnly(rental.start_time)}</small>
                        </div>
                      </td>
                      <td>
                        <div className="customer-info-cell">
                          <div className="customer-name">{rental.customer_name}</div>
                          <div className="customer-phone">{rental.customer_phone}</div>
                        </div>
                      </td>
                      <td>
                        <div className="game-info-cell">
                          <div className="game-name">{rental.game_name}</div>
                          <div className="game-price">{rental.price_per_hour} ج/ساعة</div>
                        </div>
                      </td>
                      <td>{rental.branch_name}</td>
                      <td>
                        <div className="duration-cell">
                          <div>{rental.duration_minutes} دقيقة</div>
                          {rental.status === 'نشط' && (
                            <div className="remaining-time">
                              ⏰ {calculateRemainingTime(rental.start_time, rental.duration_minutes)}
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="amount-cell">
                        <div className="amount">{formatCurrency(rental.total_amount)}</div>
                        {rental.payment_method && (
                          <div className="payment-method">
                            <small>{rental.payment_method}</small>
                          </div>
                        )}
                      </td>
                      <td>
                        <span className={`payment-badge ${getPaymentStatusBadgeClass(rental.payment_status)}`}>
                          {rental.payment_status}
                        </span>
                      </td>
                      <td>
                        <span className={`status-badge ${getStatusBadgeClass(rental.status)}`}>
                          {rental.status}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button 
                            className="btn-action btn-info"
                            onClick={() => handleViewDetails(rental)}
                            title="تفاصيل التأجير"
                          >
                            👁️
                          </button>
                          
                          {rental.status === 'نشط' && (
                            <>
                              <button 
                                className="btn-action btn-success"
                                onClick={() => handleEndRental(rental)}
                                title="إنهاء التأجير"
                              >
                                ✅
                              </button>
                              <button 
                                className="btn-action btn-warning"
                                onClick={() => handleExtendRental(rental)}
                                title="تمديد التأجير"
                              >
                                ⏱️
                              </button>
                              <button 
                                className="btn-action btn-danger"
                                onClick={() => handleCancelRental(rental)}
                                title="إلغاء التأجير"
                              >
                                ❌
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* الترقيم */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  السابق
                </button>
                <div className="pagination-numbers">
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      className={`pagination-number ${currentPage === page ? 'active' : ''}`}
                      onClick={() => setCurrentPage(page)}
                    >
                      {page}
                    </button>
                  ))}
                </div>
                <button 
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  التالي
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Modal تفاصيل التأجير */}
      {showDetailsModal && selectedRental && (
        <div className="modal-overlay">
          <div className="modal wide-modal">
            <div className="modal-header">
              <h2>📋 تفاصيل التأجير #{selectedRental.rental_number}</h2>
              <button 
                className="close-btn"
                onClick={() => setShowDetailsModal(false)}
              >
                ✕
              </button>
            </div>
            <div className="modal-body">
              <div className="rental-details-grid">
                <div className="detail-section">
                  <h3>👤 معلومات العميل</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">الاسم:</span>
                      <span className="detail-value">{selectedRental.customer_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">الهاتف:</span>
                      <span className="detail-value">{selectedRental.customer_phone}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">البريد الإلكتروني:</span>
                      <span className="detail-value">{selectedRental.customer_email || 'غير محدد'}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">ملاحظات:</span>
                      <span className="detail-value">{selectedRental.customer_notes || 'لا يوجد'}</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>🎮 معلومات اللعبة</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">اسم اللعبة:</span>
                      <span className="detail-value">{selectedRental.game_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">الفرع:</span>
                      <span className="detail-value">{selectedRental.branch_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">التصنيف:</span>
                      <span className="detail-value">{selectedRental.game_category || 'غير محدد'}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">السعر/ساعة:</span>
                      <span className="detail-value">{selectedRental.price_per_hour} ج</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>⏱️ معلومات التأجير</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">تاريخ البدء:</span>
                      <span className="detail-value">{formatDate(selectedRental.start_time)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">المدة المتفق عليها:</span>
                      <span className="detail-value">{selectedRental.duration_minutes} دقيقة</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">تاريخ الانتهاء المتوقع:</span>
                      <span className="detail-value">{formatDate(selectedRental.expected_end_time)}</span>
                    </div>
                    {selectedRental.actual_end_time && (
                      <div className="detail-item">
                        <span className="detail-label">تاريخ الانتهاء الفعلي:</span>
                        <span className="detail-value">{formatDate(selectedRental.actual_end_time)}</span>
                      </div>
                    )}
                    <div className="detail-item">
                      <span className="detail-label">أنشئ بواسطة:</span>
                      <span className="detail-value">{selectedRental.created_by_name || 'غير معروف'}</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>💰 المعلومات المالية</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">المبلغ الإجمالي:</span>
                      <span className="detail-value amount">{formatCurrency(selectedRental.total_amount)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">مبلغ التأمين:</span>
                      <span className="detail-value">{formatCurrency(selectedRental.deposit_amount || 0)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">حالة الدفع:</span>
                      <span className={`detail-value ${getPaymentStatusBadgeClass(selectedRental.payment_status)}`}>
                        {selectedRental.payment_status}
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">طريقة الدفع:</span>
                      <span className="detail-value">{selectedRental.payment_method || 'غير محدد'}</span>
                    </div>
                    {selectedRental.transaction_id && (
                      <div className="detail-item">
                        <span className="detail-label">رقم المعاملة:</span>
                        <span className="detail-value">{selectedRental.transaction_id}</span>
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {selectedRental.notes && (
                <div className="detail-section full-width">
                  <h3>📝 ملاحظات إضافية</h3>
                  <div className="notes-box">
                    {selectedRental.notes}
                  </div>
                </div>
              )}
            </div>
            <div className="modal-footer">
              <button 
                className="btn-secondary"
                onClick={() => setShowDetailsModal(false)}
              >
                إغلاق
              </button>
              {selectedRental.status === 'نشط' && (
                <button 
                  className="btn-primary"
                  onClick={() => handleEndRental(selectedRental)}
                >
                  إنهاء التأجير
                </button>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RentalsManagement;
