// frontend/src/pages/employee/Dashboard.jsx (Ù…Ø­Ø¯Ø«)
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { 
  Gamepad2, Clock, Users, DollarSign, Zap, Package, 
  TrendingUp, AlertCircle, RefreshCw, BarChart3,
  Calendar, CheckCircle, Loader2, ShoppingCart,
  Building, MapPin, User as UserIcon
} from 'lucide-react';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalGames: 0,
    totalCustomers: 0
  });
  
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [loading, setLoading] = useState({
    stats: true,
    games: true,
    rentals: true,
    shift: true
  });
  const [lastUpdate, setLastUpdate] = useState(null);
  const [refreshing, setRefreshing] = useState(false);

  const loadDashboardData = useCallback(async () => {
    try {
      const currentUser = authService.getCurrentUser();
      setUser(currentUser);
      
      // ØªØ­Ù…ÙŠÙ„ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙØ±Ø¹
      await loadBranchInfo();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
      await loadCurrentShift();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
      await loadDashboardStats();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©
      await loadAvailableGames();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
      await loadActiveRentals();
      
      // ØªØ­Ù…ÙŠÙ„ Ø¢Ø®Ø± Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      await loadRecentRentals();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ø£ÙƒØ«Ø± ØªØ£Ø¬ÙŠØ±Ø§Ù‹
      await loadTopGames();
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading({ stats: false, games: false, rentals: false, shift: false });
      setLastUpdate(new Date());
    }
  }, []);

  const loadBranchInfo = async () => {
    try {
      if (user?.branch_id) {
        const response = await api.get(`/branches/${user.branch_id}`);
        if (response.data.success) {
          setBranch(response.data.data);
        }
      }
    } catch (error) {
      console.error('Error loading branch info:', error);
    }
  };

  const loadCurrentShift = async () => {
    try {
      const response = await api.get('/shifts/current');
      if (response.data.success && response.data.data) {
        setCurrentShift(response.data.data);
      }
    } catch (error) {
      console.error('Error loading current shift:', error);
    }
  };

  const loadDashboardStats = async () => {
    try {
      setLoading(prev => ({ ...prev, stats: true }));
      
      const response = await api.get('/dashboard/stats');
      if (response.data.success) {
        const statsData = response.data.data || {};
        
        setStats({
          availableGames: statsData.available_games || 0,
          activeRentals: statsData.active_rentals || 0,
          todayRevenue: statsData.today_revenue || 0,
          todayRentals: statsData.today_rentals || 0,
          totalGames: statsData.total_games || 0,
          totalCustomers: statsData.total_customers || 0
        });
      }
    } catch (error) {
      console.error('Error loading dashboard stats:', error);
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  };

  const loadAvailableGames = async () => {
    try {
      setLoading(prev => ({ ...prev, games: true }));
      
      if (user?.branch_id) {
        const response = await api.get('/games/available', {
          params: { branch_id: user.branch_id }
        });
        
        if (response.data.success) {
          setAvailableGames(response.data.data || []);
        }
      }
    } catch (error) {
      console.error('Error loading available games:', error);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  };

  const loadActiveRentals = async () => {
    try {
      setLoading(prev => ({ ...prev, rentals: true }));
      
      if (user?.branch_id) {
        const response = await api.get('/rentals/active', {
          params: { branch_id: user.branch_id }
        });
        
        if (response.data.success) {
          setActiveRentals(response.data.data || []);
        }
      }
    } catch (error) {
      console.error('Error loading active rentals:', error);
    } finally {
      setLoading(prev => ({ ...prev, rentals: false }));
    }
  };

  const loadRecentRentals = async () => {
    try {
      const response = await api.get('/rentals/recent', {
        params: { limit: 5, branch_id: user?.branch_id }
      });
      
      if (response.data.success) {
        setRecentRentals(response.data.data || []);
      }
    } catch (error) {
      console.error('Error loading recent rentals:', error);
    }
  };

  const loadTopGames = async () => {
    try {
      const response = await api.get('/games/top-rented', {
        params: { branch_id: user?.branch_id, limit: 3 }
      });
      
      if (response.data.success) {
        setTopGames(response.data.data || []);
      }
    } catch (error) {
      console.error('Error loading top games:', error);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadDashboardData();
    setRefreshing(false);
  };

  const handleStartShift = async () => {
    try {
      const response = await api.post('/shifts/start');
      if (response.data.success) {
        await loadCurrentShift();
        await loadDashboardData();
      }
    } catch (error) {
      console.error('Error starting shift:', error);
      alert('ÙØ´Ù„ ÙÙŠ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

  const handleEndShift = async () => {
    if (window.confirm('Ù‡Ù„ ØªØ±ÙŠØ¯ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠØŸ')) {
      try {
        const response = await api.put(`/shifts/${currentShift.id}/end`);
        if (response.data.success) {
          setCurrentShift(null);
          await loadDashboardData();
          alert('ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
        }
      } catch (error) {
        console.error('Error ending shift:', error);
        alert('ÙØ´Ù„ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª');
      }
    }
  };

  useEffect(() => {
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« ØªÙ„Ù‚Ø§Ø¦ÙŠ ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    const interval = setInterval(() => {
      loadDashboardData();
    }, 60000);
    
    return () => clearInterval(interval);
  }, [loadDashboardData]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDateTime = (dateString) => {
    return new Date(dateString).toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const calculateTimeLeft = (startTime, expectedDuration) => {
    if (!startTime || !expectedDuration) return 'ØºÙŠØ± Ù…ØªØ§Ø­';
    
    const start = new Date(startTime);
    const expectedEnd = new Date(start.getTime() + expectedDuration * 60000);
    const now = new Date();
    const diff = expectedEnd - now;
    
    if (diff <= 0) return 'Ù…Ù†ØªÙ‡ÙŠ';
    
    const minutes = Math.floor(diff / 60000);
    if (minutes < 60) return `${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`;
    return `${Math.floor(minutes / 60)} Ø³Ø§Ø¹Ø© ${minutes % 60} Ø¯Ù‚ÙŠÙ‚Ø©`;
  };

  const StatCard = ({ icon: Icon, title, value, description, color = 'blue' }) => (
    <div className={`stat-card stat-card-${color}`}>
      <div className="stat-icon">
        <Icon size={24} />
      </div>
      <div className="stat-content">
        <h3>{title}</h3>
        <h2>{loading.stats ? <div className="stat-skeleton"></div> : value}</h2>
        <p>{description}</p>
      </div>
    </div>
  );

  const GameCard = ({ game }) => (
    <div className="game-card">
      <div className="game-image">
        <img 
          src={game.image_url || '/images/default-game.jpg'} 
          alt={game.name}
          onError={(e) => e.target.src = '/images/default-game.jpg'}
        />
      </div>
      <div className="game-info">
        <h4>{game.name}</h4>
        <p className="game-category">{game.category}</p>
        <p className="game-price">{formatCurrency(game.price_per_15min)} / 15 Ø¯Ù‚ÙŠÙ‚Ø©</p>
        <span className="game-status available">Ù…ØªØ§Ø­</span>
      </div>
    </div>
  );

  const RentalCard = ({ rental }) => (
    <div className="rental-card">
      <div className="rental-header">
        <h4>{rental.game_name}</h4>
        <span className={`rental-type ${rental.is_open_time ? 'open' : 'fixed'}`}>
          {rental.is_open_time ? 'ÙˆÙ‚Øª Ù…ÙØªÙˆØ­' : 'ÙˆÙ‚Øª Ø«Ø§Ø¨Øª'}
        </span>
      </div>
      <div className="rental-details">
        <p><UserIcon size={14} /> {rental.customer_name}</p>
        <p><Clock size={14} /> Ø¨Ø¯Ø£: {formatTime(rental.start_time)}</p>
        <p>â±ï¸ Ù…ØªØ¨Ù‚ÙŠ: {calculateTimeLeft(rental.start_time, rental.duration_minutes)}</p>
        <p>ðŸ’° {formatCurrency(rental.total_amount)}</p>
      </div>
    </div>
  );

  if (loading.stats && loading.games && loading.rentals) {
