import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Search, Filter, Download, RefreshCw, Eye, CheckCircle, Clock, 
  XCircle, Edit, MoreVertical, ChevronDown, ChevronUp, 
  Building, User, Calendar, DollarSign, AlertCircle,
  Save, Trash2, Plus, FilterX, ChevronLeft, ChevronRight,
  Check, X, FileText, BarChart3
} from 'lucide-react';
import api from '../../services/api';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== STATES ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(null);
  const [saving, setSaving] = useState(false);
  
  // Data States
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [employees, setEmployees] = useState([]);
  
  // Modal States
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showFiltersModal, setShowFiltersModal] = useState(false);
  const [showQuickFilters, setShowQuickFilters] = useState(true);
  const [showBulkActions, setShowBulkActions] = useState(false);
  
  // Selection & Editing
  const [selectedRental, setSelectedRental] = useState(null);
  const [editedRental, setEditedRental] = useState(null);
  const [selectedRentals, setSelectedRentals] = useState([]);
  
  // Filter States
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    game_id: 'all',
    customer_name: '',
    rental_number: '',
    date_from: '',
    date_to: '',
    payment_status: 'all',
    min_amount: '',
    max_amount: '',
    employee_id: 'all',
    shift_id: '',
    game_category: 'all',
    duration_type: 'all'
  });
  
  const [expandedFilters, setExpandedFilters] = useState({
    date: false,
    amount: false,
    advanced: false
  });
  
  // Search & Pagination
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(15);
  const [sortConfig, setSortConfig] = useState({ key: 'created_at', direction: 'desc' });
  
  // Statistics
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    completed: 0,
    cancelled: 0,
    refunded: 0,
    totalRevenue: 0,
    pendingPayments: 0,
    todaysRentals: 0,
    byBranch: {},
    byStatus: {}
  });

  // ==================== LOAD DATA ====================
  const loadAllData = async () => {
    try {
      setLoading(true);
      
      // Ø¬Ù„Ø¨ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø§Ù„ØªÙˆØ§Ø²ÙŠ
      const [branchesRes, gamesRes, rentalsRes] = await Promise.all([
        api.getBranches(),
        api.getGames({ is_active: true }),
        api.getRentals({ 
          limit: 1000,
          include_game: true,
          include_branch: true 
        })
      ]);
      
      if (branchesRes.success) setBranches(branchesRes.data || []);
      if (gamesRes.success) setGames(gamesRes.data || []);
      if (rentalsRes.success) setRentals(rentalsRes.data || []);
      
    } catch (error) {
      console.error('Error loading data:', error);
      showNotification('error', 'Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
    } finally {
      setLoading(false);
    }
  };

  // ==================== EFFECTS ====================
  useEffect(() => {
