import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // üî• ÿ£ÿ∂ŸÅ useNavigate ŸáŸÜÿß
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';
import {
  Gamepad2, Zap, DollarSign, Clock, Users,
  CheckCircle, AlertCircle, RefreshCw, Building,
  Loader2, Calendar, TrendingUp, User,
  PowerOff, LogOut, Bell, History,
  Package, BarChart3, Activity,
  // üî• ÿ£ÿ∂ŸÅ Ÿáÿ∞Ÿá ÿßŸÑÿ£ŸäŸÇŸàŸÜÿßÿ™ ÿßŸÑŸÖŸÅŸÇŸàÿØÿ©
  ShoppingCart, ChevronRight, Eye, Lightbulb
} from 'lucide-react';

const EmployeeDashboard = () => {
  // ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿ≠ŸÑŸäÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
  
  const [dashboardData, setDashboardData] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    completedRentals: 0,
    currentShift: null,
    shiftEmployees: [],
    branchInfo: null,
    rentalStats: {
      fixedTime: 0,
      openTime: 0,
      totalAmount: 0
    }
  });
  
  // ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÖŸÜŸÅÿµŸÑÿ©
  const navigate = useNavigate(); 
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [refreshInterval, setRefreshInterval] = useState(null);
  const [shiftEnded, setShiftEnded] = useState(false);
  const [lastShiftInfo, setLastShiftInfo] = useState(null);
  
  // ÿ™ÿπÿ±ŸäŸÅ ÿßŸÑŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ŸÉÿßŸÜÿ™ ŸÖŸÅŸÇŸàÿØÿ©
  const [games, setGames] = useState([]);
  const [shiftEmployees, setShiftEmployees] = useState([]);
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ÿßÿ≥ÿ™ÿÆÿ±ÿßÿ¨ currentShift ŸÖŸÜ dashboardData
  const currentShift = dashboardData.currentShift;
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿπŸÖŸÑÿ©
  const formatCurrency = (amount) => {
    if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑŸàŸÇÿ™
  const formatTime = (dateString) => {
    if (!dateString) return '--:--';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  };
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ŸàÿßŸÑŸàŸÇÿ™
  const formatDateTime = (dateString) => {
    if (!dateString) return '--/--/---- --:--';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--/--/---- --:--';
    }
  };

const loadShiftEmployees = async (shiftId) => {
  try {
    console.log(`üë• ÿ¨ŸÑÿ® ŸÖŸàÿ∏ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™ ${shiftId}`);
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ£ŸàŸÑŸâ: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©
    try {
      const response = await api.get(`/shifts/${shiftId}/employees`);
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ£ŸàŸÑŸâ:', error.message);
    }
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ´ÿßŸÜŸäÿ©: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿØŸäŸÑÿ©
    try {
      const response = await api.get('/shifts/current-employees', {
        params: { shift_id: shiftId }
      });
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ´ÿßŸÜŸäÿ©:', error.message);
    }
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ´ÿßŸÑÿ´ÿ©: ÿ•ÿ±ÿ¨ÿßÿπ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸÇÿ∑
    return user ? [{
      id: user.id,
      name: user.name,
      role: user.role,
      email: user.email,
      total_rentals: 0
    }] : [];
    
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ŸÖŸàÿ∏ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    return [];
  }
};
  const goToRentals = () => {
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ£ŸàŸÑÿßŸã ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑
    if (!dashboardData.currentShift) {
      alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      handleStartShiftFromDashboard(); // ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
      return;
    }
    
    // ÿßŸÑÿ∞Ÿáÿßÿ® ŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿπ ŸÖÿπÿ±ŸÅ ÿßŸÑÿ¥ŸäŸÅÿ™
    navigate(`/employee/rentals?shift=${dashboardData.currentShift.id}&quick=true`);
  };
  
  // üî• ÿ≤ÿ± ÿ≥ÿ±Ÿäÿπ ŸÑŸÑŸàÿµŸàŸÑ ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const QuickRentalButton = () => (
    <button onClick={goToRentals} className="quick-rental-fab">
      <Zap size={24} />
      <span>ÿßÿ®ÿØÿ£ ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ</span>
    </button>
  );
  
const QuickNavigationSection = () => (
  <div className="quick-navigation-section">
    <h2>üöÄ ÿ™ŸÜŸÇŸÑ ÿ≥ÿ±Ÿäÿπ</h2>
    <div className="quick-nav-grid">
      {/* ÿ®ÿ∑ÿßŸÇÿ© ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± */}
      <button 
        onClick={() => {
          if (!dashboardData.currentShift) {
            alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÑŸÑŸàÿµŸàŸÑ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
            handleStartShiftFromDashboard();
            return;
          }
          navigate('/employee/rentals');
        }} 
        className="quick-nav-card"
      >
        <div className="nav-icon" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
          <Gamepad2 size={24} />
        </div>
        <div className="nav-content">
          <h3>ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</h3>
          <p>ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ¨ÿØŸäÿØÿ©</p>
          <span className="nav-badge">
            {dashboardData.activeRentals || 0} ŸÜÿ¥ÿ∑ÿ©
          </span>
        </div>
        <div className="nav-arrow">‚Üí</div>
      </button>
      
      {/* ÿ®ÿ∑ÿßŸÇÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© */}
      <Link 
        to={`/employee/rentals?tab=active`} 
        className="quick-nav-card"
        onClick={(e) => {
          if (!dashboardData.currentShift) {
            e.preventDefault();
            alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©');
          }
        }}
      >
        <div className="nav-icon" style={{ background: 'linear-gradient(135deg, #ed8936 0%, #dd6b20 100%)' }}>
          <Clock size={24} />
        </div>
        <div className="nav-content">
          <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
          <p>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¨ÿßÿ±Ÿäÿ©</p>
        </div>
        <div className="nav-arrow">‚Üí</div>
      </Link>
      
      {/* ÿ®ÿ∑ÿßŸÇÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© */}
      <Link 
        to={`/employee/rentals?tab=completed`} 
        className="quick-nav-card"
        onClick={(e) => {
          if (!dashboardData.currentShift) {
            e.preventDefault();
            alert('‚ö†Ô∏è Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©');
          }
        }}
      >
        <div className="nav-icon" style={{ background: 'linear-gradient(135deg, #48bb78 0%, #38a169 100%)' }}>
          <CheckCircle size={24} />
        </div>
        <div className="nav-content">
          <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©</h3>
          <p>ÿπÿ±ÿ∂ ÿßŸÑŸÅŸàÿßÿ™Ÿäÿ± ŸàÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±</p>
        </div>
        <div className="nav-arrow">‚Üí</div>
      </Link>
      
      {/* ÿ®ÿ∑ÿßŸÇÿ© ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ */}
      <button 
        onClick={handleManualRefresh} 
        className="quick-nav-card"
      >
        <div className="nav-icon" style={{ background: 'linear-gradient(135deg, #4299e1 0%, #3182ce 100%)' }}>
          <RefreshCw size={24} />
        </div>
        <div className="nav-content">
          <h3>ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™</h3>
          <p>ÿ¨ŸÑÿ® ÿ£ÿ≠ÿØÿ´ ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™</p>
        </div>
        <div className="nav-arrow">‚Üª</div>
      </button>
    </div>
  </div>
);
// ŸÅŸä Dashboard.jsx
const loadCompletedRentals = async () => {
  try {
    if (!currentShift?.id) return [];
    
    const response = await api.getCompletedRentalsSmart(currentShift.id, user?.branch_id);
    
    if (response.success) {
      return response.data || [];
    }
    
    return [];
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
    return [];
  }
};


const loadActiveRentalsForShift = async (shiftId, branchId) => {
  try {
    console.log(`üìã ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ŸÑŸÑÿ¥ŸäŸÅÿ™ ${shiftId}`);
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 1: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿØŸäŸÑÿ©
    const response = await api.get('/rentals/active', {
      params: {
        branch_id: branchId
      }
    });
    
