    }
  };
  
  // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
  const calculateTimeLeft = (rental) => {
    if (!rental || !rental.start_time) return '--:--';
    
    const isOpenTime = rental.rental_type === 'open' || rental.is_open_time === 1;
    
    if (isOpenTime) {
      // ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠: ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedSeconds = Math.floor((now - startTime) / 1000);
      const minutes = Math.floor(elapsedSeconds / 60);
      const seconds = elapsedSeconds % 60;
      return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    } else {
