        game_id: game.id,
        game_name: game.name,
        price_per_15min: game.price_per_15min,
        rental_type: 'fixed',
        duration_minutes: 15,
        quantity: 1,
        child_name: ''
      }
    ]);

    setShowAddGameModal(false);
  }, [currentShift, isEmployee]);

  const handleUpdateCartItem = useCallback((itemId, updates) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === itemId ? { ...item, ...updates } : item
      )
    );
  }, []);

  const handleRemoveCartItem = useCallback((itemId) => {
    setCartItems(prev => prev.filter(item => item.id !== itemId));
  }, []);

  const handleClearCart = useCallback(() => {
    setCartItems([]);
    setCustomerInfo({ name: '', phone: '' });
  }, []);

  // ==================== Ø¯ÙˆØ§Ù„ Ø§Ù„ØªØ£Ø¬ÙŠØ± ====================

  const handleCreateRental = useCallback(async (data) => {
    if (!currentShift) {
      setError('âŒ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
      return;
    }

    if (cartItems.length === 0) {
      setError('âŒ Ø§Ù„Ø³Ù„Ø© ÙØ§Ø±ØºØ©');
      return;
    }

    if (!customerInfo.name?.trim()) {
      setError('âŒ Ø§Ø³Ù… Ø§Ù„Ø¹Ù…ÙŠÙ„ Ù…Ø·Ù„ÙˆØ¨');
      return;
    }

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      // ØªØ¬Ù‡ÙŠØ² Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ØªØ£Ø¬ÙŠØ±
      const rentalData = {
        customer_name: customerInfo.name.trim(),
        customer_phone: customerInfo.phone || null,
        items: cartItems.map(item => ({
          game_id: item.game_id,
          child_name: item.child_name || null,
          duration_minutes: item.rental_type === 'fixed' ? item.duration_minutes : null,
          quantity: item.quantity,
          rental_type: item.rental_type
        })),
        payment_method: data.payment_method,
        discount: data.discount || 0,
        notes: data.notes || ''
      };

      const response = await api.post('/rentals', rentalData);

      if (response.success) {
        setSuccess(`âœ… ØªÙ… Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø±Ù‚Ù… ${response.data.rental_number} Ø¨Ù†Ø¬Ø§Ø­`);
        handleClearCart();
        await loadActiveRentals();
        if (isManager) {
          await loadShiftStats(currentShift.id);
        }
        await refreshRentals();
      } else {
        setError(`âŒ ÙØ´Ù„ Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±: ${response.message}`);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±:', error);
      setError(`âŒ ÙØ´Ù„ Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift, cartItems, customerInfo, handleClearCart, loadActiveRentals, loadShiftStats, refreshRentals, isManager]);

  const handleCompleteOpenRental = useCallback(async (rental, data) => {
    if (!rental) return;

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.post(`/rentals/${rental.id}/complete-open`, {
        payment_method: data.payment_method,
        actual_minutes: data.actual_minutes,
        final_amount: data.final_amount
      });

      if (response.success) {
        setSuccess(`âœ… ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø¨Ù†Ø¬Ø§Ø­`);
        await loadActiveRentals();
        if (isManager) {
          await loadShiftStats(currentShift?.id);
        }
        await refreshRentals();
      } else {
        setError(`âŒ ÙØ´Ù„ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±: ${response.message}`);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±:', error);
      setError('Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats, refreshRentals, isManager]);

  const handleCancelRental = useCallback(async (rental) => {
    if (!isManager) {
      setError('âŒ Ù„ÙŠØ³ Ù„Ø¯ÙŠÙƒ ØµÙ„Ø§Ø­ÙŠØ© Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª');
      return;
    }

    if (!window.confirm('Ù‡Ù„ Ø£Ù†Øª Ù…ØªØ£ÙƒØ¯ Ù…Ù† Ø¥Ù„ØºØ§Ø¡ Ù‡Ø°Ø§ Ø§Ù„ØªØ£Ø¬ÙŠØ±ØŸ')) return;

    setLoading(prev => ({ ...prev, processing: true }));
    try {
      const response = await api.post(`/rentals/${rental.id}/cancel`);

      if (response.success) {
        setSuccess('âœ… ØªÙ… Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
        await loadActiveRentals();
        await loadShiftStats(currentShift?.id);
        await refreshRentals();
      } else {
        setError(`âŒ ÙØ´Ù„ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±: ${response.message}`);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±:', error);
      setError('Ø­Ø¯Ø« Ø®Ø·Ø£ ÙÙŠ Ø¥Ù„ØºØ§Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats, refreshRentals, isManager]);

  // ==================== Effects ====================

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£ÙˆÙ„ÙŠØ©
  useEffect(() => {
    refreshAllData();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¹Ù†Ø¯ ØªØºÙŠÙŠØ± Ø§Ù„Ø´ÙŠÙØª
  useEffect(() => {
    if (currentShift?.id) {
      loadActiveRentals();
      if (isManager) {
        loadShiftStats(currentShift.id);
      }
    }
  }, [currentShift?.id, loadActiveRentals, loadShiftStats, isManager]);

  // ØªØ­Ø¯ÙŠØ« Ø¯ÙˆØ±ÙŠ Ù„Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© (ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©)
  useEffect(() => {
    if (!currentShift?.id) return;

    const interval = setInterval(() => {
      loadActiveRentals();
    }, 30000);

    return () => clearInterval(interval);
  }, [currentShift?.id, loadActiveRentals]);

  // Ø¥Ø®ÙØ§Ø¡ Ø±Ø³Ø§Ø¦Ù„ Ø§Ù„Ù†Ø¬Ø§Ø­/Ø§Ù„Ø®Ø·Ø£ Ø¨Ø¹Ø¯ 5 Ø«ÙˆØ§Ù†ÙŠ
  useEffect(() => {
    if (error || success) {
      const timer = setTimeout(() => {
        setError(null);
        setSuccess(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error, success]);

  // ==================== Render ====================

  return (
    <div className="rentals-page">
      <ShiftStatusBar
        currentShift={currentShift}
        shiftStats={shiftStats}
        onStartShift={handleStartShift}
        onEndShift={handleEndShift}
        loading={loading.processing}
        userRole={userRole}
      />

      <div className="rentals-content">
        <div className="main-panel">
          <GamesGrid
            games={games}
            branchId={user?.branch_id}
            onAddToCart={handleAddToCart}
            loading={loading}
            currentShift={currentShift}
            userRole={userRole}
          />

          <ActiveRentals
            rentals={activeRentals}
            items={rentalItems}
            loading={loading}
            onComplete={(rental) => {
              setSelectedRental(rental);
              setShowCompleteOpenModal(true);
            }}
            onCancel={handleCancelRental}
            onViewDetails={(rental) => {
              setSelectedRental(rental);
              setShowRentalDetailsModal(true);
            }}
            currentShift={currentShift}
            userRole={userRole}
          />
        </div>

        <div className="cart-panel">
          <RentalCart
            items={cartItems}
            onUpdateItem={handleUpdateCartItem}
            onRemoveItem={handleRemoveCartItem}
            onClearCart={handleClearCart}
            customerInfo={customerInfo}
            onCustomerInfoChange={(field, value) => 
              setCustomerInfo(prev => ({ ...prev, [field]: value }))
            }
            onSubmit={handleCreateRental}
            isSubmitting={loading.processing}
            games={games}
            onAddGame={() => setShowAddGameModal(true)}
            currentShift={currentShift}
            userRole={userRole}
          />
        </div>
      </div>

      <AddGameModal
        show={showAddGameModal}
        onClose={() => setShowAddGameModal(false)}
        games={games}
        onAddGame={handleAddToCart}
      />

      <RentalDetailsModal
        show={showRentalDetailsModal}
        onClose={() => {
          setShowRentalDetailsModal(false);
          setSelectedRental(null);
        }}
        rental={selectedRental}
        items={rentalItems}
        userRole={userRole}
      />

      <CompleteOpenRentalModal
        show={showCompleteOpenModal}
        onClose={() => {
          setShowCompleteOpenModal(false);
          setSelectedRental(null);
        }}
        rental={selectedRental}
        items={rentalItems}
        onConfirm={handleCompleteOpenRental}
        userRole={userRole}
      />

      {/* Ø±Ø³Ø§Ø¦Ù„ Ø§Ù„ØªÙ†Ø¨ÙŠÙ‡ */}
      {error && (
        <div className="toast error">
          <AlertCircle size={20} />
          <span>{error}</span>
          <button onClick={() => setError(null)}>
            <X size={16} />
          </button>
        </div>
      )}

      {success && (
        <div className="toast success">
          <CheckCircle size={20} />
          <span>{success}</span>
          <button onClick={() => setSuccess(null)}>
            <X size={16} />
          </button>
        </div>
      )}

      {/* Ù…Ø¤Ø´Ø± Ø§Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø®Ù„ÙÙŠ */}
      {loading.processing && (
        <div className="global-loading">
          <Loader2 className="spinner" size={32} />
        </div>
      )}
    </div>
  );
};

export default Rentals;
