// pages/admin/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0,
    branchRevenues: [],
    completedRentalsByBranch: []
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ø§Ù„Ø¢Ù†');
  const [retryCount, setRetryCount] = useState(0);
  const [lastUpdate, setLastUpdate] = useState(null);

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      
      const currentUser = authService.getCurrentUser();
      setUser(currentUser);
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø´ÙƒÙ„ Ù…ØªÙˆØ§Ø²ÙŠ Ù…Ø¹ Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø£Ø®Ø·Ø§Ø¡ Ø§Ù„ÙØ±Ø¯ÙŠØ©
      const [
        allStatsResponse,
        branchesResponse,
        topCustomersResponse,
        recentRentalsResponse
      ] = await Promise.allSettled([
        api.getDashboardAllStats(),
        api.getBranches(),
        api.getBestCustomers(5),
        api.getRentals({ 
          limit: 10, 
          order_by: 'created_at', 
          order_direction: 'DESC',
          include_game: true,
          include_branch: true
        })
      ]);

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´Ø§Ù…Ù„Ø©
      if (allStatsResponse.status === 'fulfilled' && allStatsResponse.value.success) {
        const statsData = allStatsResponse.value.data;
        
        setStats(prev => ({
          ...prev,
          totalRevenue: statsData.totalRevenue || 0,
          totalRentals: statsData.totalRentals || 0,
          todayRevenue: statsData.todayRevenue || 0,
          todayRentals: statsData.todayRentals || 0,
          totalUsers: statsData.totalUsers || 0,
          totalGames: statsData.totalGames || 0,
          totalCustomers: statsData.totalCustomers || 0,
          branchRevenues: statsData.branchRevenues || [],
          completedRentalsByBranch: statsData.completedRentalsByBranch || [],
          totalBranches: statsData.totalBranches || 0
        }));
        
        if (statsData.topCustomers && Array.isArray(statsData.topCustomers)) {
          setTopCustomers(statsData.topCustomers);
        }
        
        setLastUpdate(statsData.generatedAt || new Date().toISOString());
      } else {
        // Ø¥Ø°Ø§ ÙØ´Ù„Øª Ø§Ø³ØªØ¬Ø§Ø¨Ø© getDashboardAllStatsØŒ Ø§Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø·Ø±Ù‚ Ø§Ù„Ø¨Ø¯ÙŠÙ„Ø©
        await loadFallbackData();
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ø£Ø®ÙŠØ±Ø©
      if (recentRentalsResponse.status === 'fulfilled' && recentRentalsResponse.value.success) {
        setRecentRentals(recentRentalsResponse.value.data || []);
      }

      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ† ÙÙŠ Ø§Ù„Ø®Ù„ÙÙŠØ©
      loadBackgroundData();

      setRetryCount(0); // Ø¥Ø¹Ø§Ø¯Ø© ØªØ¹ÙŠÙŠÙ† Ø¹Ø¯Ø§Ø¯ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø§Øª

    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ù„ÙˆØ­Ø© Ø§Ù„ØªØ­ÙƒÙ…:', error);
      
      if (retryCount < 2) {
        setRetryCount(prev => prev + 1);
        setTimeout(() => loadDashboardData(), 2000); // Ø¥Ø¹Ø§Ø¯Ø© Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ø¨Ø¹Ø¯ 2 Ø«Ø§Ù†ÙŠØ©
      } else {
        setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. Ø¬Ø§Ø±Ù Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø­Ù„ÙŠØ©...');
        await loadFallbackData();
      }
    } finally {
      setLoading(false);
    }
  }, [retryCount]);

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¨Ø¯ÙŠÙ„Ø©
  const loadFallbackData = async () => {
    try {
      // Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø·Ø±Ù‚ Ø§Ù„Ø¨Ø¯ÙŠÙ„Ø©
      const [
        todayStats,
        branchesData,
        customersData,
        rentalsData
      ] = await Promise.allSettled([
        api.getTodayStatsEnhanced(),
        api.getBranchesStatsEnhanced(),
        api.getBestCustomers(5),
        api.getRentals({ 
          limit: 10, 
          order_by: 'created_at', 
          order_direction: 'DESC' 
        })
      ]);

      // ØªØ¬Ù…ÙŠØ¹ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
      const fallbackStats = {
        totalRevenue: 0,
        totalRentals: 0,
        todayRevenue: 0,
        todayRentals: 0,
        totalUsers: users.length,
        totalGames: games.length,
        totalCustomers: 0,
        branchRevenues: [],
        completedRentalsByBranch: [],
        totalBranches: 0
      };

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙŠÙˆÙ…
      if (todayStats.status === 'fulfilled') {
        const todayData = todayStats.value.data || {};
        fallbackStats.todayRevenue = todayData.revenue || 0;
        fallbackStats.todayRentals = todayData.rentals || 0;
        fallbackStats.totalRevenue = todayData.revenue || 0;
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesData.status === 'fulfilled') {
        const branchesResult = branchesData.value.data || {};
        fallbackStats.branchRevenues = branchesResult.branchRevenues || [];
        fallbackStats.completedRentalsByBranch = branchesResult.completedRentalsByBranch || [];
        fallbackStats.totalBranches = branchesResult.totalBranches || 0;
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¹Ù…Ù„Ø§Ø¡
      if (customersData.status === 'fulfilled') {
        const customersResult = customersData.value.data || [];
        setTopCustomers(customersResult);
        fallbackStats.totalCustomers = customersResult.length;
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ø£Ø®ÙŠØ±Ø©
      if (rentalsData.status === 'fulfilled' && rentalsData.value.success) {
        setRecentRentals(rentalsData.value.data || []);
        fallbackStats.totalRentals = rentalsData.value.data?.length || 0;
      }

      setStats(prev => ({ ...prev, ...fallbackStats }));
      setLastUpdate(new Date().toISOString());

    } catch (fallbackError) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¨Ø¯ÙŠÙ„Ø©:', fallbackError);
      
      // Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ© ÙÙŠ Ø­Ø§Ù„ ÙØ´Ù„ ÙƒÙ„ Ø´ÙŠØ¡
      const demoStats = {
        totalRevenue: 15000,
        totalRentals: 120,
        todayRevenue: 2500,
        todayRentals: 15,
        totalUsers: 8,
        totalGames: 45,
        totalCustomers: 85,
        branchRevenues: [
          { branchId: 1, branchName: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', revenue: 1500 },
          { branchId: 2, branchName: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©', revenue: 1000 },
          { branchId: 3, branchName: 'ÙØ±Ø¹ Ø§Ù„Ø´Ø±Ù‚', revenue: 750 }
        ],
        completedRentalsByBranch: [
          { branchId: 1, branchName: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', count: 8 },
          { branchId: 2, branchName: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©', count: 5 },
          { branchId: 3, branchName: 'ÙØ±Ø¹ Ø§Ù„Ø´Ø±Ù‚', count: 2 }
        ],
        totalBranches: 3
      };
      
      setStats(prev => ({ ...prev, ...demoStats }));
      
      const demoCustomers = [
        { id: 1, name: 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯', phone: '0123456789', rental_count: 12, total_spent: 5000 },
        { id: 2, name: 'Ø³Ø§Ø±Ø© Ø¹Ù„ÙŠ', phone: '0111223344', rental_count: 8, total_spent: 3200 },
        { id: 3, name: 'Ø®Ø§Ù„Ø¯ Ø­Ø³ÙŠÙ†', phone: '0105566778', rental_count: 6, total_spent: 2800 }
      ];
      
      setTopCustomers(demoCustomers);
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙÙŠ Ø§Ù„Ø®Ù„ÙÙŠØ©
  const loadBackgroundData = async () => {
    try {
      const [gamesData, usersData] = await Promise.allSettled([
        api.getGames({ limit: 50 }),
        api.getUsers()
      ]);

      if (gamesData.status === 'fulfilled' && gamesData.value.success) {
        setGames(gamesData.value.data || []);
      }

      if (usersData.status === 'fulfilled' && usersData.value.success) {
        setUsers(usersData.value.data || []);
      }
    } catch (error) {
      console.log('âš ï¸ ØªØ­Ø°ÙŠØ± ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø®Ù„ÙÙŠØ©:', error);
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ© Ø¹Ù†Ø¯ Ø§Ù„Ù†Ù‚Ø± Ø¹Ù„Ù‰ ØªØ­Ø¯ÙŠØ«
  const handleRefreshData = async () => {
    setLoading(true);
    try {
      await loadDashboardData();
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
      setError('ÙØ´Ù„ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 3 Ø¯Ù‚Ø§Ø¦Ù‚
    const dataInterval = setInterval(loadDashboardData, 3 * 60 * 1000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, [loadDashboardData]);

  useEffect(() => {
    updateTimeAgo();
  }, [lastUpdate, currentTime]);

  const updateTimeAgo = () => {
    if (!lastUpdate) return;
    
    const lastUpdateTime = new Date(lastUpdate);
    const now = new Date();
    const diffMs = now - lastUpdateTime;
    const diffMins = Math.floor(diffMs / 60000);
    
    if (diffMins < 1) {
      setTimeAgo('Ø§Ù„Ø¢Ù†');
    } else if (diffMins === 1) {
      setTimeAgo('Ù‚Ø¨Ù„ Ø¯Ù‚ÙŠÙ‚Ø©');
    } else if (diffMins < 60) {
      setTimeAgo(`Ù‚Ø¨Ù„ ${diffMins} Ø¯Ù‚ÙŠÙ‚Ø©`);
    } else {
      const hours = Math.floor(diffMins / 60);
      setTimeAgo(`Ù‚Ø¨Ù„ ${hours} Ø³Ø§Ø¹Ø©`);
    }
  };

  // ÙˆØ¸Ø§Ø¦Ù Ø§Ù„ØªÙ†Ø³ÙŠÙ‚
  const formatCurrency = (amount) => {
    if (typeof amount !== 'number' || isNaN(amount)) {
      return '0.00 Ø¬';
    }
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' Ø¬';
  };

  const formatNumber = (number) => {
    if (typeof number !== 'number' || isNaN(number)) {
      return '0';
    }
    return number.toLocaleString('ar-EG');
  };

  const formatTime = (date) => {
    if (!date) return '--:--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const formatDate = (dateString) => {
    if (!dateString) return '--/--/----';
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit'
    });
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '--:-- --/--/----';
    const date = new Date(dateString);
    return `${date.toLocaleTimeString('ar-EG', { 
      hour: '2-digit', 
      minute: '2-digit' 
    })} - ${formatDate(dateString)}`;
  };

  const getBranchPerformance = (revenue) => {
    if (revenue === 0) return 'no-activity';
    if (revenue < 1000) return 'low';
    if (revenue < 5000) return 'medium';
    return 'high';
  };

  const calculatePerformancePercentage = (branch) => {
    const totalRevenue = stats.branchRevenues.reduce((sum, b) => sum + (b.revenue || 0), 0);
    if (totalRevenue === 0) return 0;
    return Math.round((branch.revenue / totalRevenue) * 100);
  };

  if (loading && retryCount === 0) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <div className="spinner-ring">
            <div></div>
            <div></div>
            <div></div>
