import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const RentalsManagement = () => {
  const [rentals, setRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedRental, setSelectedRental] = useState(null);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    branch: 'all',
    dateFrom: '',
    dateTo: ''
  });
  const [branches, setBranches] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      
      // تحميل الفروع
      const branchesResponse = await authService.getBranches();
      if (branchesResponse.success) {
        setBranches(branchesResponse.data);
      }
      
      // تحميل التأجيرات
      await loadRentals();
      
    } catch (error) {
      setError('تعذر تحميل البيانات');
      console.error('Load data error:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadRentals = async () => {
    try {
      const params = {};
      if (filters.status !== 'all') params.status = filters.status;
      if (filters.branch !== 'all') params.branch_id = filters.branch;
      
      const response = await authService.getRentals(params);
      if (response.success) {
        setRentals(response.data);
      } else {
        setError(response.message);
      }
    } catch (error) {
      console.error('Error loading rentals:', error);
      setError('تعذر تحميل التأجيرات');
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const handleApplyFilters = () => {
    setCurrentPage(1);
    loadRentals();
  };

  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      branch: 'all',
      dateFrom: '',
      dateTo: ''
    });
    setCurrentPage(1);
    loadRentals();
  };

  const handleViewDetails = async (rental) => {
    setSelectedRental(rental);
    setShowDetailsModal(true);
  };

  const handleEndRental = async (rentalId) => {
    const paymentMethod = prompt('أدخل طريقة الدفع:', 'كاش');
    if (!paymentMethod) return;

    try {
      const response = await authService.endRental(rentalId, {
        payment_method: paymentMethod
      });
      
      if (response.success) {
        alert('✅ تم إنهاء التأجير بنجاح');
        loadRentals();
      } else {
        alert(response.message || 'فشل في إنهاء التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء إنهاء التأجير');
    }
  };

  const handleCancelRental = async (rentalId) => {
    const confirmCancel = window.confirm('هل تريد إلغاء هذا التأجير؟');
    if (!confirmCancel) return;

    try {
      const response = await authService.cancelRental(rentalId);
      
      if (response.success) {
        alert('✅ تم إلغاء التأجير بنجاح');
        loadRentals();
      } else {
        alert(response.message || 'فشل في إلغاء التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء إلغاء التأجير');
    }
  };

  const handleExtendRental = async (rentalId) => {
    const hours = prompt('أدخل عدد الساعات الإضافية:', '1');
    if (!hours || isNaN(hours)) return;

    try {
      const response = await authService.updateRental(rentalId, {
        extended_hours: parseInt(hours)
      });
      
      if (response.success) {
        alert(`✅ تم تمديد التأجير ${hours} ساعة`);
        loadRentals();
      } else {
        alert(response.message || 'فشل في تمديد التأجير');
      }
    } catch (error) {
      alert('❌ حدث خطأ أثناء تمديد التأجير');
    }
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number') return '0.00 ج';
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ج';
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusBadgeClass = (status) => {
    switch(status?.toLowerCase()) {
      case 'نشط': return 'status-active';
      case 'مكتمل': return 'status-completed';
      case 'ملغي': return 'status-cancelled';
      default: return 'status-default';
    }
  };

  // فلترة التأجيرات
  const filteredRentals = rentals.filter(rental => {
    if (filters.status !== 'all' && rental.status !== filters.status) return false;
    if (filters.branch !== 'all' && rental.branch_id != filters.branch) return false;
    
    // فلترة حسب التاريخ
    if (filters.dateFrom) {
      const rentalDate = new Date(rental.created_at);
      const filterDate = new Date(filters.dateFrom);
      if (rentalDate < filterDate) return false;
    }
    
    if (filters.dateTo) {
      const rentalDate = new Date(rental.created_at);
      const filterDate = new Date(filters.dateTo);
      if (rentalDate > filterDate) return false;
    }
    
    return true;
  });

  const totalPages = Math.ceil(filteredRentals.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedRentals = filteredRentals.slice(startIndex, startIndex + itemsPerPage);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل التأجيرات...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <div className="header-content">
          <h1>
            <span className="header-icon">⏱️</span>
            إدارة التأجيرات
            <span className="rentals-count">{rentals.length} تأجير</span>
          </h1>
          <p className="page-subtitle">عرض وتعديل جميع تأجيرات النظام</p>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      {/* فلتر التأجيرات */}
      <div className="filters-card">
        <div className="filters-header">
          <h3>
            <span className="filter-icon">🔍</span>
            فلتر التأجيرات
          </h3>
        </div>
        <div className="filters-grid">
          <div className="filter-group">
            <label className="filter-label">الحالة</label>
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
              className="filter-select"
            >
              <option value="all">جميع الحالات</option>
              <option value="نشط">نشط</option>
              <option value="مكتمل">مكتمل</option>
              <option value="ملغي">ملغي</option>
            </select>
          </div>

          <div className="filter-group">
            <label className="filter-label">الفرع</label>
            <select
              value={filters.branch}
              onChange={(e) => handleFilterChange('branch', e.target.value)}
              className="filter-select"
            >
              <option value="all">جميع الفروع</option>
              {branches.map(branch => (
                <option key={branch.id} value={branch.id}>
                  {branch.name}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label className="filter-label">التاريخ من</label>
            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => handleFilterChange('dateFrom', e.target.value)}
              className="date-input"
            />
          </div>

          <div className="filter-group">
            <label className="filter-label">التاريخ إلى</label>
            <input
              type="date"
              value={filters.dateTo}
              onChange={(e) => handleFilterChange('dateTo', e.target.value)}
              className="date-input"
            />
          </div>
        </div>
        <div className="filters-actions">
          <button className="btn-secondary" onClick={handleResetFilters}>
            <span className="btn-icon">🔄</span>
            إعادة تعيين
          </button>
          <button className="btn-primary" onClick={handleApplyFilters}>
            <span className="btn-icon">🔍</span>
            تطبيق الفلاتر
          </button>
        </div>
      </div>

      {/* جدول التأجيرات */}
      <div className="table-container">
        <div className="table-header">
          <h3>
            <span className="table-icon">📋</span>
            قائمة التأجيرات
          </h3>
          <div className="table-info">
            عرض {Math.min(itemsPerPage, paginatedRentals.length)} من {filteredRentals.length} تأجير
          </div>
        </div>
        
        {paginatedRentals.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">⏱️</div>
            <h3>لا توجد تأجيرات</h3>
            <p>لم يتم العثور على تأجيرات تطابق معايير البحث</p>
            <button className="btn-secondary" onClick={handleResetFilters}>
              عرض جميع التأجيرات
            </button>
          </div>
        ) : (
          <>
            <div className="table-responsive">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>الفرع</th>
                    <th>المبلغ</th>
                    <th>تاريخ البدء</th>
                    <th>الحالة</th>
                    <th>الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
                  {paginatedRentals.map(rental => (
                    <tr key={rental.id}>
                      <td>
                        <strong>#{rental.rental_number}</strong>
                      </td>
                      <td>
                        <div className="customer-info-cell">
                          <div className="customer-name">{rental.customer_name}</div>
                          <div className="customer-phone">{rental.customer_phone}</div>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td>{rental.branch_name}</td>
                      <td className="amount-cell">{formatCurrency(rental.total_amount)}</td>
                      <td>{formatDate(rental.start_time)}</td>
                      <td>
                        <span className={`status-badge ${getStatusBadgeClass(rental.status)}`}>
                          {rental.status}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button 
                            className="btn-action btn-info"
                            onClick={() => handleViewDetails(rental)}
                            title="تفاصيل التأجير"
                          >
                            👁️
                          </button>
                          
                          {rental.status === 'نشط' && (
                            <>
                              <button 
                                className="btn-action btn-success"
                                onClick={() => handleEndRental(rental.id)}
                                title="إنهاء التأجير"
                              >
                                ✅
                              </button>
                              <button 
                                className="btn-action btn-warning"
                                onClick={() => handleExtendRental(rental.id)}
                                title="تمديد التأجير"
                              >
                                ⏱️
                              </button>
                              <button 
                                className="btn-action btn-danger"
                                onClick={() => handleCancelRental(rental.id)}
                                title="إلغاء التأجير"
                              >
                                ❌
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* الترقيم */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  السابق
                </button>
                <div className="pagination-numbers">
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      className={`pagination-number ${currentPage === page ? 'active' : ''}`}
                      onClick={() => setCurrentPage(page)}
                    >
                      {page}
                    </button>
                  ))}
                </div>
                <button 
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  التالي
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Modal تفاصيل التأجير */}
      {showDetailsModal && selectedRental && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>تفاصيل التأجير #{selectedRental.rental_number}</h2>
              <button 
                className="close-btn"
                onClick={() => setShowDetailsModal(false)}
              >
                ✕
              </button>
            </div>
            <div className="modal-body">
              <div className="rental-details">
                <div className="detail-section">
                  <h3>معلومات العميل</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">الاسم:</span>
                      <span className="detail-value">{selectedRental.customer_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">الهاتف:</span>
                      <span className="detail-value">{selectedRental.customer_phone}</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>معلومات اللعبة</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">اسم اللعبة:</span>
                      <span className="detail-value">{selectedRental.game_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">الفرع:</span>
                      <span className="detail-value">{selectedRental.branch_name}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">السعر/ساعة:</span>
                      <span className="detail-value">{selectedRental.price_per_hour} ج</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>معلومات التأجير</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">تاريخ البدء:</span>
                      <span className="detail-value">{formatDate(selectedRental.start_time)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">تاريخ الانتهاء المتوقع:</span>
                      <span className="detail-value">{formatDate(selectedRental.expected_end_time)}</span>
                    </div>
                    {selectedRental.actual_end_time && (
                      <div className="detail-item">
                        <span className="detail-label">تاريخ الانتهاء الفعلي:</span>
                        <span className="detail-value">{formatDate(selectedRental.actual_end_time)}</span>
                      </div>
                    )}
                    <div className="detail-item">
                      <span className="detail-label">المدة:</span>
                      <span className="detail-value">{selectedRental.duration_minutes} دقيقة</span>
                    </div>
                  </div>
                </div>

                <div className="detail-section">
                  <h3>المعلومات المالية</h3>
                  <div className="detail-grid">
                    <div className="detail-item">
                      <span className="detail-label">الإجمالي:</span>
                      <span className="detail-value amount">{formatCurrency(selectedRental.total_amount)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">حالة الدفع:</span>
                      <span className={`detail-value ${selectedRental.payment_status === 'مدفوع' ? 'text-success' : 'text-warning'}`}>
                        {selectedRental.payment_status}
                      </span>
                    </div>
                    {selectedRental.payment_method && (
                      <div className="detail-item">
                        <span className="detail-label">طريقة الدفع:</span>
                        <span className="detail-value">{selectedRental.payment_method}</span>
                      </div>
                    )}
                  </div>
                </div>

                {selectedRental.notes && (
                  <div className="detail-section">
                    <h3>ملاحظات</h3>
                    <div className="notes">
                      {selectedRental.notes}
                    </div>
                  </div>
                )}
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-secondary"
                onClick={() => setShowDetailsModal(false)}
              >
                إغلاق
              </button>
              {selectedRental.status === 'نشط' && (
                <button 
                  className="btn-primary"
                  onClick={() => {
                    handleEndRental(selectedRental.id);
                    setShowDetailsModal(false);
                  }}
                >
                  إنهاء التأجير
                </button>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RentalsManagement;
