import React, { useState, useEffect, useCallback } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';
import api from '../../services/api';

const BranchesManagement = () => {
  // ğŸ”¹ States Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  const [loadingGames, setLoadingGames] = useState(false);
  const [gamesError, setGamesError] = useState('');
  const [diagnosticInfo, setDiagnosticInfo] = useState(null);

  // ğŸ”¹ Ø®Ø±ÙŠØ·Ø© Ù…Ø³Ø§Ø±Ø§Øª Ø§Ù„ØµÙˆØ±
  const GAME_IMAGE_MAP = {
    'Driftcar': '/images/Driftcar.jpg',
    'DriftCar': '/images/Driftcar.jpg',
    'Ø¯Ø±ÙØª ÙƒØ§Ø±': '/images/Driftcar.jpg',
    'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±': '/images/Driftcar.jpg',
    'Hoverboard': '/images/Hoverboard.jpg',
    'Ù‡ÙˆÙØ± Ø¨ÙˆØ±Ø¯': '/images/Hoverboard.jpg',
    'Ù‡ÙˆÙØ±Ø¨ÙˆØ±Ø¯': '/images/Hoverboard.jpg',
