import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [gameDurations, setGameDurations] = useState({});
  const [activeRentals, setActiveRentals] = useState([]);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø© Ø¨Ø¯Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0
  });
  
  // Ù†Ø§ÙØ°Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©
  const [showEditModal, setShowEditModal] = useState(false);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [showEndModal, setShowEndModal] = useState(false);
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ØªÙ†Ø¨ÙŠÙ‡Ø§Øª Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠ
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs Ù„Ù„Ù…Ø¤Ù‚ØªØ§Øª
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ø«Ø§Ø¨ØªØ©
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 1 },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 2 },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 3 },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©', priceMultiplier: 4 },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†', priceMultiplier: 8 },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 12 },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 16 },
    { value: 300, label: '5 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 20 },
    { value: 360, label: '6 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 24 }
  ];

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const interval = setInterval(() => {
        loadData();
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„ØªÙ†Ø§Ø²Ù„ÙŠØ©
  useEffect(() => {
    Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    countdownTimersRef.current = {};
    
    activeRentals.forEach(rental => {
      if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
        startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
      }
    });
    
    return () => {
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    };
  }, [activeRentals]);

  // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠØ© ÙˆØ§Ù„Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªÙ„Ù‚Ø§Ø¦ÙŠ
  useEffect(() => {
    const checkAndCompleteExpiredRentals = async () => {
      const now = new Date();
      const alerts = [];
      
      activeRentals.forEach(rental => {
        if (rental.duration_minutes > 0 && rental.status === 'Ù†Ø´Ø·') {
          const startTime = new Date(rental.start_time);
          const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
          const timeLeftMs = endTime - now;
          
          if (timeLeftMs <= 0) {
            handleAutoCompleteRental(rental);
          } else if (timeLeftMs <= 300000) { // 5 Ø¯Ù‚Ø§Ø¦Ù‚
            alerts.push({
              rentalId: rental.id,
              rentalNumber: rental.rental_number,
              customerName: rental.customer_name,
              gameName: rental.game_name,
              timeLeft: Math.ceil(timeLeftMs / 60000)
            });
          }
        }
      });
      
      setExpiringAlerts(alerts);
    };

    const interval = setInterval(checkAndCompleteExpiredRentals, 60000);
    return () => clearInterval(interval);
  }, [activeRentals]);

  // ==================== FUNCTIONS ====================

  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      if (response.success && response.data) {
        setCurrentShift(response.data);
        
        // Ø­Ø³Ø§Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª
        const shiftRentals = activeRentals.filter(r => r.shift_id === response.data.id);
        const revenue = shiftRentals.reduce((sum, rental) => sum + (rental.paid_amount || 0), 0);
        
        setShiftStats({
          totalRevenue: revenue,
          totalRentals: shiftRentals.length,
          activeRentals: shiftRentals.filter(r => r.status === 'Ù†Ø´Ø·').length
        });
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
    }
  };

  const startShift = async () => {
