                  if (completedSection) {
                    completedSection.scrollIntoView({ behavior: 'smooth' });
                  }
                }
              }}
              className="rentals-btn rentals-btn-outline rentals-quick-btn"
            >
              <CheckCircle size={16} />
              Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©
              <span className="rentals-badge">{completedRentals.length}</span>
            </button>
          )}
        </div>
        
        {currentShift ? (
          <div className="rentals-current-shift-info">
            <div className="rentals-shift-badge">
              <Clock size={16} />
              <span>Ø§Ù„Ø´ÙŠÙØª #<strong>{currentShift.id}</strong></span>
            </div>
            <div className="rentals-shift-time">
              <span>Ø¨Ø¯Ø£: {formatTime(currentShift.start_time)}</span>
            </div>
            <div className="rentals-shift-user">
              <User size={16} />
              <span>{currentShift.employee_name || user?.name}</span>
            </div>
          </div>
        ) : (
          <button onClick={startShift} className="rentals-btn rentals-btn-success">
            <Zap size={18} />
            Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø¬Ø¯ÙŠØ¯
          </button>
        )}

        <button onClick={loadAllData} className="rentals-btn rentals-btn-outline">
          <RefreshCw size={18} />
          ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
        </button>
      </div>
    </div>
    
<DashboardStats 
  stats={stats}
  currentShift={currentShift}
  onStartShift={startShift}
  onEndShift={endShift}
  loading={loading.stats}
  onRefreshRevenue={(newRevenue) => {
    setStats(prev => ({
      ...prev,
      todayRevenue: newRevenue
    }));
  }}
/>
    
    <div className="rentals-main-content">
      <div className="rentals-left-column">
        <GamesGrid 
          games={games}
          branchId={user?.branch_id}
          onSelectGame={handleSelectGame}
          loading={loading.games}
        />
      </div>
      
      <div className="rentals-right-column">
        <ActiveRentalsList 
          rentals={activeRentals}
          loading={loading.rentals}
          currentShift={currentShift}
          onAction={handleRentalAction}
        />
        
        <CompletedRentalsList 
          rentals={completedRentals}
          loading={loading.stats}
          currentShift={currentShift}
          onRerental={handleRerental}
        />
      </div>
    </div>

  {showRentalModal && (
      <RentalModal
        show={showRentalModal}
        onClose={() => setShowRentalModal(false)}
        selectedGames={selectedGames}
        onConfirmRental={handleCreateRental}
        currentShift={currentShift}
        allGames={allGames}
      />
    )}
    

    
    {/* Modals */}
    {showRentalModal && (
      <RentalModal
        show={showRentalModal}
        onClose={() => setShowRentalModal(false)}
        selectedGames={selectedGames}
        onConfirmRental={handleCreateRental}
        currentShift={currentShift}
        allGames={allGames}
      />
    )}
    
    {showChangeGameModal && (
      <ChangeGameModal
        show={showChangeGameModal}
        onClose={() => setShowChangeGameModal(false)}
        rental={selectedRentalForChange}
        games={allGames}
        onConfirm={confirmChangeGame}
      />
    )}
    
    {showRerentalModal && (
      <RerentalModal
        show={showRerentalModal}
        onClose={() => setShowRerentalModal(false)}
        rental={selectedCompletedRental}
        games={allGames}
        onConfirm={confirmRerental}
      />
    )}

  </div>
);
};

export default Rentals;
