    }
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
  } finally {
    // Ø¥Ø¶Ø§ÙØ© ØªØ£Ø®ÙŠØ± Ø¨Ø³ÙŠØ· Ù„Ø¥Ø¹Ø·Ø§Ø¡ Ø§Ù„ÙˆÙ‚Øª Ù„ØªÙ†Ø¸ÙŠÙ… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    setTimeout(() => {
      setLoading({ games: false, rentals: false, shift: false, stats: false });
