    // â­ **TEMPORARY: Use existing endpoint with filter**
    const response = await api.get('/rentals', {
      params: {
        shift_id: currentShift.id,
        branch_id: user?.branch_id
      }
    });
    
    if (response.success && response.data) {
      const completedRentalsData = response.data.filter(rental => 
        rental.status === 'Ù…ÙƒØªÙ…Ù„'
