import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw, Calculator,
  Play, Pause, StopCircle, AlertTriangle,
  Info
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ÿßŸÑÿπÿßŸÖÿ© ====================
const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || amount === '') return '0 ÿ¨.ŸÖ';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/---- --:--';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  if (isNaN(start.getTime()) || isNaN(end.getTime())) return 0;
  return Math.max(0, Math.round((end - start) / (1000 * 60)));
};

// ÿ•ÿ∂ÿßŸÅÿ© Ÿáÿ∞Ÿá ÿßŸÑÿØÿßŸÑÿ© ŸÅŸä ŸÖŸÉŸàŸÜ Rentals
const autoCompleteFixedRentals = useCallback(async () => {
  if (!currentShift?.id) return;
  
  try {
    const response = await api.post('/rentals/auto-complete-fixed', {
      branch_id: user?.branch_id,
      shift_id: currentShift.id
    });
    
    if (response.success && response.processed > 0) {
      console.log(`‚úÖ ÿ™ŸÖÿ™ ŸÖÿπÿßŸÑÿ¨ÿ© ${response.processed} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜÿ™ŸáŸä ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã`);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿπÿØ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©
      await loadActiveRentals();
      if (isManager) {
        await loadCompletedRentals();
      }
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿäÿ©:', error);
  }
}, [currentShift?.id, user?.branch_id, loadActiveRentals, loadCompletedRentals, isManager]);

// ÿ•ÿ∂ÿßŸÅÿ© ŸÅÿ≠ÿµ ÿØŸàÿ±Ÿä ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
useEffect(() => {
  if (!currentShift?.id) return;
  
  // ŸÅÿ≠ÿµ ÿ£ŸàŸÑŸä ÿ®ÿπÿØ 30 ÿ´ÿßŸÜŸäÿ©
  const initialTimeout = setTimeout(() => {
    autoCompleteFixedRentals();
  }, 30000);
  
  // ŸÅÿ≠ÿµ ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
  const interval = setInterval(() => {
    autoCompleteFixedRentals();
  }, 60000); // 60 ÿ´ÿßŸÜŸäÿ©
  
  return () => {
    clearTimeout(initialTimeout);
    clearInterval(interval);
  };
}, [currentShift?.id, autoCompleteFixedRentals]);


// ==================== ÿßŸÑŸÖŸÉŸàŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπŸäÿ© ====================

// ŸÖŸÉŸàŸÜ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
const DashboardStats = React.memo(function DashboardStats({
  stats,
  currentShift,
  onStartShift,
  onEndShift,
  loading,
  error,
  onErrorClose
}) {
  if (loading.stats || loading.shift) {
    return (
      <div className="rentals-dashboard-stats">
        <div className="rentals-stats-grid">
          {[1, 2, 3, 4].map(i => (
            <div key={i} className="rentals-stat-card rentals-skeleton"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="rentals-dashboard-stats">
      <div className="rentals-stats-grid">
        <div className="rentals-stat-card">
          <div className="rentals-stat-icon">
            <Gamepad2 size={24} />
          </div>
          <div className="rentals-stat-content">
            <div className="rentals-stat-value">
              {stats.availableGames || 0}
            </div>
            <div className="rentals-stat-label">
              ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©
            </div>
          </div>
        </div>

        <div className="rentals-stat-card">
          <div className="rentals-stat-icon">
            <Zap size={24} />
          </div>
          <div className="rentals-stat-content">
            <div className="rentals-stat-value">
              {stats.activeRentals || 0}
            </div>
            <div className="rentals-stat-label">
              ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©
            </div>
          </div>
        </div>

        <div className="rentals-stat-card">
          <div className="rentals-stat-icon">
            <DollarSign size={24} />
          </div>
          <div className="rentals-stat-content">
            <div className="rentals-stat-value">
              {formatCurrency(stats.todayRevenue || 0)}
            </div>
            <div className="rentals-stat-label">
              ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑŸäŸàŸÖ
            </div>
          </div>
        </div>

        <div className="rentals-stat-card">
          <div className="rentals-stat-icon">
            <Clock size={24} />
          </div>
          <div className="rentals-stat-content">
            <div className="rentals-stat-value">
              {currentShift ? `#${currentShift.id}` : '--'}
            </div>
            <div className="rentals-stat-label">
              ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
            </div>
            <div className="rentals-shift-actions">
              {!currentShift ? (
                <button
                  onClick={onStartShift}
                  className="rentals-btn rentals-btn-success"
                  disabled={loading.processing}
                >
                  {loading.processing ? (
                    <Loader2 className="rentals-spinner" size={14} />
                  ) : (
                    <Zap size={14} />
                  )}
                  ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™
                </button>
              ) : (
                <button
                  onClick={onEndShift}
                  className="rentals-btn rentals-btn-warning"
                  disabled={loading.processing}
                >
                  {loading.processing ? (
                    <Loader2 className="rentals-spinner" size={14} />
                  ) : (
                    <Lock size={14} />
                  )}
                  ÿ•ŸÜŸáÿßÿ° ÿ¥ŸäŸÅÿ™
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="rentals-error-banner">
          <AlertCircle size={20} />
          <span>{error}</span>
          <button onClick={onErrorClose} className="rentals-error-close">
            <X size={16} />
          </button>
        </div>
      )}
    </div>
  );
});

// ŸÖŸÉŸàŸÜ ÿ¥ÿ®ŸÉÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®
const GamesGrid = React.memo(function GamesGrid({ 
  games, 
  branchId, 
  onSelectGame, 
  loading,
  searchTerm: externalSearchTerm,
  onSearchChange: externalOnSearchChange,
  filterCategory: externalFilterCategory,
  onFilterChange: externalOnFilterChange
}) {
  const [localSearch, setLocalSearch] = useState('');
  const [localCategory, setLocalCategory] = useState('all');

  // ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÇŸäŸÖ ÿßŸÑÿÆÿßÿ±ÿ¨Ÿäÿ© ÿ•ÿ∞ÿß Ÿàÿ¨ÿØÿ™ÿå Ÿàÿ•ŸÑÿß ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
  const searchTerm = externalSearchTerm !== undefined ? externalSearchTerm : localSearch;
  const filterCategory = externalFilterCategory !== undefined ? externalFilterCategory : localCategory;

  // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ≠ÿ≥ÿ® ÿßŸÑŸÅÿ±ÿπ
  const branchGames = useMemo(() => {
    if (!games?.length || !branchId) return [];
    const currentBranchId = Number(branchId);
    return games.filter(game => game && Number(game.branch_id) === currentBranchId);
  }, [games, branchId]);

  // ÿßÿ≥ÿ™ÿÆÿ±ÿßÿ¨ ÿßŸÑŸÅÿ¶ÿßÿ™ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
  const categories = useMemo(() => {
    if (!branchGames.length) return ['all'];
    const cats = new Set(['all']);
    branchGames.forEach(g => g.category && cats.add(g.category));
    return Array.from(cats);
  }, [branchGames]);

  // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ≠ÿ≥ÿ® ÿßŸÑÿ®ÿ≠ÿ´ ŸàÿßŸÑŸÅÿ¶ÿ©
  const filteredGames = useMemo(() => {
    if (!branchGames.length) return [];

    const search = searchTerm.toLowerCase();
    
    return branchGames.filter(game => {
      const matchesSearch = !searchTerm || 
        game.name?.toLowerCase().includes(search) ||
        game.category?.toLowerCase().includes(search);

      const matchesCategory = filterCategory === 'all' || game.category === filterCategory;

      return matchesSearch && matchesCategory;
    });
  }, [branchGames, searchTerm, filterCategory]);

  const getGameImage = useCallback((game) => {
    if (!game) return '/images/default-game.jpg';
    
    const imageMap = {
      'Driftcar': '/images/Driftcar.jpg',
      'Havoerboard': '/images/Hoverboard.jpg',
      'Sctooter': '/images/Scooter.jpg',
      'Car': '/images/Car.jpg',
      'MOtor': '/images/Motor.jpg',
      'Harlly': '/images/harley.jpg',
      'Segway': '/images/Segway.jpg',
      'Crazycar': '/images/Ninebot.jpg',
      'skeet': '/images/Skate.jpg',
      'Trampoline': '/images/Trampoline.jpg',
      'Vedio game': '/images/Simulator.jpg'
    };
    
    if (game.image_url && game.image_url !== 'default-game.jpg') {
      if (game.image_url.startsWith('http')) {
        return game.image_url;
      }
      return `/images/${game.image_url}`;
    }
    
    return imageMap[game.name] || '/images/default-game.jpg';
  }, []);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setLocalSearch(value);
    if (externalOnSearchChange) externalOnSearchChange(value);
  };

  const handleCategoryChange = (e) => {
    const value = e.target.value;
    setLocalCategory(value);
    if (externalOnFilterChange) externalOnFilterChange(value);
  };

  if (!branchId) {
    return (
      <div className="rentals-games-grid-section">
        <div className="rentals-empty-state">
          <Building size={48} />
          <h3>ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ</h3>
          <p>Ÿäÿ¨ÿ® ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</p>
        </div>
      </div>
    );
  }

  if (loading.games) {
    return (
      <div className="rentals-games-grid-section">
        <div className="rentals-loading-state">
          <Loader2 className="rentals-spinner" size={32} />
          <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="rentals-games-grid-section">
      <div className="rentals-section-header">
        <h3 className="rentals-section-title">
          <Gamepad2 size={24} />
          ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
          <span className="rentals-section-count">
            ({branchGames.length})
          </span>
        </h3>
        
      
      </div>

      {branchGames.length === 0 ? (
        <div className="rentals-empty-state">
          <Gamepad2 size={48} />
          <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©</h3>
        </div>
      ) : filteredGames.length === 0 ? (
      <div className="rentals-empty-state">
  <Gamepad2 size={48} />
  <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ™ÿ∑ÿßÿ®ŸÇ &quot;{searchTerm}&quot;</p>
</div>
      ) : (
        <div className="rentals-games-grid">
          {filteredGames.map(game => (
            <div
              key={game.id}
              className="rentals-game-card"
              onClick={() => onSelectGame(game)}
            >
              <div className="rentals-game-image-container">
                <img
                  src={getGameImage(game)}
                  alt={game.name}
                  className="rentals-game-image"
                  loading="lazy"
                  onError={(e) => {
                    e.target.src = '/images/default-game.jpg';
                  }}
                />
              </div>

              <div className="rentals-game-info">
                <h4>{game.name}</h4>

                <div className="rentals-game-price">
                  {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
});

// ŸÖŸÉŸàŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© - ŸÖÿπ ÿßŸÑÿ•ÿµŸÑÿßÿ≠
const ActiveRentalsList = React.memo(function ActiveRentalsList({ 
  rentals, 
  loading, 
  onAction,
  getRentalType 
}) {
  const [timeNow, setTimeNow] = useState(new Date());
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('all'); // all, customer, game, game_type
  const [filteredRentals, setFilteredRentals] = useState(rentals);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimeNow(new Date());
    }, 1000);
    
    return () => clearInterval(interval);
  }, []);

// ÿØÿßÿÆŸÑ ActiveRentalsListÿå ÿßÿ≥ÿ™ÿ®ÿØŸÑ ÿØÿßŸÑÿ© ÿßŸÑÿ™ÿµŸÅŸäÿ© ÿ®Ÿáÿ∞ÿß ÿßŸÑŸÉŸàÿØ
useEffect(() => {
  if (!searchTerm.trim()) {
    setFilteredRentals(rentals);
    return;
  }

  const term = searchTerm.toLowerCase().trim();
  const filtered = rentals.filter(rental => {
    // ÿ≠ŸÑ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ©: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿØÿßÿÆŸÑ ŸÉŸÑ ÿ≠ÿßŸÑÿ© ŸÖÿπ ÿ£ŸÇŸàÿßÿ≥
    if (searchType === 'customer') {
      return rental.customer_name?.toLowerCase().includes(term) ||
             rental.customer_phone?.includes(term);
    }
    
    if (searchType === 'game') {
      return rental.game_name?.toLowerCase().includes(term);
    }
    
    if (searchType === 'game_type') {
      return rental.game_type?.toLowerCase().includes(term);
    }
    
    // ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©: ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑŸÉŸÑ
    return rental.customer_name?.toLowerCase().includes(term) ||
           rental.customer_phone?.includes(term) ||
           rental.game_name?.toLowerCase().includes(term) ||
           rental.game_type?.toLowerCase().includes(term);
  });
  
  setFilteredRentals(filtered);
}, [searchTerm, searchType, rentals]);

  const calculateElapsedTime = useCallback((startTime) => {
    if (!startTime) return '--:--';
    const start = new Date(startTime);
    const diffMs = timeNow - start;
    const diffMinutes = Math.floor(diffMs / 60000);
    const diffSeconds = Math.floor((diffMs % 60000) / 1000);
    
    if (diffMinutes < 60) {
      return `${diffMinutes.toString().padStart(2, '0')}:${diffSeconds.toString().padStart(2, '0')}`;
    } else {
      const hours = Math.floor(diffMinutes / 60);
      const minutes = diffMinutes % 60;
      return `${hours}:${minutes.toString().padStart(2, '0')}:${diffSeconds.toString().padStart(2, '0')}`;
    }
  }, [timeNow]);

  const calculateRemainingTime = useCallback((rental) => {
    if (!rental.start_time) return '--:--';
    
    const rentalType = getRentalType(rental);
    const startTime = new Date(rental.start_time);
    
    if (rentalType === 'fixed') {
      const duration = rental.duration_minutes || 15;
      const endTime = new Date(startTime.getTime() + duration * 60000);
      const remainingMs = endTime - timeNow;
      
      if (remainingMs <= 0) return '00:00';
      
      const remainingMinutes = Math.floor(remainingMs / 60000);
      const remainingSeconds = Math.floor((remainingMs % 60000) / 1000);
      return `${remainingMinutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
    }
    
    return 'ŸÖŸÅÿ™Ÿàÿ≠';
  }, [timeNow, getRentalType]);

  const calculateEndTime = useCallback((rental) => {
    if (!rental.start_time || getRentalType(rental) !== 'fixed') return '--:--';
    
    const duration = rental.duration_minutes || 15;
    const startTime = new Date(rental.start_time);
    const endTime = new Date(startTime.getTime() + duration * 60000);
    
    return formatTime(endTime);
  }, [getRentalType]);

  const calculateProgress = useCallback((rental) => {
    if (!rental.start_time || getRentalType(rental) !== 'fixed') return 100;
    
    const duration = rental.duration_minutes || 15;
    const startTime = new Date(rental.start_time);
    const endTime = new Date(startTime.getTime() + duration * 60000);
    const totalMs = duration * 60000;
    const remainingMs = endTime - timeNow;
    
    if (remainingMs <= 0) return 0;
    
    const percentage = (remainingMs / totalMs) * 100;
    return Math.max(0, Math.min(100, percentage));
  }, [timeNow, getRentalType]);

  const isExpired = useCallback((rental) => {
    if (getRentalType(rental) !== 'fixed') return false;
    if (!rental.start_time) return false;
    
    const duration = rental.duration_minutes || 15;
    const startTime = new Date(rental.start_time);
    const endTime = new Date(startTime.getTime() + duration * 60000);
    
    return timeNow > endTime;
  }, [timeNow, getRentalType]);

  // ÿØÿßŸÑÿ© ŸÖÿ≥ÿßÿπÿØÿ© ŸÑŸÖÿ≥ÿ≠ ÿßŸÑÿ®ÿ≠ÿ´
  const clearSearch = () => {
    setSearchTerm('');
    setSearchType('all');
  };

  return (
    <div className="rentals-active-rentals-section">
      <div className="rentals-section-header">
        <h3 className="rentals-section-title">
          <Clock size={24} />
          ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
          <span className="rentals-section-count">
            ({filteredRentals.length} / {rentals.length})
          </span>
        </h3>

