const AddGameModal = React.memo(function AddGameModal({ 
  show, 
  onClose, 
  onSelectGame, 
  allGames 
}) {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredGames = useMemo(() => {
    if (!allGames?.length) return [];

    const availableGames = allGames.filter(game => game && game.status === 'ŸÖÿ™ÿßÿ≠');

    if (!searchTerm) return availableGames.slice(0, 12);

    return availableGames.filter(game =>
      game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      game.category?.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [searchTerm, allGames]);

  const getGameImage = useCallback((game) => {
    const imageMap = {
      'Driftcar': '/images/Driftcar.jpg',
      'Havoerboard': '/images/Hoverboard.jpg',
      'Sctooter': '/images/Scooter.jpg',
      'Car': '/images/Car.jpg',
      'MOtor': '/images/Motor.jpg',
      'Harlly': '/images/harley.jpg',
      'Segway': '/images/Segway.jpg',
      'Crazycar': '/images/Ninebot.jpg',
      'skeet': '/images/Skate.jpg',
      'Trampoline': '/images/Trampoline.jpg',
      'Vedio game': '/images/Simulator.jpg'
    };
    
    if (game.image_url && game.image_url !== 'default-game.jpg') {
      return game.image_url.startsWith('http') ? game.image_url : `/images/${game.image_url}`;
    }
    
    return imageMap[game.name] || '/images/default-game.jpg';
  }, []);

  if (!show) return null;

  return (
    <div className="rentals-modal-overlay rentals-add-game-overlay">
      <div className="rentals-modal rentals-add-game-modal">
        <div className="rentals-modal-header">
          <h3>
            <Package size={24} />
            ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿπÿ®ÿ© ÿ¨ÿØŸäÿØÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±
          </h3>
          <button onClick={onClose} className="rentals-modal-close">
            <X size={24} />
          </button>
        </div>
        
        <div className="rentals-modal-body">
         
          
          <div className="rentals-add-game-grid">
            {filteredGames.length === 0 ? (
              <div className="rentals-empty-state">
  <Search size={48} />
  <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÜÿ™ÿßÿ¶ÿ¨</h3>
  <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ™ÿ∑ÿßÿ®ŸÇ &quot;{searchTerm}&quot;</p>
</div>
            ) : (
              filteredGames.map(game => (
                <div 
                  key={game.id}
                  className="rentals-add-game-card"
                  onClick={() => {
                    onSelectGame(game);
                    onClose();
                  }}
                >
                  <div className="rentals-add-game-image">
                    <img 
                      src={getGameImage(game)} 
                      alt={game.name}
                      onError={(e) => e.target.src = '/images/default-game.jpg'}
                    />
                  </div>
                  <div className="rentals-add-game-info">
                    <h4>{game.name}</h4>
                    <div className="rentals-add-game-price">
                      {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
        
        <div className="rentals-modal-footer">
          <button 
            onClick={onClose}
            className="rentals-btn rentals-btn-secondary"
          >
            ÿ•ŸÑÿ∫ÿßÿ°
          </button>
        </div>
      </div>
    </div>
  );
});

// ŸÖŸÉŸàŸÜ ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
const RentalModal = React.memo(function RentalModal({ 
  show, 
  onClose, 
  selectedGames: initialSelectedGames, 
  onConfirmRental, 
  currentShift,
  allGames,
  calculateItemPrice
}) {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });
  
  const [rentalItems, setRentalItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('ŸÉÿßÿ¥');
  const [notes, setNotes] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showAddGameModal, setShowAddGameModal] = useState(false);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (!show) return;
    if (!initialSelectedGames?.length) return;

    const items = initialSelectedGames.map(game => ({
      id: Date.now() + Math.random(),
      gameId: game.id,
      gameName: game.name,
      gameImage: '/images/default-game.jpg',
      rentalType: 'fixed',
      duration: 15,
      pricePer15min: game.price_per_15min || 100,
      quantity: 1,
      childName: '',
      notes: ''
    }));

    setRentalItems(items);
    setErrors({});
  }, [show, initialSelectedGames]);

  const updateRentalItem = useCallback((id, updates) => {
    setRentalItems(items => 
      items.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  }, []);

  const removeRentalItem = useCallback((id) => {
    setRentalItems(items => items.filter(item => item.id !== id));
  }, []);

  const validateForm = useCallback(() => {
    const newErrors = {};
    
    if (!customerInfo.name.trim()) {
      newErrors.name = 'ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ ŸÖÿ∑ŸÑŸàÿ®';
    }
    
    if (customerInfo.phone && !validatePhone(customerInfo.phone)) {
      newErrors.phone = 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠';
    }
    
    if (rentalItems.length === 0) {
      newErrors.games = 'Ÿäÿ¨ÿ® ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿπÿ®ÿ© Ÿàÿßÿ≠ÿØÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [customerInfo.name, customerInfo.phone, rentalItems.length]);

  const handleSubmit = useCallback(async () => {
    if (!validateForm()) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ™ÿµÿ≠Ÿäÿ≠ ÿßŸÑÿ£ÿÆÿ∑ÿßÿ° ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©');
      return;
    }
    
    if (rentalItems.length === 0) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      return;
    }

    setIsSubmitting(true);

    try {
      const rentalData = {
        customerInfo,
        rentalItems,
        paymentMethod,
        notes,
        totalAmount: calculateTotal(),
        shiftId: currentShift?.id
      };

      await onConfirmRental(rentalData);
      
      // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
      setCustomerInfo({ name: '', phone: '', email: '', address: '' });
      setRentalItems([]);
      setPaymentMethod('ŸÉÿßÿ¥');
      setNotes('');
      setErrors({});
      onClose();
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setIsSubmitting(false);
    }
  }, [validateForm, rentalItems, customerInfo, paymentMethod, notes, currentShift?.id, onConfirmRental, onClose]);

  const calculateTotal = useCallback(() => {
    return rentalItems.reduce((total, item) => {
      if (item.rentalType === 'open') return total;
      return total + (calculateItemPrice(item) || 0);
    }, 0);
  }, [rentalItems, calculateItemPrice]);

  const handleSelectNewGame = useCallback((game) => {
    const newItem = {
      id: Date.now() + Math.random(),
      gameId: game.id,
      gameName: game.name,
      gameImage: '/images/default-game.jpg',
      rentalType: 'fixed',
      duration: 15,
      pricePer15min: game.price_per_15min || 100,
      quantity: 1,
      childName: '',
      notes: ''
    };
    
    setRentalItems(prev => [...prev, newItem]);
  }, []);

  if (!show) return null;

  return (
    <>
