const ChangeGameModal = React.memo(function ChangeGameModal({ 
  show, 
  onClose, 
  rental, 
  games, 
  onConfirm 
}) {
  const [selectedGame, setSelectedGame] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (show && games.length > 0) {
      const currentGame = games.find(g => g.id === rental?.game_id);
      setSelectedGame(currentGame || null);
    }
  }, [show, rental, games]);

  const filteredGames = useMemo(() => {
    if (!games.length) return [];
    
    return games.filter(game => 
      game.status === 'ŸÖÿ™ÿßÿ≠' &&
      (game.id !== rental?.game_id) &&
      (!searchTerm || 
       game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
       game.category?.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [games, rental, searchTerm]);

  const handleConfirm = async () => {
    if (!selectedGame) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ©');
      return;
    }

    setIsSubmitting(true);
    try {
      await onConfirm(selectedGame);
      onClose();
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!show || !rental) return null;

  return (
    <div className="rentals-modal-overlay">
      <div className="rentals-modal rentals-change-game-modal">
