  // Refs ŸÑŸÑÿ™ÿ≠ŸÉŸÖ ŸÅŸä ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™
  const isMountedRef = useRef(true);
  const updateLockRef = useRef(false); // ŸÇŸÅŸÑ ŸÑŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const pendingUpdatesRef = useRef({
    games: false,
    rentals: false,
    shift: false,
    stats: false
  });
  const lastUpdateTimeRef = useRef({
    games: 0,
    rentals: 0,
    shift: 0,
    stats: 0
  });
  const refreshTimerRef = useRef(null);
  const intervalRef = useRef(null);
  const shiftIntervalRef = useRef(null);
  const retryCountRef = useRef(0);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ‚úÖ ÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const getRentalType = useCallback((rental) => {
    if (!rental) return 'fixed';
    
    if (rental.rental_type === 'open' || rental.is_open_time === 1) {
      return 'open';
    }
    
    if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
      return 'fixed';
    }
    
    if (rental.payment_status === 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°' || rental.payment_status === 'pending') {
      return 'open';
    }
    
    if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' || rental.payment_status === 'paid') {
      return 'fixed';
    }
    
    return 'fixed';
  }, []);

// ‚úÖ ÿ≠ÿ≥ÿßÿ® ÿ≥ÿπÿ± ÿßŸÑÿπŸÜÿµÿ± - ŸÖÿπ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿµÿ≠Ÿäÿ≠
const calculateItemPrice = useCallback((item) => {
  if (item.rentalType === 'open') return 0;
  
  // üîß ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿØÿ© ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ©
  const actualDuration = item.duration || 15;
  
  // ÿ≠ÿ≥ÿßÿ® ÿπÿØÿØ ÿßŸÑŸàÿ≠ÿØÿßÿ™ (ŸÉŸÑ 15 ÿØŸÇŸäŸÇÿ© Ÿàÿ≠ÿØÿ©)
  const units = Math.ceil(actualDuration / 15);
  
  // ÿßŸÑÿ≥ÿπÿ± = ÿ≥ÿπÿ± ÿßŸÑŸàÿ≠ÿØÿ© √ó ÿπÿØÿØ ÿßŸÑŸàÿ≠ÿØÿßÿ™ √ó ÿßŸÑŸÉŸÖŸäÿ©
  const price = (item.pricePer15min * units * item.quantity) || 0;
  
  console.log('üí∞ ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ≥ÿπÿ±:', {
    duration: actualDuration,
    units: units,
    pricePer15min: item.pricePer15min,
    quantity: item.quantity,
    total: price
  });
  
  return price;
}, []);

  // ‚úÖ ÿØÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ¢ŸÖŸÜÿ© - ÿ™ŸÖŸÜÿπ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const safeUpdate = useCallback(async (updateFn, updateType, force = false) => {
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÇŸÅŸÑ
    if (updateLockRef.current && !force) {
      console.log(`‚è≥ [LOCKED] ${updateType} - ŸáŸÜÿßŸÉ ÿ™ÿ≠ÿØŸäÿ´ ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞`);
      pendingUpdatesRef.current[updateType] = true;
      return { success: false, queued: true };
    }
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´ (ŸÑŸÖŸÜÿπ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ŸÉÿ±ÿ±ÿ©)
    const now = Date.now();
    const lastUpdate = lastUpdateTimeRef.current[updateType] || 0;
    const minInterval = updateType === 'stats' ? 10000 : 5000; // 10 ÿ´ŸàÿßŸÜŸä ŸÑŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ÿå 5 ŸÑŸÑÿ®ÿßŸÇŸä
    
    if (now - lastUpdate < minInterval && !force) {
      console.log(`‚è±Ô∏è [THROTTLED] ${updateType} - ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´ ŸÉÿßŸÜ ŸÇÿ®ŸÑ ${Math.round((now - lastUpdate)/1000)} ÿ´ÿßŸÜŸäÿ©`);
      return { success: false, throttled: true };
    }
    
    try {
      updateLockRef.current = true;
      console.log(`üîí [LOCK ACQUIRED] ${updateType}`);
      
      const result = await updateFn();
      
      lastUpdateTimeRef.current[updateType] = Date.now();
      pendingUpdatesRef.current[updateType] = false;
      
      return { success: true, result };
    } catch (error) {
      console.error(`üî• [UPDATE ERROR] ${updateType}:`, error);
      return { success: false, error };
    } finally {
      updateLockRef.current = false;
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿπŸÑŸÇÿ©
      setTimeout(() => {
        Object.keys(pendingUpdatesRef.current).forEach(key => {
          if (pendingUpdatesRef.current[key]) {
            console.log(`üîÑ [PROCESS PENDING] ${key}`);
            pendingUpdatesRef.current[key] = false;
            // ÿ≥Ÿäÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ŸÅŸä ÿßŸÑÿØŸàÿ±ÿ© ÿßŸÑÿ™ÿßŸÑŸäÿ©
          }
        });
      }, 100);
    }
  }, []);

  // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® - ŸÖÿπ ÿ≠ŸÖÿßŸäÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const loadGames = useCallback(async () => {
    if (!user?.branch_id) {
      setGames([]);
      setAllGames([]);
      return;
    }
    
    return await safeUpdate(async () => {
      try {
        setLoading(prev => ({ ...prev, games: true }));
        
        const response = await api.loadGamesSmart(user.branch_id);
        
        if (response.success && response.data && isMountedRef.current) {
          setGames(response.data);
          setAllGames(response.data);
          
          const availableCount = response.data.filter(game => 
            game.status === 'ŸÖÿ™ÿßÿ≠'
          ).length;
          
          // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ - ŸàŸÑŸÉŸÜ ŸÑŸäÿ≥ ÿ∂ŸÖŸÜ ŸÇŸÅŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
          setTimeout(() => {
            if (isMountedRef.current) {
              setStats(prev => ({
                ...prev,
                availableGames: availableCount,
                totalGames: response.data.length
              }));
            }
          }, 0);
          
          return response.data;
        }
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
        throw error;
      } finally {
        if (isMountedRef.current) {
          setLoading(prev => ({ ...prev, games: false }));
        }
      }
    }, 'games');
  }, [user?.branch_id, safeUpdate]);

  // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä - ŸÖÿπ ÿ≠ŸÖÿßŸäÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const loadCurrentShift = useCallback(async () => {
    return await safeUpdate(async () => {
      try {
        setLoading(prev => ({ ...prev, shift: true }));
        
        const response = await api.getCurrentShift();
        
        if (response.success && response.data && isMountedRef.current) {
          const shift = response.data;
          
          if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
            setCurrentShift(shift);
            localStorage.setItem('current_shift', JSON.stringify(shift));
            return shift;
          } else {
            console.warn('‚ö†Ô∏è ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑ ŸÑÿß ŸäŸÜÿ™ŸÖŸä ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä');
            setCurrentShift(null);
            localStorage.removeItem('current_shift');
            return null;
          }
        } else {
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } catch (error) {
        console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
        throw error;
      } finally {
        if (isMountedRef.current) {
          setLoading(prev => ({ ...prev, shift: false }));
        }
      }
    }, 'shift');
  }, [user?.id, user?.name, safeUpdate]);

  // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© - ŸÖÿπ ÿ≠ŸÖÿßŸäÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const loadActiveRentals = useCallback(async () => {
    if (!currentShift?.id || !user?.branch_id) {
      setActiveRentals([]);
      return;
    }
    
    return await safeUpdate(async () => {
      try {
        setLoading(prev => ({ ...prev, rentals: true }));
        
        const response = await api.get('/rentals/active', {
          params: {
            shift_id: currentShift.id,
            branch_id: user.branch_id
          }
        });
        
        let rentals = [];
        if (response.success && response.data) {
          rentals = Array.isArray(response.data) ? response.data : 
                    (response.data.data && Array.isArray(response.data.data) ? response.data.data : []);
        }
        
        const shiftRentals = rentals.filter(rental => 
          rental.shift_id == currentShift.id && rental.status === 'ŸÜÿ¥ÿ∑'
        );
        
        if (isMountedRef.current) {
          setActiveRentals(shiftRentals);
          
          // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ - ÿ®ÿ¥ŸÉŸÑ ÿ∫Ÿäÿ± ŸÖÿ™ÿ≤ÿßŸÖŸÜ
          setTimeout(() => {
            if (isMountedRef.current) {
              setStats(prev => ({
                ...prev,
                activeRentals: shiftRentals.length
              }));
            }
          }, 0);
        }
        
        return shiftRentals;
      } catch (error) {
        console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error);
        throw error;
      } finally {
        if (isMountedRef.current) {
          setLoading(prev => ({ ...prev, rentals: false }));
        }
      }
    }, 'rentals');
  }, [currentShift?.id, user?.branch_id, safeUpdate]);

  // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© - ŸÖÿπ ÿ≠ŸÖÿßŸäÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
  const loadCompletedRentals = useCallback(async () => {
    if (!isManager || !currentShift?.id || !user?.branch_id) return;
    
    return await safeUpdate(async () => {
      try {
        const response = await api.get('/rentals', {
          params: {
            shift_id: currentShift.id,
            branch_id: user.branch_id,
            status: 'ŸÖŸÉÿ™ŸÖŸÑ',
            limit: 100
          }
        });
        
        if (response.success && response.data && isMountedRef.current) {
          setCompletedRentals(response.data);
          
          const revenue = response.data.reduce((total, rental) => {
            if (rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ') {
              const rentalType = getRentalType(rental);
              if (rentalType === 'open') {
                return total + parseFloat(rental.final_amount || 0);
              } else {
                return total + parseFloat(rental.paid_amount || rental.total_amount || 0);
              }
            }
            return total;
          }, 0);
          
          setTimeout(() => {
            if (isMountedRef.current) {
              setStats(prev => ({
                ...prev,
                todayRevenue: revenue,
                todayRentals: response.data.length
              }));
            }
          }, 0);
          
          return response.data;
        }
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
        throw error;
      }
    }, 'stats');
  }, [currentShift?.id, isManager, user?.branch_id, getRentalType, safeUpdate]);

  // ‚úÖ ÿØÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ¥ÿßŸÖŸÑ - ŸÖÿπ ÿ™ŸÜÿ≥ŸäŸÇ ŸÉÿßŸÖŸÑ ÿ®ŸäŸÜ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™
  const refreshAllData = useCallback(async (force = false) => {
    if (!isMountedRef.current) return;
    
    // ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑŸÖÿ§ŸÇÿ™ ÿßŸÑÿ≥ÿßÿ®ŸÇ
    if (refreshTimerRef.current) {
      clearTimeout(refreshTimerRef.current);
      refreshTimerRef.current = null;
    }
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÇŸÅŸÑÿå ŸÜÿ§ÿ¨ŸÑ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
    if (updateLockRef.current && !force) {
      console.log('‚è≥ [DEFERRED] ÿ™ÿ£ÿ¨ŸäŸÑ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ¥ÿßŸÖŸÑ - ŸáŸÜÿßŸÉ ŸÇŸÅŸÑ ŸÜÿ¥ÿ∑');
      refreshTimerRef.current = setTimeout(() => {
        refreshAllData(force);
      }, 2000);
      return;
    }
    
    console.log('üîÑ [BATCH UPDATE] ÿ®ÿØÿ° ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ¥ÿßŸÖŸÑ');
    
    try {
      // 1. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã
      let shiftData = currentShift;
      if (force || !shiftData) {
        shiftData = await loadCurrentShift();
      }
      
      if (!shiftData) {
        console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑');
        return;
      }
      
      // 2. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ÿ®ÿ¥ŸÉŸÑ ŸÖÿ™ÿ≥ŸÑÿ≥ŸÑ (ŸàŸÑŸäÿ≥ ŸÖÿ™Ÿàÿßÿ≤Ÿä)
      // Ÿáÿ∞ÿß ŸäŸÖŸÜÿπ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ÿßÿ™ ÿßŸÑŸÖÿ™ÿ≤ÿßŸÖŸÜÿ©
      
      // 2.1 ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
      await loadGames();
      
      // 2.2 ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      await loadActiveRentals();
      
      // 2.3 ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© (ŸÑŸÑŸÖÿØŸäÿ± ŸÅŸÇÿ∑)
      if (isManager) {
        await loadCompletedRentals();
      }
      
      setError(null);
      retryCountRef.current = 0;
      
    } catch (error) {
      console.error('üî• [BATCH UPDATE ERROR]', error);
      
      // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿπ ÿ™ÿ£ÿÆŸäÿ± ÿ™ÿµÿßÿπÿØŸä
      if (retryCountRef.current < 3) {
        retryCountRef.current++;
        const delay = Math.min(1000 * Math.pow(2, retryCountRef.current), 10000);
        console.log(`üîÑ ÿ•ÿπÿßÿØÿ© ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿ®ÿπÿØ ${delay}ms (ŸÖÿ≠ÿßŸàŸÑÿ© ${retryCountRef.current}/3)`);
        
        refreshTimerRef.current = setTimeout(() => {
          refreshAllData(force);
        }, delay);
      } else {
        setError('ŸÅÿ¥ŸÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿπÿØ ÿπÿØÿ© ŸÖÿ≠ÿßŸàŸÑÿßÿ™');
      }
    }
  }, [currentShift, loadCurrentShift, loadGames, loadActiveRentals, loadCompletedRentals, isManager]);

  // ‚úÖ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© - ŸÖÿπ ÿ≠ŸÖÿßŸäÿ© ÿßŸÑŸÇŸÅŸÑ
const processCompletedRentals = useCallback(async () => {
  if (!currentShift?.id) return { success: true, processed: 0 };
  
  // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÇŸÅŸÑÿå ŸÜÿ§ÿ¨ŸÑ ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ©
  if (updateLockRef.current) {
    console.log('‚è≥ ÿ™ÿ£ÿ¨ŸäŸÑ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© - ŸáŸÜÿßŸÉ ŸÇŸÅŸÑ ŸÜÿ¥ÿ∑');
    setTimeout(() => processCompletedRentals(), 2000);
    return { success: true, processed: 0 };
  }
  
  try {
    updateLockRef.current = true;
    
    const now = new Date();
    const expiredFixedRentals = activeRentals.filter(rental => {
      const rentalType = getRentalType(rental);
      if (rentalType !== 'fixed') return false;
      
      if (!rental.start_time) return false;
      
      const startTime = new Date(rental.start_time);
      // üîß ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÖÿØÿ© ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ© ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      const duration = rental.duration_minutes || 15;
      const endTime = new Date(startTime.getTime() + duration * 60000);
      
      console.log(`‚è∞ ŸÅÿ≠ÿµ ÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, {
        duration: duration,
        startTime: startTime.toISOString(),
        endTime: endTime.toISOString(),
        now: now.toISOString(),
        expired: now > endTime
      });
      
      return now > endTime;
    });
      
      if (expiredFixedRentals.length === 0) {
        return { success: true, processed: 0 };
      }
      
      let processedCount = 0;
      const movedRentals = [];
      
      for (const rental of expiredFixedRentals) {
        try {
          let serverSuccess = false;
          
          try {
            const response = await api.completeRental(rental.id, {
              payment_method: 'ŸÉÿßÿ¥',
              final_amount: rental.paid_amount || rental.total_amount || 0,
              auto_complete: true
            });
            
            if (response.success) serverSuccess = true;
          } catch (serverError) {
            console.warn(`‚ö†Ô∏è ŸÅÿ¥ŸÑ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ± ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, serverError.message);
          }
          
          const completedRental = {
            ...rental,
            status: 'ŸÖŸÉÿ™ŸÖŸÑ',
            end_time: now.toISOString(),
            actual_end_time: now.toISOString(),
            payment_status: 'ŸÖÿØŸÅŸàÿπ',
            final_amount: rental.paid_amount || rental.total_amount || 0,
