          return [...newRentals, ...prev];
        });
        
        const addedRevenue = movedRentals.reduce((sum, rental) => 
          sum + (rental.final_amount || 0), 0
        );
        
        setStats(prev => ({
          ...prev,
          activeRentals: remainingRentals.length,
          todayRevenue: (prev.todayRevenue || 0) + addedRevenue,
          todayRentals: (prev.todayRentals || 0) + processedCount,
