        });
        
        if (response.success) {
          alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ ÿ®ŸÜÿ¨ÿßÿ≠!\nÿßŸÑŸÖÿ®ŸÑÿ∫: ${formatCurrency(response.data.final_amount)}`);
          
          const completedRental = {
            ...rental,
            status: 'ŸÖŸÉÿ™ŸÖŸÑ',
            end_time: new Date().toISOString(),
            final_amount: response.data.final_amount,
            paid_amount: response.data.final_amount,
            actual_duration_minutes: response.data.actual_minutes
          };
          
          setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
          setCompletedRentals(prev => [completedRental, ...prev]);
          
          setStats(prev => ({
            ...prev,
            activeRentals: prev.activeRentals - 1,
            todayRevenue: (prev.todayRevenue || 0) + response.data.final_amount,
            todayRentals: (prev.todayRentals || 0) + 1,
            availableGames: (prev.availableGames || 0) + 1
          }));
          
          setGames(prev => prev.map(g => 
            g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
          ));
          
          setAllGames(prev => prev.map(g => 
            g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
          ));
          
        } else {
          alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
        }
      } else {
        alert('ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ ŸäÿØŸàŸäÿßŸã - Ÿäÿ™ŸÖ ŸÜŸÇŸÑŸá ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿπŸÜÿØ ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑŸàŸÇÿ™');
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [getRentalType]);

  // ‚úÖ ÿ•ŸÑÿ∫ÿßÿ° ŸÖÿ®ŸÉÿ±
  const handleEarlyEndRental = useCallback(async (rental) => {
    try {
      if (!rental || !rental.id) {
        alert('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©');
        return;
      }
      
      const isFixedTime = getRentalType(rental) === 'fixed';
      
      if (!isFixedTime) {
        alert('ÿßŸÑÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ± ÿÆÿßÿµ ÿ®ÿßŸÑÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ÿ© ŸÅŸÇÿ∑');
        return;
      }
      
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedMinutes = Math.floor((now - startTime) / 60000);
      
      if (elapsedMinutes > 3) {
        alert(`‚ùå ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ÿπÿØ ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ\nÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©`);
        return;
      }
      
      const paidAmount = rental.paid_amount || rental.total_amount || 0;
      
      if (!window.confirm(`ÿ™ÿ£ŸÉŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ®ŸÉÿ±ÿü\nÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ±ÿØ: ${formatCurrency(paidAmount)}`)) {
        return;
      }
      
      setLoading(prev => ({ ...prev, processing: true }));
      
      const response = await api.post(`/rentals/${rental.id}/cancel-early`, {
        reason: 'ÿ•ŸÑÿ∫ÿßÿ° ŸÖÿ®ŸÉÿ± ÿ®Ÿàÿßÿ≥ÿ∑ÿ© ÿßŸÑŸÖŸàÿ∏ŸÅ'
      });
      
      if (response.success) {
        if (getRentalType(rental) === 'fixed') {
          setStats(prev => ({
            ...prev,
            todayRevenue: Math.max(0, (prev.todayRevenue || 0) - paidAmount)
          }));
        }
        
        // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÑÿπÿ®ÿ©
        setGames(prev => prev.map(g => 
          g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
        ));
        
        setAllGames(prev => prev.map(g => 
          g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
        ));
        
        setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
        
        alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠`);
        
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [getRentalType]);

  // ‚úÖ ÿ™ÿ∫ŸäŸäÿ± ŸÑÿπÿ®ÿ©
  const handleChangeGame = useCallback((rental) => {
    setSelectedRentalForChange(rental);
    setShowChangeGameModal(true);
  }, []);

  const confirmChangeGame = useCallback(async (newGame) => {
    try {
      const rental = selectedRentalForChange;
      if (!rental || !newGame) return;

      setLoading(prev => ({ ...prev, processing: true }));

      const response = await api.updateRental(rental.id, {
        game_id: newGame.id,
        price_per_15min: newGame.price_per_15min,
        notes: `ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ŸÖŸÜ ${rental.game_name} ÿ•ŸÑŸâ ${newGame.name}`
      });

      if (response.success) {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
        setActiveRentals(prev => prev.map(r => 
          r.id === rental.id 
            ? { ...r, game_id: newGame.id, game_name: newGame.name, price_per_15min: newGame.price_per_15min }
            : r
        ));
        
        alert('‚úÖ ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
        setShowChangeGameModal(false);
        setSelectedRentalForChange(null);
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©: ${response.message}`);
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [selectedRentalForChange]);

  // ‚úÖ ÿ•ÿπÿßÿØÿ© ÿ™ÿ£ÿ¨Ÿäÿ±
  const handleRerental = useCallback((completedRental) => {
    setSelectedCompletedRental(completedRental);
    setShowRerentalModal(true);
  }, []);

  const confirmRerental = useCallback(async (newGame, rentalType, duration) => {
    try {
      const rental = selectedCompletedRental;
      if (!rental) return;

      setLoading(prev => ({ ...prev, processing: true }));

      const shiftValid = await validateShiftBeforeRental();
      if (!shiftValid) return;

      const pricePer15Min = newGame.price_per_15min || 100;
      const totalAmount = rentalType === 'fixed' ? (Math.ceil(duration / 15) * pricePer15Min) : 0;

      const rentalRequest = {
        game_id: newGame.id,
        customer_name: rental.customer_name,
        customer_phone: rental.customer_phone,
        rental_type: rentalType || 'fixed',
        duration_minutes: rentalType === 'fixed' ? duration : 15,
        price_per_15min: pricePer15Min,
        total_amount: totalAmount,
        paid_amount: rentalType === 'fixed' ? totalAmount : 0,
        is_open_time: rentalType === 'open' ? 1 : 0,
        payment_method: 'ŸÉÿßÿ¥',
        payment_status: rentalType === 'fixed' ? 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' : 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°',
        notes: `ÿ•ÿπÿßÿØÿ© ÿ™ÿ£ÿ¨Ÿäÿ±`
      };

      const response = await api.post('/rentals', rentalRequest);

      if (response.success) {
        if (rentalType === 'fixed') {
          setStats(prev => ({
            ...prev,
            todayRevenue: (prev.todayRevenue || 0) + totalAmount
          }));
        }
        
        alert('‚úÖ ÿ™ŸÖ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
        setShowRerentalModal(false);
        setSelectedCompletedRental(null);
        
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        setTimeout(() => {
          loadActiveRentals();
        }, 500);
        
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [selectedCompletedRental, validateShiftBeforeRental, loadActiveRentals]);

  // ‚úÖ ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const handleRentalAction = useCallback(async (action, rental) => {
    switch(action) {
      case 'details':
        alert(`üìã ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:
ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${rental.rental_number || 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±'}
ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}
ÿßŸÑŸáÿßÿ™ŸÅ: ${rental.customer_phone}
ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}
ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°: ${formatDateTime(rental.start_time)}
ÿßŸÑÿ≠ÿßŸÑÿ©: ${rental.status}
ÿßŸÑŸÖÿ®ŸÑÿ∫: ${formatCurrency(rental.total_amount || 0)}
ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ: ${rental.payment_status}`);
        break;
        
      case 'end':
        await handleEndRental(rental);
        break;
        
      case 'early_end':
        await handleEarlyEndRental(rental);
        break;
        
      case 'change_game':
        handleChangeGame(rental);
        break;
        
      case 'cancel':
        if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü')) {
          try {
            setLoading(prev => ({ ...prev, processing: true }));
            
            const response = await api.post(`/rentals/${rental.id}/cancel-early`, {
              reason: 'ÿ™ŸÖ ÿßŸÑÿ•ŸÑÿ∫ÿßÿ° ŸÖŸÜ ŸÇÿ®ŸÑ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ'
            });
            
            if (response.success) {
              // ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÑÿπÿ®ÿ©
              setGames(prev => prev.map(g => 
                g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
              ));
              
              setAllGames(prev => prev.map(g => 
                g.id === rental.game_id ? { ...g, status: 'ŸÖÿ™ÿßÿ≠' } : g
              ));
              
              setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
              
              alert('‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
            } else {
              alert(`‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ•ŸÑÿ∫ÿßÿ°: ${response.message}`);
            }
          } catch (error) {
            alert('üî• ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
          } finally {
            setLoading(prev => ({ ...prev, processing: false }));
          }
        }
        break;
        
      default:
        console.warn(`‚ö†Ô∏è ÿ•ÿ¨ÿ±ÿßÿ° ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ: ${action}`);
    }
  }, [handleEndRental, handleEarlyEndRental, handleChangeGame]);


  // ‚úÖ ==================== Effects ŸÖÿπ ÿßŸÑÿ™ŸÜÿ≥ŸäŸÇ ====================

  // ÿ™ÿπŸäŸäŸÜ isMountedRef
  useEffect(() => {
    isMountedRef.current = true;
    return () => {
      isMountedRef.current = false;
      
      // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™ÿßÿ™
      if (refreshTimerRef.current) {
        clearTimeout(refreshTimerRef.current);
      }
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      if (shiftIntervalRef.current) {
        clearInterval(shiftIntervalRef.current);
      }
    };
  }, []);

  // ‚úÖ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸàŸÑŸä - ŸÖÿ±ÿ© Ÿàÿßÿ≠ÿØÿ© ŸÅŸÇÿ∑
  useEffect(() => {
    if (!user?.branch_id) return;
    
    let mounted = true;
    
    const initData = async () => {
      // ÿ™ÿ£ÿÆŸäÿ± ÿ®ÿ≥Ÿäÿ∑ ŸÑÿ™ÿ¨ŸÜÿ® ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ÿµŸäŸäÿ±
      await new Promise(resolve => setTimeout(resolve, 500));
      
      if (mounted) {
        await refreshAllData(true);
      }
    };
    
    initData();
    
    return () => {
      mounted = false;
    };
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user?.branch_id]); // refreshAllData ÿ∫Ÿäÿ± ŸÖÿØÿ±ÿ¨ ŸÑÿ™ÿ¨ŸÜÿ® ÿßŸÑÿ™ŸÉÿ±ÿßÿ±

  // ‚úÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ¥ŸäŸÅÿ™ - ŸÖÿπ ÿ™ÿ£ÿÆŸäÿ± Ÿàÿ≠ŸÖÿßŸäÿ©
  useEffect(() => {
    if (!currentShift?.id || !user?.branch_id) return;
    
    let timer = setTimeout(() => {
      if (isMountedRef.current && !updateLockRef.current) {
        loadActiveRentals();
      }
    }, 300);
    
    return () => clearTimeout(timer);
  }, [currentShift?.id, user?.branch_id, loadActiveRentals]);

  // ‚úÖ ÿßŸÑŸÅÿ≠ÿµ ÿßŸÑÿØŸàÿ±Ÿä ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© - ŸÖÿπ ŸÇŸÅŸÑ ŸàŸÖŸÜÿπ ÿßŸÑÿ™ŸÉÿ±ÿßÿ±
  useEffect(() => {
    if (!currentShift?.id) return;
    
    // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÄ interval ÿßŸÑÿ≥ÿßÿ®ŸÇ
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    
    // ŸÅÿ≠ÿµ ÿ£ŸàŸÑŸä ÿ®ÿπÿØ 10 ÿ´ŸàÿßŸÜŸä
    const initialTimeout = setTimeout(() => {
      if (isMountedRef.current && !updateLockRef.current) {
        processCompletedRentals();
      }
    }, 10000);
    
    // ŸÅÿ≠ÿµ ŸÉŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ
    intervalRef.current = setInterval(() => {
      if (isMountedRef.current && !updateLockRef.current) {
        processCompletedRentals();
      }
    }, 180000); // 3 ÿØŸÇÿßÿ¶ŸÇ
    
    return () => {
      clearTimeout(initialTimeout);
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [currentShift?.id, processCompletedRentals]);

  // ‚úÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿØŸàÿ±Ÿä ŸÑŸÑÿ®ŸäÿßŸÜÿßÿ™ - ŸÖÿπ ŸÇŸÅŸÑ ŸàŸÖŸÜÿπ ÿßŸÑÿ™ŸÉÿ±ÿßÿ±
  useEffect(() => {
    if (!currentShift?.id) return;
    
    // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÄ interval ÿßŸÑÿ≥ÿßÿ®ŸÇ
    if (shiftIntervalRef.current) {
      clearInterval(shiftIntervalRef.current);
    }
    
    // ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ ÿØŸÇŸäŸÇÿ™ŸäŸÜ (ŸàŸÑŸäÿ≥ 60 ÿ´ÿßŸÜŸäÿ©)
    shiftIntervalRef.current = setInterval(() => {
      if (isMountedRef.current && !updateLockRef.current) {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿÆŸÅŸäŸÅ ŸÅŸÇÿ∑ (ŸÑÿß Ÿäÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ŸÉŸÑ ÿ¥Ÿäÿ°)
        loadActiveRentals();
        if (isManager) {
          loadCompletedRentals();
        }
      }
    }, 120000); // ÿØŸÇŸäŸÇÿ™ŸäŸÜ
    
    return () => {
      if (shiftIntervalRef.current) {
        clearInterval(shiftIntervalRef.current);
      }
    };
  }, [currentShift?.id, isManager, loadActiveRentals, loadCompletedRentals]);

  // ‚úÖ ŸÖÿπÿßŸÑÿ¨ÿ© visibility change - ŸÖÿπ ÿ™ÿ£ÿÆŸäÿ± ŸàŸÖŸÜÿπ ÿßŸÑÿ™ŸÉÿ±ÿßÿ±
  useEffect(() => {
    if (!currentShift?.id) return;
    
    const handleVisibilityChange = () => {
      if (!document.hidden && isMountedRef.current && !updateLockRef.current) {
        // ÿ™ÿ£ÿÆŸäÿ± 3 ÿ´ŸàÿßŸÜŸä ÿ®ÿπÿØ ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿµŸÅÿ≠ÿ©
        setTimeout(() => {
          if (isMountedRef.current && !updateLockRef.current) {
            refreshAllData();
          }
        }, 3000);
      }
    };
    
    document.addEventListener('visibilitychange', handleVisibilityChange);
    
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [currentShift?.id, refreshAllData]);

  // ‚úÖ ==================== Render ====================

  return (
    <div className="rentals-container">
      <div className="rentals-header">
        <h1>
          <Gamepad2 size={32} />
          ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
        </h1>
        <div className="rentals-header-actions">
         
          
          <button 
            onClick={() => refreshAllData(true)}
            className="rentals-btn rentals-btn-outline"
            disabled={loading.processing || updateLockRef.current}
          >
            {loading.processing ? (
              <Loader2 className="rentals-spinner" size={16} />
            ) : (
              <RefreshCw size={16} />
            )}
            ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </button>
          
          {currentShift && (
            <div className="rentals-shift-info">
              <Clock size={16} />
              <span>ÿßŸÑÿ¥ŸäŸÅÿ™ #{currentShift.id} - {currentShift.employee_name}</span>
            </div>
          )}
        </div>
      </div>

      <DashboardStats
        stats={stats}
        currentShift={currentShift}
        onStartShift={handleStartShift}
        onEndShift={handleEndShift}
        loading={loading}
        error={error}
        onErrorClose={() => setError(null)}
      />

      <GamesGrid
        games={games}
        branchId={user?.branch_id}
        onSelectGame={handleSelectGame}
        loading={loading}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        filterCategory={filterCategory}
        onFilterChange={setFilterCategory}
      />

      <ActiveRentalsList
        rentals={activeRentals}
        loading={loading}
        onAction={handleRentalAction}
        getRentalType={getRentalType}
      />

      {isManager && (
        <CompletedRentalsList
          rentals={completedRentals}
          loading={loading}
          onRerental={handleRerental}
          onAction={handleRentalAction}
          isManager={isManager}
        />
      )}

      <RentalModal
        show={showRentalModal}
        onClose={() => setShowRentalModal(false)}
        selectedGames={selectedGames}
        onConfirmRental={handleCreateRental}
        currentShift={currentShift}
        allGames={allGames}
        calculateItemPrice={calculateItemPrice}
      />

      <ChangeGameModal
        show={showChangeGameModal}
        onClose={() => {
          setShowChangeGameModal(false);
          setSelectedRentalForChange(null);
        }}
        rental={selectedRentalForChange}
        games={games}
        onConfirm={confirmChangeGame}
      />

      <RerentalModal
        show={showRerentalModal}
        onClose={() => {
          setShowRerentalModal(false);
          setSelectedCompletedRental(null);
        }}
        rental={selectedCompletedRental}
        games={games}
        onConfirm={confirmRerental}
      />
    </div>
  );
};

export default Rentals;
