import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import shiftSyncService from '../../services/ShiftSyncService';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true; // âœ… Ù‚Ø¨ÙˆÙ„ Ø§Ù„Ø­Ù‚Ù„ Ø§Ù„ÙØ§Ø±Øº
  
  // Ø¥Ø°Ø§ ØªÙ… Ø¥Ø¯Ø®Ø§Ù„ Ù‡Ø§ØªÙØŒ Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù†Ù‡
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};


 
 
 
  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });

    const [currentShift, setCurrentShift] = useState(() => shiftSyncService.getCurrentShift());
  const [shiftStats, setShiftStats] = useState(() => shiftSyncService.getShiftStats());
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [currentShiftId, setCurrentShiftId] = useState(null);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

const loadGames = async () => {
  try {
    console.log('ğŸ® ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ÙØ±Ø¹', user?.branch_id);
    
    if (!user?.branch_id) {
      console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯');
      setGames([]);
      return;
    }
    
    // Ø§Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø¯Ø§Ù„Ø© Ø§Ù„Ù…Ø­Ø³Ù†Ø©
    const response = await api.loadGamesSmart(user.branch_id);
      
    if (response.success) {
      console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data.length} Ù„Ø¹Ø¨Ø©`);
      setGames(response.data);
      setAllGames(response.data);
      setStats(prev => ({
        ...prev,
        availableGames: response.data.length,
        totalGames: response.data.length
      }));
    } else {
      console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', response.message);
      setGames([]);
    }
  } catch (error) {
    console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
    setGames([]);
  } finally {
    setLoading(prev => ({ ...prev, games: false }));
  }
};

const loadCurrentShift = async () => {
  try {
    const response = await api.get('/shifts/simple');
    
    if (response.success && response.data) {
      if (response.data.employee_id === user?.id || 
          response.data.employee_name === user?.name) {
        setCurrentShift(response.data);
      } else {
        setCurrentShift(null);
      }
    } else {
      setCurrentShift(null);
    }
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
    setCurrentShift(null);
  } finally {
    setLoading(prev => ({ ...prev, shift: false }));
  }
};

const loadCompletedRentals = async () => {
  if (!isManager) return;
  
  setLoading(prev => ({ ...prev, stats: true }));
  
  try {
    let response;
    
    if (currentShift && currentShift.id) {
      response = await api.get('/rentals/completed-only', {
        params: {
          shift_id: currentShift.id,
          branch_id: user?.branch_id
        }
      });
    } else {
      setCompletedRentals([]);
      setStats(prev => ({
        ...prev,
        todayRevenue: 0,
        todayRentals: 0
      }));
      return;
    }
    
    if (response.success && response.data) {
      const rentals = Array.isArray(response.data) ? response.data : [];
      
      setCompletedRentals(rentals);
      
      // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© (Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…ÙØªÙˆØ­ ÙÙ‚Ø·)
      let completedRevenue = 0;
      rentals.forEach(rental => {
        if (rental.rental_type === 'open' || rental.is_open_time === 1) {
          completedRevenue += parseFloat(rental.final_amount || 0);
        }
      });
      
      setStats(prev => ({
        ...prev,
        todayRevenue: completedRevenue,
        todayRentals: rentals.length
      }));
    } else {
      setCompletedRentals([]);
      setStats(prev => ({
        ...prev,
        todayRevenue: 0,
        todayRentals: 0
      }));
    }
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø©:', error);
    setCompletedRentals([]);
    setStats(prev => ({
      ...prev,
      todayRevenue: 0,
      todayRentals: 0
    }));
  } finally {
    setLoading(prev => ({ ...prev, stats: false }));
  }
};

const loadActiveRentalsForCurrentShift = async () => {
  if (!user?.branch_id || !currentShift?.id) {
    console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯ Ø£Ùˆ Ø´ÙŠÙØª Ù†Ø´Ø·');
    setActiveRentals([]);
    return;
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ù„Ù„Ø´ÙŠÙØª #${currentShift.id}`);
    
    // âœ… Ø§Ø³ØªØ®Ø¯Ø§Ù… Ù†Ù‚Ø·Ø© Ø¬Ø¯ÙŠØ¯Ø© ÙÙŠ API Ù„Ø¬Ù„Ø¨ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ ÙÙ‚Ø·
    try {
      const response = await api.get('/rentals/active-for-shift', {
        params: {
          shift_id: currentShift.id,
          branch_id: user.branch_id
        }
      });
      
      if (response.success) {
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data?.length || 0} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø· Ù„Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ`);
        setActiveRentals(response.data || []);
        setStats(prev => ({
          ...prev,
          activeRentals: response.data?.length || 0
        }));
        return;
      }
    } catch (error) {
      console.warn('âš ï¸ ÙØ´Ù„ /rentals/active-for-shift:', error.message);
    }
    
    // âœ… Ø¨Ø¯ÙŠÙ„: ØªØµÙÙŠØ© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø­Ø³Ø¨ shift_id
    try {
      const response = await api.get('/rentals/active');
      
      if (response.success && response.data) {
        // ØªØµÙÙŠØ© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ù„Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ ÙÙ‚Ø·
        const shiftActiveRentals = response.data.filter(rental => 
          rental.shift_id === currentShift.id
        );
        
        console.log(`âœ… ØªÙ… ØªØµÙÙŠØ© ${shiftActiveRentals.length} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø· Ù„Ù„Ø´ÙŠÙØª #${currentShift.id}`);
        setActiveRentals(shiftActiveRentals);
        setStats(prev => ({
          ...prev,
          activeRentals: shiftActiveRentals.length
        }));
        return;
      }
    } catch (error) {
      console.warn('âš ï¸ ÙØ´Ù„ /rentals/active:', error.message);
    }
    
    // Ø¥Ø°Ø§ ÙØ´Ù„Øª Ø¬Ù…ÙŠØ¹ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø§Øª
    setActiveRentals([]);
    
  } catch (error) {
    console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
};


useEffect(() => {
    // Ù…Ø³ØªÙ…Ø¹ Ù„Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª
    const unsubscribeStarted = shiftSyncService.addListener('SHIFT_STARTED', (data) => {
      console.log('ğŸ”„ [Rentals] Received SHIFT_STARTED event:', data.shift);
      setCurrentShift(data.shift);
      setShiftStats({ todayRevenue: 0, todayRentals: 0, activeRentals: 0 });
    });

    // Ù…Ø³ØªÙ…Ø¹ Ù„Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª
    const unsubscribeEnded = shiftSyncService.addListener('SHIFT_ENDED', (data) => {
