const handleEndOpenTimeEnhanced = async (rental) => {
  try {
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const elapsedMinutes = Math.floor((now - startTime) / 60000);
    
    if (elapsedMinutes < 1) {
      showNotification('error', 'Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ ÿßŸÑŸÖÿØÿ© ÿØŸÇŸäŸÇÿ© Ÿàÿßÿ≠ÿØÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ');
      return;
    }
    
    // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ≥ÿπÿ±
    const pricePer15Min = rental.price_per_15min || 100;
    const units = Math.ceil(elapsedMinutes / 15);
    const totalAmount = pricePer15Min * units;
    
    // ÿπÿ±ÿ∂ ŸÜÿßŸÅÿ∞ÿ© ÿ™ÿ£ŸÉŸäÿØ ŸÖÿπ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
    const confirmMessage = `
‚èπÔ∏è ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠

ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: #${rental.rental_number}
ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}
ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}

ÿßŸÑŸÖÿØÿ© ÿßŸÑŸÅÿπŸÑŸäÿ©: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©
ÿßŸÑÿ≥ÿπÿ± ŸÑŸÉŸÑ 15 ÿØŸÇŸäŸÇÿ©: ${formatCurrency(pricePer15Min)}
ÿπÿØÿØ ÿßŸÑŸàÿ≠ÿØÿßÿ™: ${units}

üí∞ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: ${formatCurrency(totalAmount)}

‚úÖ ÿ≥Ÿäÿ™ŸÖ:
‚Ä¢ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
‚Ä¢ ÿ™ÿ≠ÿµŸäŸÑ ÿßŸÑŸÖÿ®ŸÑÿ∫ ŸÖŸÜ ÿßŸÑÿπŸÖŸäŸÑ
‚Ä¢ ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ ÿ•ŸÑŸâ "ŸÖÿØŸÅŸàÿπ"

ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©ÿü`;

    if (!window.confirm(confirmMessage)) return;
    
    // ÿßÿÆÿ™Ÿäÿßÿ± ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ
    const paymentMethod = prompt(
      `ÿßÿÆÿ™ÿ± ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ ŸÑŸÑŸÖÿ®ŸÑÿ∫ ${formatCurrency(totalAmount)}:\n\n1. ŸÉÿßÿ¥ üíµ\n2. ÿ®ÿ∑ÿßŸÇÿ© üí≥\n3. ŸÅŸàÿØÿßŸÅŸàŸÜ ŸÉÿßÿ¥ üì±\n\nÿ£ÿØÿÆŸÑ ÿßŸÑÿ±ŸÇŸÖ:`,
      '1'
    );
    
    let paymentMethodText = 'ŸÉÿßÿ¥';
    if (paymentMethod === '2') paymentMethodText = 'ÿ®ÿ∑ÿßŸÇÿ©';
    if (paymentMethod === '3') paymentMethodText = 'ŸÅŸàÿØÿßŸÅŸàŸÜ ŸÉÿßÿ¥';
    
    showNotification('info', 'ÿ¨ÿßÿ±Ÿä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸàÿØŸÅÿπ ÿßŸÑŸÖÿ®ŸÑÿ∫...');
    
    const response = await api.put(`/rentals/${rental.id}/complete`, {
      final_amount: totalAmount,
      payment_status: 'ŸÖÿØŸÅŸàÿπ',
      payment_method: paymentMethodText,
      paid_amount: totalAmount,
      notes: `ÿ•ŸÜŸáÿßÿ° ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠ - ÿßŸÑŸÖÿØÿ©: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©ÿå ${units} √ó ${formatCurrency(pricePer15Min)}`
    });
    
    if (response.success) {
      showNotification('success', `‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸàÿØŸÅÿπ ${formatCurrency(totalAmount)} ÿ®ŸÜÿ¨ÿßÿ≠`);
      
      // ÿ•ÿ≤ÿßŸÑÿ© ŸÖŸÜ ÿßŸÑŸÜÿ¥ÿ∑ÿ© Ÿàÿ•ÿ∂ÿßŸÅÿ© ÿ•ŸÑŸâ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
      setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
      setCompletedRentals(prev => [{
        ...rental,
        duration_minutes: elapsedMinutes,
        total_amount: totalAmount,
        payment_method: paymentMethodText,
        payment_status: 'ŸÖÿØŸÅŸàÿπ',
        paid_amount: totalAmount,
        status: 'ŸÖŸÉÿ™ŸÖŸÑ',
        actual_end_time: now.toISOString()
      }, ...prev]);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      await updateStatistics();
      
    } else {
      showNotification('error', response.message || 'ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
    
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠:', error);
    showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
  }
};


