};

  const handleChangeGame = async (rentalId, currentGameId) => {
    try {
      const rental = activeRentals.find(r => r.id === rentalId);
      if (!rental) {
        showNotification('error', 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ');
        return;
      }
      
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ŸàŸÇÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedMinutes = Math.floor((now - startTime) / 60000);
      const remainingMinutes = rental.duration_minutes - elapsedMinutes;
      
      if (remainingMinutes <= 0) {
        showNotification('error', 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜÿ™ŸáŸä ŸàŸÑÿß ŸäŸÖŸÉŸÜ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©');
        return;
      }
      
      // ÿπÿ±ÿ∂ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
      const availableGames = games.filter(game => 
        game.status === 'ŸÖÿ™ÿßÿ≠' && game.id !== currentGameId
      );
      
      if (availableGames.length === 0) {
        showNotification('error', 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ£ÿÆÿ±Ÿâ ŸÖÿ™ÿßÿ≠ÿ©');
        return;
      }
      
      let gamesList = 'ÿßÿÆÿ™ÿ± ŸÑÿπÿ®ÿ© ÿ¨ÿØŸäÿØÿ©:\n\n';
      availableGames.forEach((game, index) => {
        const priceDiff = game.price_per_15min - (rental.price_per_15min || 100);
        gamesList += `${index + 1}. ${game.name} - ${formatCurrency(game.price_per_15min)}`;
        
        if (priceDiff > 0) {
          gamesList += ` (+${formatCurrency(priceDiff)})`;
        } else if (priceDiff < 0) {
          gamesList += ` (${formatCurrency(priceDiff)})`;
        }
        
        gamesList += '\n';
      });
      
      const choice = prompt(gamesList + '\nÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸÑÿπÿ®ÿ©:');
      if (!choice) return;
      
      const selectedIndex = parseInt(choice) - 1;
      if (selectedIndex < 0 || selectedIndex >= availableGames.length) {
        showNotification('error', 'ÿßÿÆÿ™Ÿäÿßÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');
        return;
      }
      
      const selectedGame = availableGames[selectedIndex];
      const priceDiff = selectedGame.price_per_15min - (rental.price_per_15min || 100);
      
      // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÅÿ±ŸÇ ŸÅŸä ÿßŸÑÿ≥ÿπÿ±
      let confirmMessage = `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ÿ•ŸÑŸâ ${selectedGame.name}?\n`;
      
      if (priceDiff > 0) {
        const additionalAmount = Math.ceil(remainingMinutes / 15) * priceDiff;
        confirmMessage += `ÿ≥ŸäŸÉŸàŸÜ ŸáŸÜÿßŸÉ ŸÅÿ±ŸÇ ÿ•ÿ∂ÿßŸÅŸä: ${formatCurrency(additionalAmount)}\n`;
      } else if (priceDiff < 0) {
        const refundAmount = Math.abs(Math.ceil(remainingMinutes / 15) * priceDiff);
        confirmMessage += `ÿ≥Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿ±ÿØÿßÿØ: ${formatCurrency(refundAmount)}\n`;
      }
      
      if (!window.confirm(confirmMessage)) return;
      
      showNotification('info', 'ÿ¨ÿßÿ±Ÿä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©...');
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
      const response = await api.put(`/rentals/${rentalId}/change-game`, {
        new_game_id: selectedGame.id,
        price_difference: priceDiff,
        notes: `ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ŸÖŸÜ ${rental.game_name} ÿ•ŸÑŸâ ${selectedGame.name}`
      });
      
      if (response.success) {
        showNotification('success', 'ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
        
        // ‚ö° **ŸÑÿß ŸÜŸÇŸàŸÖ ÿ®ÿ™ÿ≠ÿØŸäÿ´ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÑÿπÿ®ÿ© - ÿ™ÿ®ŸÇŸâ ŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ™ŸÉÿ±ÿ±**
        
        // ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÅŸä ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÜÿ¥ÿ∑ÿ©
        setActiveRentals(prev => prev.map(r => 
          r.id === rentalId ? {
            ...r,
