                          <span>
                            {Object.values(selectedGameQuantities).reduce((sum, qty) => sum + qty, 0)}
                          </span>
                        </div>
                        
                        <div className="rentals-page-summary-item">
                          <span>ÿßŸÑŸÖÿØÿ©:</span>
                          <span>
                            {durationType === 'fixed' ? `${selectedDuration} ÿØŸÇŸäŸÇÿ©` : 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠'}
                          </span>
                        </div>
                        
                        <div className="rentals-page-summary-item">
                          <span>ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ:</span>
                          <span className={durationType === 'fixed' ? 'text-green-600' : 'text-yellow-600'}>
                            {durationType === 'fixed' ? 'ŸÖÿØŸÅŸàÿπ ÿπŸÜÿØ ÿßŸÑÿ®ÿØÿ°' : 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°'}
                          </span>
                        </div>
                        
                        <div className="rentals-page-summary-item">
                          <span>ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ:</span>
                          <span>
                            {paymentMethod === 'cash' ? 'ŸÉÿßÿ¥' : 
                            paymentMethod === 'card' ? 'ÿ®ÿ∑ÿßŸÇÿ©' : 'ŸÅŸàÿØÿßŸÅŸàŸÜ ŸÉÿßÿ¥'}
                          </span>
                        </div>
                      </div>
                      
                      <div className="rentals-page-summary-total">
                        <span>ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä:</span>
                        <span className="rentals-page-total-amount">
                          {formatCurrency(calculateTotalPrice())}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Footer ŸÖÿπ ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ */}
            <div className="rentals-page-modal-actions rentals-page-enhanced-actions">
              {/* ÿ≤ÿ± ÿßŸÑÿ•ŸÑÿ∫ÿßÿ° */}
              <button 
                className="rentals-page-btn-cancel"
                onClick={() => {
                  if (selectedGames.length > 0 || customerName || customerPhone) {
                    if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü ÿ≥Ÿäÿ™ŸÖ ŸÅŸÇÿØÿßŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿØÿÆŸÑÿ©.')) {
                      setShowRentalModal(false);
                      resetForm();
                    }
                    return;
                  }
                  setShowRentalModal(false);
                }}
              >
                ÿ•ŸÑÿ∫ÿßÿ°
              </button>

              {/* ÿ≤ÿ± ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± */}
              <button 
                className="rentals-page-btn-start-rental"
                onClick={handleStartRentalEnhanced}
                disabled={
                  !currentShift || 
                  !customerName || 
                  !customerPhone || 
                  !validatePhone(customerPhone) || 
                  selectedGames.length === 0
                }
              >
                <div className="rentals-page-btn-content">
                  <span className="rentals-page-btn-icon">üöÄ</span>
                  <div className="rentals-page-btn-text">
                    <div className="rentals-page-btn-title">
                      {durationType === 'fixed' ? 'ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± (ÿØŸÅÿπ ÿßŸÑÿ¢ŸÜ)' : 'ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± (ÿØŸÅÿπ ÿπŸÜÿØ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°)'}
                    </div>
                    <div className="rentals-page-btn-subtitle">
                      {formatCurrency(calculateTotalPrice())} ‚Ä¢ {Object.values(selectedGameQuantities).reduce((sum, qty) => sum + qty, 0)} ŸÑÿπÿ®ÿ©
                    </div>
                  </div>
                </div>
              </button>
            </div>
          </div>
        </>
      )}

      {/* ŸÜÿßŸÅÿ∞ÿ© ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖÿ≤ŸäÿØ ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ® */}
      {showAddMoreGames && (
        <div className="rentals-page-modal-overlay">
          <div className="rentals-page-modal rentals-page-add-games-modal">
            <div className="rentals-page-modal-header">
              <h2>
                <span className="rentals-page-header-icon">‚ûï</span>
                ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ÿ£ÿÆÿ±Ÿâ
              </h2>
              <button onClick={() => setShowAddMoreGames(false)} className="rentals-page-close-btn">‚úï</button>
            </div>
            <div className="rentals-page-modal-body">
              <div className="rentals-page-available-games-list">
                {getAvailableGamesForAddition().length === 0 ? (
                  <div className="rentals-page-empty-state">
                    <div className="rentals-page-empty-icon">‚úÖ</div>
                    <p>ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÖÿ∂ÿßŸÅÿ© ÿ®ÿßŸÑŸÅÿπŸÑ</p>
                  </div>
                ) : (
                  getAvailableGamesForAddition().map(game => (
                    <div 
                      key={game.id} 
                      className="rentals-page-available-game-item"
                      onClick={() => handleAddGameFromModal(game)}
                    >
                      <div className="rentals-page-available-game-image">
                        <img 
                          src={`frontend/src/images/${game.image_url || 'default-game.jpg'}`} 
                          alt={game.name}
                          onError={(e) => {
                            e.target.onerror = null;
                            e.target.src = 'frontend/src/images/default-game.jpg'; 
                          }}
                        />
                      </div>
                      <div className="rentals-page-available-game-info">
                        <h4>{game.name}</h4>
                        <p className="rentals-page-game-category">{game.category || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</p>
                        <div className="rentals-page-game-pricing-info">
                          <div className="rentals-page-price-item">
                            <span>ÿ≥ÿπÿ± 15 ÿØŸÇŸäŸÇÿ©:</span>
                            <span className="rentals-page-price-value">{formatCurrency(game.price_per_15min || 100)}</span>
                          </div>
                        </div>
                      </div>
                      <button 
                        className="rentals-page-btn-add-game"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAddGameFromModal(game);
                        }}
                      >
                        ‚ûï ÿ•ÿ∂ÿßŸÅÿ©
                      </button>
                    </div>
                  ))
                )}
              </div>
              <div className="rentals-page-modal-actions">
                <button onClick={() => setShowAddMoreGames(false)} className="rentals-page-btn-done">
                  ÿ™ŸÖ ({getAvailableGamesForAddition().length} ŸÑÿπÿ®ÿ© ŸÖÿ™ÿßÿ≠ÿ©)
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Rentals;
