      console.log('ğŸ“¥ Ø§Ø³ØªØ¬Ø§Ø¨Ø© Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª:', response.data);
      
      if (response.data && response.data.success) {
        showNotification('success', 'ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­!');
        setCurrentShift(null);
        setShiftStats(null);
        setAvailableGames([]);
        setActiveRentals([]);
      } else {
        showNotification('success', 'ØªÙ… Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª');
        setCurrentShift(null);
        setShiftStats(null);
        setAvailableGames([]);
        setActiveRentals([]);
      }
      
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª:', error);
      showNotification('error', 'ÙØ´Ù„ ÙÙŠ Ø¥Ù†Ù‡Ø§Ø¡ Ø§Ù„Ø´ÙŠÙØª');
