                <div className="form-group">
                  <label>ÿßŸÑŸÅÿ±ÿπ</label>
                  <select
                    value={newUser.branch_id}
                    onChange={(e) => setNewUser({...newUser, branch_id: parseInt(e.target.value)})}
                  >
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>
