import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸàŸÇÿ™ ŸÉŸÑ ÿ´ÿßŸÜŸäÿ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
    const dataInterval = setInterval(loadDashboardData, 30000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const calculateDefaultStats = (branchesData = []) => {
    console.log('üìä ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© ŸÖÿπ ÿßŸÑŸÅÿ±Ÿàÿπ:', branchesData.length);
    
    setStats(prev => ({
      ...prev,
      totalRevenue: 15000,
      totalRentals: 45,
      activeRentals: 3,
      availableGames: 15,
      todayRevenue: 1200,
      todayRentals: 2,
      totalUsers: 8,
      totalBranches: branchesData.length || 3,
      totalGames: 20,
      totalCustomers: 25
    }));
  };

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      console.log('üìä ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÑŸàÿ≠ÿ©...');
      
      // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä ŸàŸÇÿ™ Ÿàÿßÿ≠ÿØ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ Promise.all
      const [
        statsResponse,
        branchesResponse,
        gamesResponse,
        rentalsResponse,
        usersResponse
      ] = await Promise.allSettled([
        analyticsService.getDashboardStats(),
        api.getBranches(),
        api.getGames(),
        api.getRentals({ status: 'ŸÜÿ¥ÿ∑', limit: 5 }),
        api.getUsers()
      ]);
      
      console.log('üìà ŸÜÿ™ÿßÿ¶ÿ¨ ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', {
        stats: statsResponse.status,
        branches: branchesResponse.status,
        games: gamesResponse.status,
        rentals: rentalsResponse.status,
        users: usersResponse.status
      });
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÅÿ±Ÿàÿπ ÿ£ŸàŸÑÿßŸã
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        const branchesData = branchesResponse.value.data || [];
        setBranches(branchesData);
        console.log(`‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ${branchesData.length} ŸÅÿ±ÿπ`);
      } else {
        console.warn('‚ö†Ô∏è  ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑŸÅÿ±Ÿàÿπÿå ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©');
        const mockBranches = [
          { id: 1, name: 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä', location: 'ÿßŸÑŸÇÿßŸáÿ±ÿ©', total_games: 10 },
          { id: 2, name: 'ŸÅÿ±ÿπ ÿßŸÑŸÖÿπÿßÿØŸä', location: 'ÿßŸÑŸÖÿπÿßÿØŸä', total_games: 8 },
          { id: 3, name: 'ŸÅÿ±ÿπ ÿßŸÑÿ™ÿ¨ŸÖÿπ', location: 'ÿßŸÑÿ™ÿ¨ŸÖÿπ ÿßŸÑÿÆÿßŸÖÿ≥', total_games: 12 }
        ];
        setBranches(mockBranches);
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ
      if (statsResponse.status === 'fulfilled' && statsResponse.value.success) {
        const statsData = statsResponse.value.data?.summary || {};
        setStats(prev => ({
          ...prev,
          totalRevenue: statsData.total_revenue || 0,
          totalRentals: statsData.total_rentals || 0,
          activeRentals: statsData.active_rentals || 0,
          availableGames: statsData.available_games || 0,
          todayRevenue: statsData.today_revenue || 0,
          todayRentals: statsData.today_rentals || 0,
          totalUsers: statsData.total_users || 0,
          totalBranches: statsData.total_branches || branches.length,
          totalGames: statsData.total_games || 0,
          totalCustomers: statsData.total_customers || 0
        }));
      } else {
        console.log('üìä ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©');
        calculateDefaultStats(branches);
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const gamesData = gamesResponse.value.data || [];
        setGames(gamesData);
        const availableGames = gamesData.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').length;
        setStats(prev => ({
          ...prev,
          totalGames: gamesData.length,
          availableGames: availableGames
        }));
        
        // ÿ≠ÿ≥ÿßÿ® ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        calculateTopGames(gamesData);
      } else {
        console.warn('‚ö†Ô∏è  ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ®');
        // ÿ•ŸÜÿ¥ÿßÿ° ÿ£ŸÑÿπÿßÿ® ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
        const mockGames = [
          { id: 1, name: 'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±', category: 'ÿ≥Ÿäÿßÿ±ÿßÿ™', price_per_hour: 150, status: 'ŸÖÿ™ÿßÿ≠', rental_count: 15, total_revenue: 4500 },
          { id: 2, name: 'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ÿØÿ±ÿßÿ¨ÿßÿ™ ŸÜÿßÿ±Ÿäÿ©', price_per_hour: 120, status: 'ŸÖÿ™ÿßÿ≠', rental_count: 12, total_revenue: 3600 },
          { id: 3, name: 'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', category: 'ÿ≥Ÿäÿßÿ±ÿßÿ™', price_per_hour: 200, status: 'ŸÖÿ§ÿ¨ÿ±', rental_count: 8, total_revenue: 3200 },
          { id: 4, name: 'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ', category: 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', price_per_hour: 80, status: 'ŸÖÿ™ÿßÿ≠', rental_count: 6, total_revenue: 2400 },
          { id: 5, name: 'ÿ≥ŸÉŸàÿ™ÿ± ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä', category: 'ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©', price_per_hour: 70, status: 'ŸÖÿ™ÿßÿ≠', rental_count: 5, total_revenue: 2100 }
        ];
        setGames(mockGames);
        calculateTopGames(mockGames);
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
        const rentalsData = rentalsResponse.value.data || [];
        setRecentRentals(rentalsData);
        setStats(prev => ({
          ...prev,
          activeRentals: rentalsData.length
        }));
      } else {
        console.warn('‚ö†Ô∏è  ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
        // ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
        const mockRentals = [
          { 
            id: 1, 
            rental_number: 'RENT-001',
            customer_name: 'ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
            customer_phone: '01012345678',
            game_name: 'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±',
            branch_name: 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä',
            total_amount: 150,
            status: 'ŸÜÿ¥ÿ∑',
            start_time: new Date(Date.now() - 30 * 60000).toISOString()
          },
          { 
            id: 2, 
            rental_number: 'RENT-002',
            customer_name: 'ŸÖÿ≠ŸÖÿØ ÿπŸÑŸä',
            customer_phone: '01123456789',
            game_name: 'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä',
            branch_name: 'ŸÅÿ±ÿπ ÿßŸÑŸÖÿπÿßÿØŸä',
            total_amount: 120,
            status: 'ŸÜÿ¥ÿ∑',
            start_time: new Date(Date.now() - 45 * 60000).toISOString()
          }
        ];
        setRecentRentals(mockRentals);
      }
      
      // ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ
      if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
