              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ© ÿ≠ÿßŸÑŸäÿßŸã</p>
              <p className="hint">ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿ™ÿ™ŸÖ ŸÅŸÇÿ∑ ŸÖŸÜ ÿÆŸÑÿßŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ</p>
            </div>
          ) : (
            <div className="rentals-table">
              <table>
                <thead>
                  <tr>
                    <th>ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</th>
                    <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                    <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                    <th>ÿßŸÑŸÅÿ±ÿπ</th>
                    <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
                    <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                    <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                  </tr>
                </thead>
                <tbody>
                  {recentRentals.map((rental, index) => (
                    <tr key={rental.id || index}>
                      <td>
                        <strong>#{rental.rental_number || rental.id || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}</strong>
                        {rental.start_time && (
                          <div className="rental-time">
                            <small>ÿ®ÿØÿ£: {new Date(rental.start_time).toLocaleTimeString('ar-EG', {hour: '2-digit', minute:'2-digit'})}</small>
                          </div>
                        )}
                      </td>
                      <td>
                        <div className="customer-info">
                          <div className="customer-name">{rental.customer_name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}</div>
                          {rental.customer_phone && (
                            <small className="customer-phone">{rental.customer_phone}</small>
                          )}
                        </div>
                      </td>
                      <td>{rental.game_name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}</td>
                      <td>{rental.branch_name || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}</td>
                      <td className="amount">{formatCurrency(rental.total_amount || 0)}</td>
                      <td>
                        <span className={`status-badge ${getStatusBadgeClass(rental.status)}`}>
                          {rental.status || 'ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          {rental.status === 'ŸÜÿ¥ÿ∑' && (
                            <>
                              <button 
                                className="btn-action btn-success"
                                onClick={() => handleRentalAction(rental.id, 'end')}
                                title="ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                              >
                                ‚úÖ
                              </button>
                              <button 
                                className="btn-action btn-warning"
                                onClick={() => handleRentalAction(rental.id, 'extend')}
                                title="ÿ™ŸÖÿØŸäÿØ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                              >
                                ‚è±Ô∏è
                              </button>
                              <button 
                                className="btn-action btn-danger"
                                onClick={() => handleRentalAction(rental.id, 'cancel')}
                                title="ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                              >
                                ‚ùå
                              </button>
                            </>
                          )}
                          <button 
                            className="btn-action btn-info"
                            onClick={() => handleRentalAction(rental.id, 'details')}
                            title="ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                          >
                            üëÅÔ∏è
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑÿ£ŸÉÿ´ÿ± ÿ™ÿ£ÿ¨Ÿäÿ±ÿßŸã */}
        <div className="top-games-section">
          <div className="section-header">
            <h2>üèÜ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑÿ£ŸÉÿ´ÿ± ÿ™ÿ£ÿ¨Ÿäÿ±ÿßŸã</h2>
            <Link to="/admin/games" className="btn-view-all">
              ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ‚Üí
            </Link>
          </div>
          
          {topGames.length === 0 ? (
            <div className="empty-state">
              <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿπŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
              <p className="hint">ÿ≥Ÿäÿ™ŸÖ ÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ®ÿπÿØ ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</p>
            </div>
          ) : (
            <div className="games-grid">
              {topGames.map((game, index) => (
                <div key={game.id || index} className="game-card">
                  <div className="game-image-container">
                    {game.image_url ? (
                      <img 
                        src={`/images/${game.image_url}`}
                        alt={game.name}
                        className="game-image"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "/images/default-game.jpg";
                        }}
                      />
                    ) : (
                      <div className="game-image-placeholder">
                        <span className="placeholder-icon">üéÆ</span>
                      </div>
                    )}
                    <div className="game-overlay">
                      <div className="game-rank">üèÜ #{index + 1}</div>
                      <div className="game-price">
                        <span className="price-amount">{game.price_per_hour || 0}</span>
                        <span className="price-unit">ÿ¨/ÿ≥ÿßÿπÿ©</span>
                      </div>
                    </div>
                  </div>
                  
                  <div className="game-card-body">
                    <h3 className="game-title">{game.name || 'ŸÑÿπÿ®ÿ© ÿ®ÿØŸàŸÜ ÿßÿ≥ŸÖ'}</h3>
                    <div className="game-category">{game.category || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</div>
                    <div className="game-stats">
                      <div className="stat-item">
                        <span className="stat-label">ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:</span>
                        <span className="stat-value">{game.rental_count || 0}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™:</span>
                        <span className="stat-value">{formatCurrency(game.total_revenue || 0)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* ÿßŸÑÿµŸÅ ÿßŸÑÿ±ÿßÿ®ÿπ: ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑÿ¥Ÿáÿ±Ÿäÿ© */}
      <div className="monthly-revenue-section">
        <div className="section-header">
          <h2>üìà ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑÿ¥Ÿáÿ±Ÿäÿ© (ÿ¢ÿÆÿ± 6 ÿ£ÿ¥Ÿáÿ±)</h2>
        </div>
        
        {monthlyRevenue.length === 0 ? (
          <div className="empty-state">
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™ ÿπŸÜ ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑÿ¥Ÿáÿ±Ÿäÿ©</p>
          </div>
        ) : (
          <div className="revenue-chart">
            <div className="chart-container">
              <div className="chart-bars">
                {monthlyRevenue.map((monthData, index) => {
                  const maxRevenue = Math.max(...monthlyRevenue.map(m => m.revenue || 0));
                  const height = maxRevenue > 0 ? (monthData.revenue / maxRevenue) * 100 : 0;
                  
                  return (
                    <div key={index} className="chart-bar-container">
                      <div className="chart-bar-wrapper">
                        <div 
                          className="chart-bar" 
                          style={{ height: `${height}%` }}
                        >
                          <div className="chart-bar-value">
                            {formatCurrency(monthData.revenue)}
                          </div>
                        </div>
                      </div>
                      <div className="chart-label">
                        {monthData.month_name || monthData.month}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
            
            <div className="revenue-summary">
              <div className="summary-card">
                <h3>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿ¢ÿÆÿ± 6 ÿ£ÿ¥Ÿáÿ±</h3>
                <h2>
                  {formatCurrency(
                    monthlyRevenue.reduce((sum, month) => sum + (month.revenue || 0), 0)
                  )}
                </h2>
              </div>
              
              <div className="summary-card">
                <h3>ŸÖÿ™Ÿàÿ≥ÿ∑ ÿ¥Ÿáÿ±Ÿä</h3>
                <h2>
                  {formatCurrency(
                    monthlyRevenue.length > 0 
                      ? monthlyRevenue.reduce((sum, month) => sum + (month.revenue || 0), 0) / monthlyRevenue.length
                      : 0
                  )}
                </h2>
              </div>
              
              <div className="summary-card">
                <h3>ÿ£ÿπŸÑŸâ ÿ¥Ÿáÿ±</h3>
                <h2>
                  {monthlyRevenue.length > 0 
                    ? formatCurrency(Math.max(...monthlyRevenue.map(m => m.revenue || 0)))
                    : formatCurrency(0)
                  }
                </h2>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
