// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState(null);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  const timersRef = useRef({});

  // ÿ™ÿ£ÿ´Ÿäÿ±ÿßÿ™
  useEffect(() => {
    if (user) {
      initializeShift();
      loadData();
      
      const interval = setInterval(() => {
        if (currentShift) {
          loadData();
        }
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user, currentShift]);

  // ==================== SHIFT MANAGEMENT ====================


const initializeShift = async () => {
  try {
    console.log('üîÑ ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑...');
    
    // ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸáŸÑÿ© ÿ≤ŸÖŸÜŸäÿ© ŸÑŸÑÿ∑ŸÑÿ®
    const response = await Promise.race([
      api.get('/shifts/active'),
      new Promise((_, reject) => 
        setTimeout(() => reject(new Error('ÿßŸÜÿ™Ÿáÿ™ ŸÖŸáŸÑÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ')), 10000)
      )
    ]);
    
    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿßŸÑÿ¥ÿßŸÖŸÑ ŸÑŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
    if (!response) {
      console.error('‚ùå ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ŸÅÿßÿ±ÿ∫ÿ© ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ');
      showNotification('error', 'ÿßŸÑÿÆÿßÿØŸÖ ŸÑŸÖ Ÿäÿ≥ÿ™ÿ¨ÿ®');
      setCurrentShift(null);
      return;
    }
    
    console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿÆÿßÿØŸÖ ŸÉÿßŸÖŸÑÿ©:', response);
    console.log('üìä ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©:', response.data);
    
    // ‚ùå**ÿßŸÑÿ™ÿµÿ≠Ÿäÿ≠**: ŸÅÿ≠ÿµ ŸÖÿ®ÿßÿ¥ÿ± ŸÑŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿØŸÑÿßŸã ŸÖŸÜ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ success
    const responseData = response.data;
    
    // ÿßŸÑÿ≠ÿßŸÑÿ© 1: ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ£ÿ™Ÿä ŸÖÿ®ÿßÿ¥ÿ±ÿ© (ÿ®ÿØŸàŸÜ ŸÉÿßÿ¶ŸÜ ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©)
    if (responseData && responseData.id) {
      console.log('‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ¥ŸäŸÅÿ™ ŸÖÿ®ÿßÿ¥ÿ±:', responseData.id);
      setCurrentShift(responseData);
      await loadShiftStats(responseData.id);
      return;
    }
    
    // ÿßŸÑÿ≠ÿßŸÑÿ© 2: ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ£ÿ™Ÿä ŸÅŸä ÿ≠ŸÇŸÑ data
    if (responseData && responseData.data) {
      if (responseData.data.id) {
        console.log('‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ¥ŸäŸÅÿ™ ŸÅŸä ÿ≠ŸÇŸÑ data:', responseData.data.id);
        setCurrentShift(responseData.data);
        await loadShiftStats(responseData.data.id);
        return;
      } else {
        console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ (data ŸÖŸàÿ¨ŸàÿØ ŸàŸÑŸÉŸÜ ŸÅÿßÿ±ÿ∫)');
        setCurrentShift(null);
        return;
      }
    }
    
    // ÿßŸÑÿ≠ÿßŸÑÿ© 3: ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™
    console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑');
    setCurrentShift(null);
    
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑:', error);
    setCurrentShift(null);
    
    // ÿπÿ±ÿ∂ ÿ±ÿ≥ÿßŸÑÿ© ÿÆÿ∑ÿ£ ŸÖŸÜÿßÿ≥ÿ®ÿ©
    if (error.message === 'ÿßŸÜÿ™Ÿáÿ™ ŸÖŸáŸÑÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ') {
      showNotification('error', 'ÿßŸÜÿ™Ÿáÿ™ ŸÖŸáŸÑÿ© ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ');
    } else if (error.response) {
      console.error('üìä ÿ™ŸÅÿßÿµŸäŸÑ ÿÆÿ∑ÿ£ ÿßŸÑÿÆÿßÿØŸÖ:', {
        status: error.response.status,
        data: error.response.data
      });
      showNotification('error', `ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ: ${error.response.status}`);
    } else if (error.request) {
      console.error('üì° ŸÑŸÖ Ÿäÿ™ŸÖ ÿ™ŸÑŸÇŸä ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ');
