      };
      const minutesLeft = Math.floor(timeLeftMs / 60000);
      const hours = Math.floor(minutesLeft / 60);
      const minutes = minutesLeft % 60;
      return {
        expired: false,
        minutes: minutesLeft,
        formatted: hours > 0 ? `${hours} ÿ≥ÿßÿπÿ© ${minutes} ÿØŸÇŸäŸÇÿ©` : `${minutes} ÿØŸÇŸäŸÇÿ©`
