// src/services/authService.js - ÿßŸÑŸÜÿ≥ÿÆÿ© ÿßŸÑŸÖÿ≠ÿØÿ´ÿ© ŸàÿßŸÑŸÉÿßŸÖŸÑÿ©
import api from './api';

const authService = {
  // ================= AUTHENTICATION =================
  
  login: async (email, password) => {
