        console.log('üë§ ÿ™ŸÖ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', user);
        
        if (!token) {
          console.error('‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ!');
          return {
            success: false,
            message: 'ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ŸÑÿßŸÖ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ'
          };
        }
        
        // ‚ö° **ÿ™ÿÆÿ≤ŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ŸàÿßŸÑÿ®ŸäÿßŸÜÿßÿ™**
        api.setToken(token);
        localStorage.setItem('user', JSON.stringify(user));
        localStorage.setItem('lastLogin', new Date().toISOString());
        
        console.log('‚úÖ ÿ™ŸÖ ÿ™ÿÆÿ≤ŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ ŸàÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
        
        return {
          success: true,
          data: response.data,
          message: 'ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠'
        };
      } else {
        return {
          success: false,
          message: response.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
        };
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ ÿßŸÑŸÖŸÜŸÅÿ∞ 5000'
      };
    }
  },

  logout: () => {
    api.logout();
  },

  getCurrentUser: () => {
    try {
      const userStr = localStorage.getItem('user');
      return userStr ? JSON.parse(userStr) : null;
    } catch (error) {
      return null;
    }
  },

  getToken: () => {
    const token = localStorage.getItem('token');
    return token ? token.trim() : null;
  },

  isAuthenticated: () => {
    const token = authService.getToken();
    const user = authService.getCurrentUser();
    return !!(token && user);
  },

  // ================= PROFILE MANAGEMENT =================
  
  updateProfile: async (profileData) => {
    try {
      const response = await api.updateProfile(profileData);
      
      if (response.success && response.user) {
        localStorage.setItem('user', JSON.stringify(response.user));
      }
      
      return response;
    } catch (error) {
      return {
        success: false,
        message: error.message || 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä'
      };
    }
  },

  changePassword: async (passwordData) => {
