    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿ®ÿ≥ÿ∑ÿ©:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™',
        data: {
          available_games: 0,
          total_games: 0,
          today_revenue: 0,
          today_rentals: 0,
          active_rentals: 0
        }
      };
    }
  },

  // üÜï ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©: ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±
  getManagerStats: async () => {
    try {
      console.log('üëë ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±...');
      const response = await api.getManagerStats ? 
        await api.getManagerStats() : 
        await api.get('/dashboard/stats/manager');
      
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖÿØŸäÿ±'
      };
    }
  },

  // ÿØÿßŸÑÿ© ŸÖŸàÿ¨ŸàÿØÿ© ÿ®ÿßŸÑŸÅÿπŸÑ - ŸÜÿ≥ÿÆÿ© ŸÖÿ≠ÿ≥ŸÜÿ©
  getDashboardStats: async () => {
    try {
      console.log('üìà ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ...');
      const response = await api.get('/dashboard/stats');
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™',
        data: { 
          summary: {},
          today_revenue: 0,
          today_rentals: 0,
          active_rentals: 0,
          total_games: 0,
          available_games: 0,
          total_users: 0,
          total_branches: 0
        }
      };
    }
  },

  // üÜï ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©: ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÉÿßŸÖŸÑÿ© ŸÑŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
  getDashboardAllStats: async () => {
    try {
      console.log('üìä ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ...');
      const response = await api.getDashboardAllStats ? 
        await api.getDashboardAllStats() : 
        await api.get('/dashboard/stats/all');
      
      return response;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™:', error);
      
      // Fallback: ÿ¨ŸÑÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ŸÅŸÇÿ∑
      try {
        const basicStats = await authService.getDashboardStats();
        return {
          success: basicStats.success,
          data: {
            basic: basicStats.data || {},
            branchRevenues: [],
            completedRentalsByBranch: [],
            todayRevenue: basicStats.data?.today_revenue || 0,
            todayRentals: basicStats.data?.today_rentals || 0
          }
        };
      } catch {
        return {
          success: false,
          message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™',
          data: {
            basic: {},
            branchRevenues: [],
            completedRentalsByBranch: [],
            todayRevenue: 0,
            todayRentals: 0
          }
        };
      }
    }
  },

  // ================= BRANCHES MANAGEMENT =================
  
  getBranches: async () => {
