        data: []
      };
    }
  },

  createBranch: async (branchData) => {
    try {
      console.log('üîë ÿßŸÑÿ™ŸàŸÉŸÜ ÿßŸÑÿ≠ÿßŸÑŸä:', authService.getToken());
      console.log('üë§ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä:', authService.getCurrentUser());
      
      const response = await api.post('/branches', branchData);
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑŸÅÿ±ÿπ:', response);
      return response;
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä createBranch:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      return {
        success: false,
