  deleteBranch: async (id) => {
    try {
      return await api.delete(`/branches/${id}`);
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ'
      };
    }
  },

  // ÿ≠ÿ∞ŸÅ ŸÅÿ±ÿπ ŸÜŸáÿßÿ¶ŸäÿßŸã
  deleteBranchPermanent: async (id) => {
    try {
      console.log('üóëÔ∏è [AUTH SERVICE] ÿ≠ÿ∞ŸÅ ŸÅÿ±ÿπ ŸÜŸáÿßÿ¶ŸäÿßŸã:', id);
      
      const token = authService.getToken();
      if (!token) {
        return {
          success: false,
          message: 'ÿßŸÑÿ™ŸàŸÉŸÜ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ'
        };
      }
      
      const response = await fetch(`http://localhost:5000/api/branches/${id}?permanent=true`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      const data = await response.json();
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ:', data);
      
      return {
        success: data.success || false,
        message: data.message || 'ÿ™ŸÖ ÿßŸÑÿ≠ÿ∞ŸÅ',
        data: data
      };
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ∞ŸÅ ÿßŸÑŸÅÿ±ÿπ:', error);
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ: ' + error.message
      };
    }
  },

  // ================= GAMES MANAGEMENT =================
  
 // ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ©
getGames: async (params = {}) => {
  try {
    console.log('üéÆ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ®:', params);
    
    // ÿ•ÿ∞ÿß ÿ∑ŸÑÿ®ŸÜÿß ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπÿå ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©
    if (params.all_branches === true) {
      return await authService.getAllGamesFromAllBranches();
    }
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÅŸÑÿ™ÿ± ŸÖÿ™ŸÇÿØŸÖÿå ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿØÿßŸÑÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©
    if (params.branch_id || params.status || params.category) {
      return await authService.getGamesWithFilter(params);
    }
    
    // Ÿàÿ•ŸÑÿß ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿπÿßÿØŸäÿ©
    const response = await api.get('/games', { params });
    
    if (!response.data) {
      return {
        success: false,
        message: 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ®ŸäÿßŸÜÿßÿ™',
        data: [],
        stats: { total: 0, available: 0, rented: 0, maintenance: 0 }
      };
    }
    
    // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
    const stats = {
      total: response.data.length,
      available: response.data.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').length,
      rented: response.data.filter(g => g.status === 'ŸÖÿ§ÿ¨ÿ±ÿ©').length,
      maintenance: response.data.filter(g => g.status === 'ÿµŸäÿßŸÜÿ©').length
    };
    
    return {
      success: true,
      data: response.data,
      stats: stats,
      message: `ÿ™ŸÖ ÿ¨ŸÑÿ® ${response.data.length} ŸÑÿπÿ®ÿ©`
