getGamesStatsByBranch: async () => {
  try {
    console.log('üìä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ≠ÿ≥ÿ® ÿßŸÑŸÅÿ±Ÿàÿπ...');
    
    // ‚úÖ **ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ SQL ŸÖÿ®ÿßÿ¥ÿ± ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ ÿ•ÿ∞ÿß ŸÑŸÖ ÿ™ŸÉŸÜ ÿßŸÑŸÜŸÇÿ∑ÿ© ŸÖŸàÿ¨ŸàÿØÿ©**
    const gamesResponse = await authService.getGames();
    
    if (gamesResponse.success) {
      const gamesData = gamesResponse.data || [];
      const branchesResponse = await authService.getBranches();
      const branchesData = branchesResponse.success ? branchesResponse.data : [];
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÖÿ≠ŸÑŸäÿßŸã
      const stats = {};
      const totals = {
        total_branches: branchesData.length,
        total_games: 0,
        available_games: 0,
        rented_games: 0,
        maintenance_games: 0
      };
      
      // ÿ™ŸáŸäÿ¶ÿ© ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸÉŸÑ ŸÅÿ±ÿπ
      branchesData.forEach(branch => {
        stats[branch.id] = {
          branch_id: branch.id,
          branch_name: branch.name,
          total_games: 0,
          available_games: 0,
          rented_games: 0,
          maintenance_games: 0
        };
      });
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÉŸÑ ŸÅÿ±ÿπ
      gamesData.forEach(game => {
        const branchId = game.branch_id;
        if (stats[branchId]) {
          stats[branchId].total_games++;
          totals.total_games++;
          
          switch (game.status) {
            case 'ŸÖÿ™ÿßÿ≠':
              stats[branchId].available_games++;
              totals.available_games++;
              break;
            case 'ŸÖÿ§ÿ¨ÿ±ÿ©':
              stats[branchId].rented_games++;
              totals.rented_games++;
              break;
            case 'ÿµŸäÿßŸÜÿ©':
              stats[branchId].maintenance_games++;
              totals.maintenance_games++;
              break;
          }
        }
      });
      
      return {
        success: true,
        data: Object.values(stats),
        totals: totals,
        message: `ÿ™ŸÖ ÿ≠ÿ≥ÿßÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ${branchesData.length} ŸÅÿ±ÿπ`
      };
    }
    
    return {
      success: false,
      message: 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™',
      data: [],
      totals: {}
    };
    
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ:', error);
    return {
      success: false,
      message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ',
      data: [],
      totals: {}
    };
  }
},

