  searchGamesInCurrentBranch: async (searchTerm) => {
    try {
      const user = authService.getCurrentUser();
      if (!user?.branch_id) {
        return {
          success: false,
          message: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ŸÅÿ±ÿπ',
          data: []
        };
      }
      
      // ŸÖÿ≠ÿßŸàŸÑÿ© ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿ≠ÿ´ ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ŸÖŸàÿ¨ŸàÿØÿ©
      try {
        const response = await api.get(`/branches/${user.branch_id}/games/search`, {
          params: { q: searchTerm }
        });
        
        if (response.success) return response;
      } catch (searchError) {
        console.log('‚ö†Ô∏è ŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿ≠ÿ´ ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠ÿ©ÿå ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ™ÿµŸÅŸäÿ© ÿßŸÑŸÖÿ≠ŸÑŸäÿ©');
      }
      
      // fallback: ÿ™ÿµŸÅŸäÿ© ŸÖÿ≠ŸÑŸäÿ©
      const allGames = await authService.getAvailableGamesForCurrentBranch();
      if (allGames.success) {
        const filtered = allGames.data.filter(game =>
          game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          game.category?.toLowerCase().includes(searchTerm.toLowerCase())
        );
        
        return {
          success: true,
          data: filtered,
          message: `ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ${filtered.length} ŸÜÿ™Ÿäÿ¨ÿ©`
        };
      }
      
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®',
        data: []
      };
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®',
        data: []
      };
    }
  },

  updateRentalTimers: async () => {
    try {
      const user = authService.getCurrentUser();
      if (!user?.branch_id) return;
      
      const response = await api.get(`/rentals/${user.branch_id}/check-expired`);
      return response;
    } catch (error) {
      console.warn('‚ö†Ô∏è ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸàŸÇÿ™:', error.message);
    }
  },

  refreshCurrentBranchData: async () => {
    try {
      const user = authService.getCurrentUser();
      if (!user?.branch_id) return null;
      
      const [gamesResponse, rentalsResponse, statsResponse] = await Promise.all([
        authService.getAvailableGamesForCurrentBranch(),
        authService.getActiveRentalsForCurrentBranch(),
        authService.getCurrentBranchStats()
      ]);
      
      return {
        success: true,
        games: gamesResponse.success ? gamesResponse.data : [],
        activeRentals: rentalsResponse.success ? rentalsResponse.data : [],
        stats: statsResponse.success ? statsResponse.data : null,
        message: 'ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™'
      };
    } catch (error) {
      return {
        success: false,
        message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
        games: [],
        activeRentals: [],
        stats: null
      };
    }
  },

  // ================= UTILITY FUNCTIONS =================
  
  formatError: (error) => {
    if (error.response) {
      return error.response.data?.message || `ÿÆÿ∑ÿ£ ${error.response.status}: ${error.response.statusText}`;
    } else if (error.request) {
      return 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ';
    } else {
      return error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  },

  hasRole: (role) => {
    const user = authService.getCurrentUser();
    return user?.role === role;
  },

  hasAnyRole: (roles) => {
    const user = authService.getCurrentUser();
    return roles.includes(user?.role);
  },

  validateCustomerPhone: (phone) => {
    const phoneRegex = /^01[0-9]{9}$/;
    return phoneRegex.test(phone);
  },

  formatCurrency: (amount) => {
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(num);
  },

  formatDuration: (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ©${remainingMinutes > 0 ? ` ${remainingMinutes} ÿØŸÇŸäŸÇÿ©` : ''}`;
  },

  formatTime: (dateString) => {
    try {
      return new Date(dateString).toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  },

  formatDate: (dateString) => {
    try {
      return new Date(dateString).toLocaleDateString('ar-EG', {
        weekday: 'short',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch {
      return '--/--/----';
    }
  },

  showNotification: (type, message) => {
    const messages = {
      error: `‚ùå ${message}`,
      success: `‚úÖ ${message}`,
      warning: `‚ö†Ô∏è ${message}`,
      info: `‚ÑπÔ∏è ${message}`
    };
    
    // ŸäŸÖŸÉŸÜ ÿßÿ≥ÿ™ÿ®ÿØÿßŸÑ Ÿáÿ∞ÿß ÿ®ŸÖŸÉÿ™ÿ®ÿ© ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ŸÖÿ´ŸÑ react-toastify
    alert(messages[type] || message);
  },

  calculateTimeRemaining: (endTime) => {
    if (!endTime) return null;
    
    try {
      const end = new Date(endTime);
      const now = new Date();
      const timeLeftMs = end - now;
      
      if (timeLeftMs <= 0) return { expired: true, minutes: 0, formatted: 'ÿßŸÜÿ™ŸáŸâ' };
      
      const minutesLeft = Math.floor(timeLeftMs / 60000);
      const hours = Math.floor(minutesLeft / 60);
      const minutes = minutesLeft % 60;
      
      return {
        expired: false,
        minutes: minutesLeft,
        formatted: hours > 0 ? `${hours} ÿ≥ÿßÿπÿ© ${minutes} ÿØŸÇŸäŸÇÿ©` : `${minutes} ÿØŸÇŸäŸÇÿ©`
      };
    } catch (error) {
      return null;
    }
  },

  prepareRentalFormData: () => {
    return {
      customer_name: '',
      customer_phone: '',
      customer_notes: '',
      duration_type: 'fixed',
      duration_minutes: 60,
      selected_games: [],
      quantities: {},
      discount: 0,
      payment_method: 'cash'
    };
  },

  validateRentalForm: (formData) => {
    const errors = [];
    
    if (!formData.customer_name?.trim()) {
      errors.push('ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ ŸÖÿ∑ŸÑŸàÿ®');
    }
    
    if (!formData.customer_phone?.trim()) {
      errors.push('ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ŸÖÿ∑ŸÑŸàÿ®');
    } else if (!authService.validateCustomerPhone(formData.customer_phone)) {
      errors.push('ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠. Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ®ÿØÿ£ ÿ®ŸÄ 01 ŸàŸäÿ™ŸÉŸàŸÜ ŸÖŸÜ 11 ÿ±ŸÇŸÖ');
    }
    
    if (formData.selected_games.length === 0) {
      errors.push('Ÿäÿ¨ÿ® ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ© Ÿàÿßÿ≠ÿØÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ');
    }
    
    if (formData.duration_type === 'fixed' && !formData.duration_minutes) {
      errors.push('Ÿäÿ¨ÿ® ÿ™ÿ≠ÿØŸäÿØ ŸÖÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
    
    return {
      isValid: errors.length === 0,
      errors: errors
    };
  },

  calculateRentalPrice: (game, durationMinutes, quantity = 1) => {
    if (!game) return 0;
    
    let price = 0;
    
    if (durationMinutes >= 60) {
      price = (game.price_per_hour || 0) * (durationMinutes / 60);
    } else {
      const quarters = Math.ceil(durationMinutes / 15);
      price = (game.price_per_15min || 0) * quarters;
    }
    
    return price * quantity;
  }
};

export default authService;
