// src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø© Ø¨Ø¯Ø¡ Ø§Ù„ØªØ£Ø¬ÙŠØ±
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [quantity, setQuantity] = useState(1);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  
  // Refs Ù„Ù„Ù…Ø¤Ù‚ØªØ§Øª
  const timersRef = useRef({});

  // Ù…Ø¯Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ø§Ù„Ø«Ø§Ø¨ØªØ©
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©', priceFactor: 1 },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©', priceFactor: 2 },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©', priceFactor: 3 },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©', priceFactor: 4 },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†', priceFactor: 8 },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª', priceFactor: 12 },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª', priceFactor: 16 }
  ];

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const interval = setInterval(() => {
        loadData();
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);

  // ==================== FUNCTIONS ====================

  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      if (response.success) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª:', error);
    }
  };

  const startShift = async () => {
    try {
      const response = await api.startShift();
      if (response.success) {
        setCurrentShift(response.data);
        showNotification('success', 'ØªÙ… Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª Ø¨Ù†Ø¬Ø§Ø­');
        loadData();
      }
    } catch (error) {
      showNotification('error', 'ÙØ´Ù„ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª');
    }
  };

  const loadData = async () => {
    try {
      if (!user?.branch_id) return;
      
      setLoading(true);
      
      // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
      const branchResponse = await api.get(`/branches/${user.branch_id}`);
      if (branchResponse.success) {
        setBranch(branchResponse.data);
      }
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      await loadGames();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
      await loadActiveRentals();
      
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadGames = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get(`/branches/${user.branch_id}/games`);
      if (response.success) {
        setGames(response.data || []);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setGames([]);
    }
  };

  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'Ù†Ø´Ø·'
        }
      });
      
      if (response.success) {
        setActiveRentals(response.data);
        startTimers(response.data);
      }
    } catch (error) {
      console.error('Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
      setActiveRentals([]);
    }
  };

  const startTimers = (rentals) => {
    // ØªÙ†Ø¸ÙŠÙ Ø§Ù„Ù…Ø¤Ù‚ØªØ§Øª Ø§Ù„Ù‚Ø¯ÙŠÙ…Ø©
    Object.values(timersRef.current).forEach(timer => clearInterval(timer));
    timersRef.current = {};
    
    // Ù„Ø§ Ø¯Ø§Ø¹ÙŠ Ù„Ø¹Ù…Ù„ timers ÙÙŠ Ù‡Ø°Ù‡ Ø§Ù„Ù†Ø³Ø®Ø© Ø§Ù„Ù…Ø¨Ø³Ø·Ø©
  };

  // ğŸ® Ø¹Ù†Ø¯ Ø§Ù„Ù†Ù‚Ø± Ø¹Ù„Ù‰ Ù„Ø¹Ø¨Ø©
  const handleGameClick = (game) => {
    if (!currentShift) {
      showNotification('error', 'ÙŠØ¬Ø¨ Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø£ÙˆÙ„Ø§Ù‹ Ù‚Ø¨Ù„ Ø¨Ø¯Ø¡ ØªØ£Ø¬ÙŠØ± Ø¬Ø¯ÙŠØ¯');
      startShift();
      return;
    }
    
    if (game.status !== 'Ù…ØªØ§Ø­') {
      showNotification('error', `Ø§Ù„Ù„Ø¹Ø¨Ø© ØºÙŠØ± Ù…ØªØ§Ø­Ø© Ù„Ù„Ø§Ø³ØªØ®Ø¯Ø§Ù… (${game.status})`);
      return;
    }
    
    if (game.available_quantity < 1) {
      showNotification('error', 'Ù„Ø§ ØªÙˆØ¬Ø¯ ÙƒÙ…ÙŠØ© Ù…ØªØ§Ø­Ø© Ù…Ù† Ù‡Ø°Ù‡ Ø§Ù„Ù„Ø¹Ø¨Ø©');
      return;
    }
    
    // ÙØªØ­ Ù†Ø§ÙØ°Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ± Ù…Ø¹ Ø§Ù„Ù„Ø¹Ø¨Ø© Ø§Ù„Ù…Ø®ØªØ§Ø±Ø©
    setSelectedGame(game);
    setQuantity(1);
    setShowRentalModal(true);
  };

  // ğŸš€ Ø¨Ø¯Ø¡ ØªØ£Ø¬ÙŠØ± Ø¬Ø¯ÙŠØ¯
  const handleStartRental = async () => {
    // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    if (!customerName.trim()) {
