const smartLoadData = async (type) => {
  const now = Date.now();
  const minInterval = 5000; // 5 ثواني كحد أدنى بين الطلبات
  
  // التحقق من التكرار السريع
  if (now - lastLoadTime[type] < minInterval) {
    console.log(`⏱️ تم إلغاء تحميل ${type} (تكرار سريع)`);
    return;
  }
  
  setIsLoading(prev => ({ ...prev, [type]: true }));
  setLastLoadTime(prev => ({ ...prev, [type]: now }));
  
  try {
    switch(type) {
      case 'games':
        await loadGames();
        break;
      case 'activeRentals':
        await loadActiveRentals();
        break;
      case 'completedRentals':
        await loadCompletedRentals();
        break;
      case 'statistics':
        await updateStatisticsImmediate();
        break;
    }
    
    console.log(`✅ تم تحميل ${type} بنجاح`);
  } catch (error) {
    console.error(`❌ خطأ في تحميل ${type}:`, error);
  } finally {
    setIsLoading(prev => ({ ...prev, [type]: false }));
  }
};


